/**
 * Kanban Board Styles
 *
 * Organized sections:
 * 1. Board List Page
 * 2. Board View
 * 3. Column
 * 4. Card
 * 5. Forms (AddCard, CreateBoard)
 * 6. Modal
 * 7. Reports placeholder
 * 8. Responsive styles
 */

/* =============================================================================
   1. BOARD LIST PAGE
   ============================================================================= */

.board-list-page {
  max-width: 1200px;
  margin: 0 auto;
}

.board-list-header {
  display: flex;
  align-items: center;
  justify-content: space-between;
  margin-bottom: var(--space-6);
}

.board-list-header h2 {
  margin: 0;
  font-size: 1.5rem;
  font-weight: 600;
  color: var(--color-text);
}

.board-grid {
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(280px, 1fr));
  gap: var(--space-4);
}

.board-card {
  display: flex;
  flex-direction: column;
  padding: var(--space-4);
  background-color: var(--color-bg-elevated);
  border: 1px solid var(--color-border);
  border-radius: var(--radius-lg);
  cursor: pointer;
  transition: box-shadow var(--transition-fast),
    transform var(--transition-fast),
    border-color var(--transition-fast);
}

.board-card:hover {
  box-shadow: var(--shadow-md);
  transform: translateY(-2px);
  border-color: var(--color-primary);
}

.board-card:focus-visible {
  outline: 2px solid var(--color-border-focus);
  outline-offset: 2px;
}

.board-card h3 {
  margin: 0 0 var(--space-2);
  font-size: 1.125rem;
  font-weight: 600;
  color: var(--color-text);
}

.board-card-meta {
  color: var(--color-text-muted);
  font-size: 0.875rem;
}

.create-board-card {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  min-height: 120px;
  padding: var(--space-4);
  background-color: var(--color-bg-secondary);
  border: 2px dashed var(--color-border);
  border-radius: var(--radius-lg);
  cursor: pointer;
  transition: border-color var(--transition-fast),
    background-color var(--transition-fast);
}

.create-board-card:hover {
  border-color: var(--color-primary);
  background-color: var(--color-bg);
}

.create-board-card:focus-visible {
  outline: 2px solid var(--color-border-focus);
  outline-offset: 2px;
}

.create-board-card .icon {
  font-size: 2rem;
  margin-bottom: var(--space-2);
  color: var(--color-text-muted);
}

.create-board-card span {
  color: var(--color-text-muted);
  font-weight: 500;
}

/* =============================================================================
   2. BOARD VIEW
   ============================================================================= */

.board-view {
  display: flex;
  flex-direction: column;
  height: calc(100vh - 120px);
  min-height: 500px;
}

.board-header {
  display: flex;
  align-items: center;
  gap: var(--space-4);
  margin-bottom: var(--space-4);
  flex-shrink: 0;
}

.board-header h2 {
  margin: 0;
  font-size: 1.5rem;
  font-weight: 600;
  color: var(--color-text);
}

.board-columns {
  display: flex;
  gap: var(--space-4);
  flex: 1;
  overflow-x: auto;
  padding-bottom: var(--space-4);
}

.add-column-btn {
  flex-shrink: 0;
  min-width: 280px;
  height: fit-content;
  padding: var(--space-4);
  background-color: var(--color-bg-secondary);
  border: 2px dashed var(--color-border);
  border-radius: var(--radius-lg);
  cursor: pointer;
  color: var(--color-text-muted);
  font-weight: 500;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: var(--space-2);
  transition: border-color var(--transition-fast),
    background-color var(--transition-fast);
}

.add-column-btn:hover {
  border-color: var(--color-primary);
  background-color: var(--color-bg);
  color: var(--color-primary);
}

/* =============================================================================
   3. COLUMN
   ============================================================================= */

.column {
  flex-shrink: 0;
  width: 300px;
  min-width: 280px;
  max-width: 320px;
  display: flex;
  flex-direction: column;
  background-color: var(--color-bg-secondary);
  border-radius: var(--radius-lg);
  max-height: 100%;
}

.column-header {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: var(--space-3) var(--space-4);
  border-bottom: 1px solid var(--color-border);
}

.column-title {
  display: flex;
  align-items: center;
  gap: var(--space-2);
  font-weight: 600;
  color: var(--color-text);
}

.column-title h3 {
  margin: 0;
  font-size: 0.875rem;
  text-transform: uppercase;
  letter-spacing: 0.05em;
}

.column-count {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  min-width: 24px;
  height: 24px;
  padding: 0 var(--space-2);
  background-color: var(--color-border);
  border-radius: var(--radius-full);
  font-size: 0.75rem;
  font-weight: 600;
  color: var(--color-text-muted);
}

.column-actions {
  display: flex;
  gap: var(--space-1);
}

.column-cards {
  flex: 1;
  padding: var(--space-3);
  display: flex;
  flex-direction: column;
  gap: var(--space-3);
  overflow-y: auto;
  min-height: 100px;
}

.column-cards:empty::after {
  content: 'No cards';
  display: block;
  padding: var(--space-4);
  text-align: center;
  color: var(--color-text-muted);
  font-size: 0.875rem;
  font-style: italic;
}

/* =============================================================================
   4. CARD
   ============================================================================= */

.card {
  padding: var(--space-3);
  background-color: var(--color-bg-elevated);
  border: 1px solid var(--color-border);
  border-radius: var(--radius-md);
  box-shadow: var(--shadow-sm);
  cursor: pointer;
  transition: box-shadow var(--transition-fast),
    transform var(--transition-fast),
    border-color var(--transition-fast);
}

.card:hover {
  box-shadow: var(--shadow-md);
  transform: translateY(-1px);
  border-color: var(--color-primary);
}

.card:focus-visible {
  outline: 2px solid var(--color-border-focus);
  outline-offset: 2px;
}

.card-title {
  margin: 0;
  font-size: 0.9375rem;
  font-weight: 500;
  color: var(--color-text);
  word-wrap: break-word;
}

.card-description {
  margin: var(--space-2) 0 0;
  font-size: 0.8125rem;
  color: var(--color-text-muted);
  display: -webkit-box;
  -webkit-line-clamp: 2;
  -webkit-box-orient: vertical;
  overflow: hidden;
}

/* =============================================================================
   5. FORMS
   ============================================================================= */

/* Inline form container */
.inline-form {
  padding: var(--space-3);
  background-color: var(--color-bg-elevated);
  border: 1px solid var(--color-border);
  border-radius: var(--radius-md);
}

.inline-form-input {
  width: 100%;
  padding: var(--space-2);
  border: 1px solid var(--color-border);
  border-radius: var(--radius-md);
  font-size: 0.9375rem;
  font-family: inherit;
  background-color: var(--color-bg);
  color: var(--color-text);
  transition: border-color var(--transition-fast);
}

.inline-form-input:focus {
  border-color: var(--color-border-focus);
  outline: none;
}

.inline-form-input::placeholder {
  color: var(--color-text-muted);
}

.inline-form-actions {
  display: flex;
  gap: var(--space-2);
  margin-top: var(--space-2);
}

/* Add card form specific */
.add-card-form {
  margin-top: var(--space-2);
}

.add-card-btn {
  display: flex;
  align-items: center;
  justify-content: center;
  gap: var(--space-2);
  width: 100%;
  padding: var(--space-2);
  background: none;
  border: 1px dashed var(--color-border);
  border-radius: var(--radius-md);
  color: var(--color-text-muted);
  font-size: 0.875rem;
  cursor: pointer;
  transition: border-color var(--transition-fast),
    color var(--transition-fast),
    background-color var(--transition-fast);
}

.add-card-btn:hover {
  border-color: var(--color-primary);
  color: var(--color-primary);
  background-color: var(--color-bg-elevated);
}

/* Create board form */
.create-board-form {
  padding: var(--space-4);
  background-color: var(--color-bg-elevated);
  border: 1px solid var(--color-border);
  border-radius: var(--radius-lg);
}

.create-board-form label {
  display: block;
  margin-bottom: var(--space-2);
  font-weight: 500;
  color: var(--color-text);
}

/* =============================================================================
   6. MODAL
   ============================================================================= */

.modal-overlay {
  position: fixed;
  inset: 0;
  background-color: rgba(0, 0, 0, 0.5);
  display: flex;
  align-items: center;
  justify-content: center;
  padding: var(--space-4);
  z-index: 1000;
}

.modal {
  width: 100%;
  max-width: 500px;
  max-height: 90vh;
  background-color: var(--color-bg-elevated);
  border-radius: var(--radius-lg);
  box-shadow: var(--shadow-md);
  display: flex;
  flex-direction: column;
  overflow: hidden;
}

.modal-header {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: var(--space-4);
  border-bottom: 1px solid var(--color-border);
}

.modal-header h2 {
  margin: 0;
  font-size: 1.125rem;
  font-weight: 600;
  color: var(--color-text);
}

.modal-close {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 32px;
  height: 32px;
  padding: 0;
  background: none;
  border: none;
  border-radius: var(--radius-md);
  color: var(--color-text-muted);
  cursor: pointer;
  transition: background-color var(--transition-fast),
    color var(--transition-fast);
}

.modal-close:hover {
  background-color: var(--color-bg-secondary);
  color: var(--color-text);
}

.modal-body {
  flex: 1;
  padding: var(--space-4);
  overflow-y: auto;
}

.modal-footer {
  display: flex;
  gap: var(--space-2);
  justify-content: flex-end;
  padding: var(--space-4);
  border-top: 1px solid var(--color-border);
}

/* Form fields in modal */
.form-field {
  margin-bottom: var(--space-4);
}

.form-field:last-child {
  margin-bottom: 0;
}

.form-label {
  display: block;
  margin-bottom: var(--space-2);
  font-weight: 500;
  color: var(--color-text);
}

.form-input {
  width: 100%;
  padding: var(--space-2) var(--space-3);
  border: 1px solid var(--color-border);
  border-radius: var(--radius-md);
  font-size: 1rem;
  font-family: inherit;
  background-color: var(--color-bg);
  color: var(--color-text);
  transition: border-color var(--transition-fast);
}

.form-input:focus {
  border-color: var(--color-border-focus);
  outline: none;
}

.form-textarea {
  min-height: 100px;
  resize: vertical;
}

/* =============================================================================
   7. BUTTONS
   ============================================================================= */

.btn {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  gap: var(--space-2);
  padding: var(--space-2) var(--space-4);
  font-size: 0.875rem;
  font-weight: 500;
  font-family: inherit;
  border: 1px solid transparent;
  border-radius: var(--radius-md);
  cursor: pointer;
  transition: background-color var(--transition-fast),
    border-color var(--transition-fast),
    color var(--transition-fast);
}

.btn:disabled {
  opacity: 0.6;
  cursor: not-allowed;
}

.btn-primary {
  background-color: var(--color-primary);
  color: var(--color-text-inverse);
}

.btn-primary:hover:not(:disabled) {
  background-color: var(--color-primary-hover);
}

.btn-secondary {
  background-color: transparent;
  color: var(--color-text);
  border-color: var(--color-border);
}

.btn-secondary:hover:not(:disabled) {
  background-color: var(--color-bg-secondary);
}

.btn-danger {
  background-color: var(--color-error);
  color: var(--color-text-inverse);
}

.btn-danger:hover:not(:disabled) {
  background-color: #b91c1c;
}

.btn-icon {
  width: 32px;
  height: 32px;
  padding: 0;
  background: none;
  border: none;
  border-radius: var(--radius-md);
  color: var(--color-text-muted);
  cursor: pointer;
  transition: background-color var(--transition-fast),
    color var(--transition-fast);
}

.btn-icon:hover:not(:disabled) {
  background-color: var(--color-bg-secondary);
  color: var(--color-text);
}

.btn-sm {
  padding: var(--space-1) var(--space-2);
  font-size: 0.8125rem;
}

/* =============================================================================
   8. REPORTS PAGE
   ============================================================================= */

.reports-page {
  max-width: 1200px;
  margin: 0 auto;
}

.reports-header {
  margin-bottom: var(--space-6);
}

.reports-header h2 {
  margin: 0;
  font-size: 1.5rem;
  font-weight: 600;
  color: var(--color-text);
}

/* =============================================================================
   9. UTILITY STATES
   ============================================================================= */

.loading-state {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: var(--space-8);
  color: var(--color-text-muted);
}

.loading-spinner {
  width: 32px;
  height: 32px;
  border: 3px solid var(--color-border);
  border-top-color: var(--color-primary);
  border-radius: 50%;
  animation: spin 0.8s linear infinite;
}

@keyframes spin {
  to {
    transform: rotate(360deg);
  }
}

.error-state {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: var(--space-8);
  color: var(--color-error);
  text-align: center;
}

.error-state p {
  margin: var(--space-2) 0 0;
}

.empty-state {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: var(--space-8);
  color: var(--color-text-muted);
  text-align: center;
}

/* =============================================================================
   10. RESPONSIVE STYLES
   ============================================================================= */

/* Horizontal scroll for narrow viewports */
@media (max-width: 768px) {
  .board-columns {
    /* Enable smooth horizontal scrolling on mobile */
    -webkit-overflow-scrolling: touch;
    scroll-snap-type: x mandatory;
  }

  .column {
    scroll-snap-align: start;
    /* Ensure columns don't get too narrow */
    min-width: 280px;
    width: 85vw;
    max-width: 320px;
  }

  .add-column-btn {
    min-width: 200px;
  }

  /* Larger touch targets on mobile */
  .btn {
    min-height: 44px;
    padding: var(--space-3) var(--space-4);
  }

  .btn-icon {
    width: 44px;
    height: 44px;
  }

  .card {
    padding: var(--space-4);
  }

  /* Board list adjustments */
  .board-list-header {
    flex-direction: column;
    align-items: flex-start;
    gap: var(--space-4);
  }

  .board-grid {
    grid-template-columns: 1fr;
  }
}

@media (max-width: 480px) {
  .app-header {
    flex-direction: column;
    align-items: flex-start;
    gap: var(--space-3);
    padding: var(--space-3) var(--space-4);
  }

  .app-main {
    padding: var(--space-4);
  }

  .modal {
    max-width: 100%;
    margin: var(--space-2);
    max-height: calc(100vh - var(--space-4));
  }
}

/* High contrast mode support */
@media (prefers-contrast: high) {
  .card {
    border-width: 2px;
  }

  .column {
    border: 2px solid var(--color-border);
  }

  .btn {
    border-width: 2px;
  }
}

/* Reduced motion support */
@media (prefers-reduced-motion: reduce) {
  *,
  *::before,
  *::after {
    animation-duration: 0.01ms !important;
    animation-iteration-count: 1 !important;
    transition-duration: 0.01ms !important;
  }
}

toolUseResult.content:
/**
 * Kanban Board Styles
 *
 * Organized sections:
 * 1. Board List Page
 * 2. Board View
 * 3. Column
 * 4. Card
 * 5. Forms (AddCard, CreateBoard)
 * 6. Modal
 * 7. Reports placeholder
 * 8. Responsive styles
 */

/* =============================================================================
   1. BOARD LIST PAGE
   ============================================================================= */

.board-list-page {
  max-width: 1200px;
  margin: 0 auto;
}

.board-list-header {
  display: flex;
  align-items: center;
  justify-content: space-between;
  margin-bottom: var(--space-6);
}

.board-list-header h2 {
  margin: 0;
  font-size: 1.5rem;
  font-weight: 600;
  color: var(--color-text);
}

.board-grid {
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(280px, 1fr));
  gap: var(--space-4);
}

.board-card {
  display: flex;
  flex-direction: column;
  padding: var(--space-4);
  background-color: var(--color-bg-elevated);
  border: 1px solid var(--color-border);
  border-radius: var(--radius-lg);
  cursor: pointer;
  transition: box-shadow var(--transition-fast),
    transform var(--transition-fast),
    border-color var(--transition-fast);
}

.board-card:hover {
  box-shadow: var(--shadow-md);
  transform: translateY(-2px);
  border-color: var(--color-primary);
}

.board-card:focus-visible {
  outline: 2px solid var(--color-border-focus);
  outline-offset: 2px;
}

.board-card h3 {
  margin: 0 0 var(--space-2);
  font-size: 1.125rem;
  font-weight: 600;
  color: var(--color-text);
}

.board-card-meta {
  color: var(--color-text-muted);
  font-size: 0.875rem;
}

.create-board-card {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  min-height: 120px;
  padding: var(--space-4);
  background-color: var(--color-bg-secondary);
  border: 2px dashed var(--color-border);
  border-radius: var(--radius-lg);
  cursor: pointer;
  transition: border-color var(--transition-fast),
    background-color var(--transition-fast);
}

.create-board-card:hover {
  border-color: var(--color-primary);
  background-color: var(--color-bg);
}

.create-board-card:focus-visible {
  outline: 2px solid var(--color-border-focus);
  outline-offset: 2px;
}

.create-board-card .icon {
  font-size: 2rem;
  margin-bottom: var(--space-2);
  color: var(--color-text-muted);
}

.create-board-card span {
  color: var(--color-text-muted);
  font-weight: 500;
}

/* =============================================================================
   2. BOARD VIEW
   ============================================================================= */

.board-view {
  display: flex;
  flex-direction: column;
  height: calc(100vh - 120px);
  min-height: 500px;
}

.board-header {
  display: flex;
  align-items: center;
  gap: var(--space-4);
  margin-bottom: var(--space-4);
  flex-shrink: 0;
}

.board-header h2 {
  margin: 0;
  font-size: 1.5rem;
  font-weight: 600;
  color: var(--color-text);
}

.board-columns {
  display: flex;
  gap: var(--space-4);
  flex: 1;
  overflow-x: auto;
  padding-bottom: var(--space-4);
}

.add-column-btn {
  flex-shrink: 0;
  min-width: 280px;
  height: fit-content;
  padding: var(--space-4);
  background-color: var(--color-bg-secondary);
  border: 2px dashed var(--color-border);
  border-radius: var(--radius-lg);
  cursor: pointer;
  color: var(--color-text-muted);
  font-weight: 500;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: var(--space-2);
  transition: border-color var(--transition-fast),
    background-color var(--transition-fast);
}

.add-column-btn:hover {
  border-color: var(--color-primary);
  background-color: var(--color-bg);
  color: var(--color-primary);
}

/* =============================================================================
   3. COLUMN
   ============================================================================= */

.column {
  flex-shrink: 0;
  width: 300px;
  min-width: 280px;
  max-width: 320px;
  display: flex;
  flex-direction: column;
  background-color: var(--color-bg-secondary);
  border-radius: var(--radius-lg);
  max-height: 100%;
}

.column-header {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: var(--space-3) var(--space-4);
  border-bottom: 1px solid var(--color-border);
}

.column-title {
  display: flex;
  align-items: center;
  gap: var(--space-2);
  font-weight: 600;
  color: var(--color-text);
}

.column-title h3 {
  margin: 0;
  font-size: 0.875rem;
  text-transform: uppercase;
  letter-spacing: 0.05em;
}

.column-count {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  min-width: 24px;
  height: 24px;
  padding: 0 var(--space-2);
  background-color: var(--color-border);
  border-radius: var(--radius-full);
  font-size: 0.75rem;
  font-weight: 600;
  color: var(--color-text-muted);
}

.column-actions {
  display: flex;
  gap: var(--space-1);
}

.column-cards {
  flex: 1;
  padding: var(--space-3);
  display: flex;
  flex-direction: column;
  gap: var(--space-3);
  overflow-y: auto;
  min-height: 100px;
}

.column-cards:empty::after {
  content: 'No cards';
  display: block;
  padding: var(--space-4);
  text-align: center;
  color: var(--color-text-muted);
  font-size: 0.875rem;
  font-style: italic;
}

/* =============================================================================
   4. CARD
   ============================================================================= */

.card {
  padding: var(--space-3);
  background-color: var(--color-bg-elevated);
  border: 1px solid var(--color-border);
  border-radius: var(--radius-md);
  box-shadow: var(--shadow-sm);
  cursor: pointer;
  transition: box-shadow var(--transition-fast),
    transform var(--transition-fast),
    border-color var(--transition-fast);
}

.card:hover {
  box-shadow: var(--shadow-md);
  transform: translateY(-1px);
  border-color: var(--color-primary);
}

.card:focus-visible {
  outline: 2px solid var(--color-border-focus);
  outline-offset: 2px;
}

.card-title {
  margin: 0;
  font-size: 0.9375rem;
  font-weight: 500;
  color: var(--color-text);
  word-wrap: break-word;
}

.card-description {
  margin: var(--space-2) 0 0;
  font-size: 0.8125rem;
  color: var(--color-text-muted);
  display: -webkit-box;
  -webkit-line-clamp: 2;
  -webkit-box-orient: vertical;
  overflow: hidden;
}

/* =============================================================================
   5. FORMS
   ============================================================================= */

/* Inline form container */
.inline-form {
  padding: var(--space-3);
  background-color: var(--color-bg-elevated);
  border: 1px solid var(--color-border);
  border-radius: var(--radius-md);
}

.inline-form-input {
  width: 100%;
  padding: var(--space-2);
  border: 1px solid var(--color-border);
  border-radius: var(--radius-md);
  font-size: 0.9375rem;
  font-family: inherit;
  background-color: var(--color-bg);
  color: var(--color-text);
  transition: border-color var(--transition-fast);
}

.inline-form-input:focus {
  border-color: var(--color-border-focus);
  outline: none;
}

.inline-form-input::placeholder {
  color: var(--color-text-muted);
}

.inline-form-actions {
  display: flex;
  gap: var(--space-2);
  margin-top: var(--space-2);
}

/* Add card form specific */
.add-card-form {
  margin-top: var(--space-2);
}

.add-card-btn {
  display: flex;
  align-items: center;
  justify-content: center;
  gap: var(--space-2);
  width: 100%;
  padding: var(--space-2);
  background: none;
  border: 1px dashed var(--color-border);
  border-radius: var(--radius-md);
  color: var(--color-text-muted);
  font-size: 0.875rem;
  cursor: pointer;
  transition: border-color var(--transition-fast),
    color var(--transition-fast),
    background-color var(--transition-fast);
}

.add-card-btn:hover {
  border-color: var(--color-primary);
  color: var(--color-primary);
  background-color: var(--color-bg-elevated);
}

/* Create board form */
.create-board-form {
  padding: var(--space-4);
  background-color: var(--color-bg-elevated);
  border: 1px solid var(--color-border);
  border-radius: var(--radius-lg);
}

.create-board-form label {
  display: block;
  margin-bottom: var(--space-2);
  font-weight: 500;
  color: var(--color-text);
}

/* =============================================================================
   6. MODAL
   ============================================================================= */

.modal-overlay {
  position: fixed;
  inset: 0;
  background-color: rgba(0, 0, 0, 0.5);
  display: flex;
  align-items: center;
  justify-content: center;
  padding: var(--space-4);
  z-index: 1000;
}

.modal {
  width: 100%;
  max-width: 500px;
  max-height: 90vh;
  background-color: var(--color-bg-elevated);
  border-radius: var(--radius-lg);
  box-shadow: var(--shadow-md);
  display: flex;
  flex-direction: column;
  overflow: hidden;
}

.modal-header {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: var(--space-4);
  border-bottom: 1px solid var(--color-border);
}

.modal-header h2 {
  margin: 0;
  font-size: 1.125rem;
  font-weight: 600;
  color: var(--color-text);
}

.modal-close {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 32px;
  height: 32px;
  padding: 0;
  background: none;
  border: none;
  border-radius: var(--radius-md);
  color: var(--color-text-muted);
  cursor: pointer;
  transition: background-color var(--transition-fast),
    color var(--transition-fast);
}

.modal-close:hover {
  background-color: var(--color-bg-secondary);
  color: var(--color-text);
}

.modal-body {
  flex: 1;
  padding: var(--space-4);
  overflow-y: auto;
}

.modal-footer {
  display: flex;
  gap: var(--space-2);
  justify-content: flex-end;
  padding: var(--space-4);
  border-top: 1px solid var(--color-border);
}

/* Form fields in modal */
.form-field {
  margin-bottom: var(--space-4);
}

.form-field:last-child {
  margin-bottom: 0;
}

.form-label {
  display: block;
  margin-bottom: var(--space-2);
  font-weight: 500;
  color: var(--color-text);
}

.form-input {
  width: 100%;
  padding: var(--space-2) var(--space-3);
  border: 1px solid var(--color-border);
  border-radius: var(--radius-md);
  font-size: 1rem;
  font-family: inherit;
  background-color: var(--color-bg);
  color: var(--color-text);
  transition: border-color var(--transition-fast);
}

.form-input:focus {
  border-color: var(--color-border-focus);
  outline: none;
}

.form-textarea {
  min-height: 100px;
  resize: vertical;
}

/* =============================================================================
   7. BUTTONS
   ============================================================================= */

.btn {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  gap: var(--space-2);
  padding: var(--space-2) var(--space-4);
  font-size: 0.875rem;
  font-weight: 500;
  font-family: inherit;
  border: 1px solid transparent;
  border-radius: var(--radius-md);
  cursor: pointer;
  transition: background-color var(--transition-fast),
    border-color var(--transition-fast),
    color var(--transition-fast);
}

.btn:disabled {
  opacity: 0.6;
  cursor: not-allowed;
}

.btn-primary {
  background-color: var(--color-primary);
  color: var(--color-text-inverse);
}

.btn-primary:hover:not(:disabled) {
  background-color: var(--color-primary-hover);
}

.btn-secondary {
  background-color: transparent;
  color: var(--color-text);
  border-color: var(--color-border);
}

.btn-secondary:hover:not(:disabled) {
  background-color: var(--color-bg-secondary);
}

.btn-danger {
  background-color: var(--color-error);
  color: var(--color-text-inverse);
}

.btn-danger:hover:not(:disabled) {
  background-color: #b91c1c;
}

.btn-icon {
  width: 32px;
  height: 32px;
  padding: 0;
  background: none;
  border: none;
  border-radius: var(--radius-md);
  color: var(--color-text-muted);
  cursor: pointer;
  transition: background-color var(--transition-fast),
    color var(--transition-fast);
}

.btn-icon:hover:not(:disabled) {
  background-color: var(--color-bg-secondary);
  color: var(--color-text);
}

.btn-sm {
  padding: var(--space-1) var(--space-2);
  font-size: 0.8125rem;
}

/* =============================================================================
   8. REPORTS PAGE
   ============================================================================= */

.reports-page {
  max-width: 1200px;
  margin: 0 auto;
}

.reports-header {
  margin-bottom: var(--space-6);
}

.reports-header h2 {
  margin: 0;
  font-size: 1.5rem;
  font-weight: 600;
  color: var(--color-text);
}

/* =============================================================================
   9. UTILITY STATES
   ============================================================================= */

.loading-state {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: var(--space-8);
  color: var(--color-text-muted);
}

.loading-spinner {
  width: 32px;
  height: 32px;
  border: 3px solid var(--color-border);
  border-top-color: var(--color-primary);
  border-radius: 50%;
  animation: spin 0.8s linear infinite;
}

@keyframes spin {
  to {
    transform: rotate(360deg);
  }
}

.error-state {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: var(--space-8);
  color: var(--color-error);
  text-align: center;
}

.error-state p {
  margin: var(--space-2) 0 0;
}

.empty-state {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: var(--space-8);
  color: var(--color-text-muted);
  text-align: center;
}

/* =============================================================================
   10. RESPONSIVE STYLES
   ============================================================================= */

/* Horizontal scroll for narrow viewports */
@media (max-width: 768px) {
  .board-columns {
    /* Enable smooth horizontal scrolling on mobile */
    -webkit-overflow-scrolling: touch;
    scroll-snap-type: x mandatory;
  }

  .column {
    scroll-snap-align: start;
    /* Ensure columns don't get too narrow */
    min-width: 280px;
    width: 85vw;
    max-width: 320px;
  }

  .add-column-btn {
    min-width: 200px;
  }

  /* Larger touch targets on mobile */
  .btn {
    min-height: 44px;
    padding: var(--space-3) var(--space-4);
  }

  .btn-icon {
    width: 44px;
    height: 44px;
  }

  .card {
    padding: var(--space-4);
  }

  /* Board list adjustments */
  .board-list-header {
    flex-direction: column;
    align-items: flex-start;
    gap: var(--space-4);
  }

  .board-grid {
    grid-template-columns: 1fr;
  }
}

@media (max-width: 480px) {
  .app-header {
    flex-direction: column;
    align-items: flex-start;
    gap: var(--space-3);
    padding: var(--space-3) var(--space-4);
  }

  .app-main {
    padding: var(--space-4);
  }

  .modal {
    max-width: 100%;
    margin: var(--space-2);
    max-height: calc(100vh - var(--space-4));
  }
}

/* High contrast mode support */
@media (prefers-contrast: high) {
  .card {
    border-width: 2px;
  }

  .column {
    border: 2px solid var(--color-border);
  }

  .btn {
