  }
}

/* Reduced motion support */
@media (prefers-reduced-motion: reduce) {
  *,
  *::before,
  *::after {
    animation-duration: 0.01ms !important;
    animation-iteration-count: 1 !important;
    transition-duration: 0.01ms !important;
  }
}

input.content:
/**
 * Card component displays a single task card in a Kanban column.
 *
 * Clicking the card triggers the edit modal via onEdit callback.
 * Cards are accessible via keyboard (Enter/Space to edit).
 *
 * Note: Drag-and-drop is NOT implemented in this phase.
 *
 * @param {Object} props
 * @param {Object} props.card - Card data with id, title, description
 * @param {Function} props.onEdit - Callback when card is clicked to edit
 */
function Card({ card, onEdit }) {
  /**
   * Handle click and keyboard interaction to open edit modal.
   */
  const handleClick = () => {
    if (onEdit) {
      onEdit(card);
    }
  };

  /**
   * Handle keyboard navigation - Enter or Space triggers edit.
   */
  const handleKeyDown = (event) => {
    if (event.key === 'Enter' || event.key === ' ') {
      event.preventDefault();
      handleClick();
    }
  };

  return (
    <article
      className="card"
      onClick={handleClick}
      onKeyDown={handleKeyDown}
      tabIndex={0}
      role="button"
      aria-label={`Edit card: ${card.title}`}
      data-testid={`card-${card.id}`}
    >
      <h4 className="card-title">{card.title}</h4>
      {card.description && (
        <p className="card-description">{card.description}</p>
      )}
    </article>
  );
}

export default Card;

import { useState, useRef, useEffect } from 'react';

/**
 * AddCardForm provides an inline form for quick card creation.
 *
 * When collapsed, shows a button to add a card. When expanded,
 * shows a text input that submits on Enter or button click.
 *
 * @param {Object} props
 * @param {Function} props.onSubmit - Callback when form is submitted with title
 * @param {boolean} props.disabled - Whether the form is disabled (during API call)
 */
function AddCardForm({ onSubmit, disabled = false }) {
  const [isExpanded, setIsExpanded] = useState(false);
  const [title, setTitle] = useState('');
  const inputRef = useRef(null);

  /**
   * Focus input when form expands.
   */
  useEffect(() => {
    if (isExpanded && inputRef.current) {
      inputRef.current.focus();
    }
  }, [isExpanded]);

  /**
   * Handle form submission.
   */
  const handleSubmit = (event) => {
    event.preventDefault();
    const trimmedTitle = title.trim();
    if (trimmedTitle && onSubmit) {
      onSubmit(trimmedTitle);
      setTitle('');
      // Keep form expanded for rapid card creation
    }
  };

  /**
   * Handle Enter key to submit, Escape to cancel.
   */
  const handleKeyDown = (event) => {
    if (event.key === 'Escape') {
      setTitle('');
      setIsExpanded(false);
    }
  };

  /**
   * Cancel and collapse the form.
   */
  const handleCancel = () => {
    setTitle('');
    setIsExpanded(false);
  };

  if (!isExpanded) {
    return (
      <button
        type="button"
        className="add-card-btn"
        onClick={() => setIsExpanded(true)}
        aria-label="Add a card"
        data-testid="add-card-button"
      >
        <span aria-hidden="true">+</span>
        <span>Add a card</span>
      </button>
    );
  }

  return (
    <form
      className="add-card-form inline-form"
      onSubmit={handleSubmit}
      data-testid="add-card-form"
    >
      <input
        ref={inputRef}
        type="text"
        className="inline-form-input"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        onKeyDown={handleKeyDown}
        placeholder="Enter card title..."
        disabled={disabled}
        aria-label="Card title"
        data-testid="add-card-input"
      />
      <div className="inline-form-actions">
        <button
          type="submit"
          className="btn btn-primary btn-sm"
          disabled={disabled || !title.trim()}
          data-testid="add-card-submit"
        >
          Add Card
        </button>
        <button
          type="button"
          className="btn btn-secondary btn-sm"
          onClick={handleCancel}
          disabled={disabled}
          data-testid="add-card-cancel"
        >
          Cancel
        </button>
      </div>
    </form>
  );
}

export default AddCardForm;

import { useState, useEffect, useRef } from 'react';
import { createPortal } from 'react-dom';

/**
 * CardModal provides a modal dialog for editing card details.
 *
 * Includes form fields for title and description, with save and delete actions.
 * Closes on backdrop click, X button, or Escape key.
 *
 * @param {Object} props
 * @param {Object} props.card - Card data with id, title, description
 * @param {Function} props.onSave - Callback when card is saved with updated data
 * @param {Function} props.onDelete - Callback when card is deleted
 * @param {Function} props.onClose - Callback when modal is closed
 * @param {boolean} props.isLoading - Whether save/delete is in progress
 */
function CardModal({ card, onSave, onDelete, onClose, isLoading = false }) {
  const [title, setTitle] = useState(card?.title || '');
  const [description, setDescription] = useState(card?.description || '');
  const modalRef = useRef(null);
  const titleInputRef = useRef(null);

  /**
   * Focus the title input when modal opens.
   */
  useEffect(() => {
    if (titleInputRef.current) {
      titleInputRef.current.focus();
      // Move cursor to end of input
      titleInputRef.current.setSelectionRange(title.length, title.length);
    }
  }, []);

  /**
   * Handle Escape key to close modal.
   */
  useEffect(() => {
    const handleEscape = (event) => {
      if (event.key === 'Escape') {
        onClose();
      }
    };
    document.addEventListener('keydown', handleEscape);
    return () => document.removeEventListener('keydown', handleEscape);
  }, [onClose]);

  /**
   * Trap focus within the modal for accessibility.
   */
  useEffect(() => {
    const modal = modalRef.current;
    if (!modal) return;

    const focusableElements = modal.querySelectorAll(
      'button, [href], input, select, textarea, [tabindex]:not([tabindex="-1"])'
    );
    const firstFocusable = focusableElements[0];
    const lastFocusable = focusableElements[focusableElements.length - 1];

    const trapFocus = (event) => {
      if (event.key !== 'Tab') return;

      if (event.shiftKey) {
        if (document.activeElement === firstFocusable) {
          event.preventDefault();
          lastFocusable.focus();
        }
      } else {
        if (document.activeElement === lastFocusable) {
          event.preventDefault();
          firstFocusable.focus();
        }
      }
    };

    modal.addEventListener('keydown', trapFocus);
    return () => modal.removeEventListener('keydown', trapFocus);
  }, []);

  /**
   * Handle backdrop click to close modal.
   */
  const handleBackdropClick = (event) => {
    if (event.target === event.currentTarget) {
      onClose();
    }
  };

  /**
   * Handle form submission to save changes.
   */
  const handleSubmit = (event) => {
    event.preventDefault();
    const trimmedTitle = title.trim();
    if (trimmedTitle && onSave) {
      onSave({
        ...card,
        title: trimmedTitle,
        description: description.trim(),
      });
    }
  };

  /**
   * Handle delete action.
   */
  const handleDelete = () => {
    if (onDelete) {
      onDelete(card.id);
    }
  };

  const modalContent = (
    <div
      className="modal-overlay"
      onClick={handleBackdropClick}
      role="presentation"
      data-testid="modal-overlay"
    >
      <div
        ref={modalRef}
        className="modal"
        role="dialog"
        aria-modal="true"
        aria-labelledby="card-modal-title"
        data-testid="card-modal"
      >
        <header className="modal-header">
          <h2 id="card-modal-title">Edit Card</h2>
          <button
            type="button"
            className="modal-close btn-icon"
            onClick={onClose}
            aria-label="Close modal"
            data-testid="modal-close"
          >
            <span aria-hidden="true">&times;</span>
          </button>
        </header>

        <form onSubmit={handleSubmit}>
          <div className="modal-body">
            <div className="form-field">
              <label htmlFor="card-title" className="form-label">
                Title
              </label>
              <input
                ref={titleInputRef}
                id="card-title"
                type="text"
                className="form-input"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
                disabled={isLoading}
                required
                data-testid="card-title-input"
              />
            </div>

            <div className="form-field">
              <label htmlFor="card-description" className="form-label">
                Description
              </label>
              <textarea
                id="card-description"
                className="form-input form-textarea"
                value={description}
                onChange={(e) => setDescription(e.target.value)}
                disabled={isLoading}
                placeholder="Add a description..."
                data-testid="card-description-input"
              />
            </div>
          </div>

          <footer className="modal-footer">
            <button
              type="button"
              className="btn btn-danger"
              onClick={handleDelete}
              disabled={isLoading}
              data-testid="card-delete-button"
            >
              Delete
            </button>
            <button
              type="button"
              className="btn btn-secondary"
              onClick={onClose}
              disabled={isLoading}
            >
              Cancel
            </button>
            <button
              type="submit"
              className="btn btn-primary"
              disabled={isLoading || !title.trim()}
              data-testid="card-save-button"
            >
              {isLoading ? 'Saving...' : 'Save'}
            </button>
          </footer>
        </form>
      </div>
    </div>
  );

  // Render modal as a portal to ensure proper stacking
  return createPortal(modalContent, document.body);
}

export default CardModal;

