/**
 * Card component displays a single task card in a Kanban column.
 *
 * Clicking the card triggers the edit modal via onEdit callback.
 * Cards are accessible via keyboard (Enter/Space to edit).
 *
 * Note: Drag-and-drop is NOT implemented in this phase.
 *
 * @param {Object} props
 * @param {Object} props.card - Card data with id, title, description
 * @param {Function} props.onEdit - Callback when card is clicked to edit
 */
function Card({ card, onEdit }) {
  /**
   * Handle click and keyboard interaction to open edit modal.
   */
  const handleClick = () => {
    if (onEdit) {
      onEdit(card);
    }
  };

  /**
   * Handle keyboard navigation - Enter or Space triggers edit.
   */
  const handleKeyDown = (event) => {
    if (event.key === 'Enter' || event.key === ' ') {
      event.preventDefault();
      handleClick();
    }
  };

  return (
    <article
      className="card"
      onClick={handleClick}
      onKeyDown={handleKeyDown}
      tabIndex={0}
      role="button"
      aria-label={`Edit card: ${card.title}`}
      data-testid={`card-${card.id}`}
    >
      <h4 className="card-title">{card.title}</h4>
      {card.description && (
        <p className="card-description">{card.description}</p>
      )}
    </article>
  );
}

export default Card;

import { useState, useRef, useEffect } from 'react';

/**
 * AddCardForm provides an inline form for quick card creation.
 *
 * When collapsed, shows a button to add a card. When expanded,
 * shows a text input that submits on Enter or button click.
 *
 * @param {Object} props
 * @param {Function} props.onSubmit - Callback when form is submitted with title
 * @param {boolean} props.disabled - Whether the form is disabled (during API call)
 */
function AddCardForm({ onSubmit, disabled = false }) {
  const [isExpanded, setIsExpanded] = useState(false);
  const [title, setTitle] = useState('');
  const inputRef = useRef(null);

  /**
   * Focus input when form expands.
   */
  useEffect(() => {
    if (isExpanded && inputRef.current) {
      inputRef.current.focus();
    }
  }, [isExpanded]);

  /**
   * Handle form submission.
   */
  const handleSubmit = (event) => {
    event.preventDefault();
    const trimmedTitle = title.trim();
    if (trimmedTitle && onSubmit) {
      onSubmit(trimmedTitle);
      setTitle('');
      // Keep form expanded for rapid card creation
    }
  };

  /**
   * Handle Enter key to submit, Escape to cancel.
   */
  const handleKeyDown = (event) => {
    if (event.key === 'Escape') {
      setTitle('');
      setIsExpanded(false);
    }
  };

  /**
   * Cancel and collapse the form.
   */
  const handleCancel = () => {
    setTitle('');
    setIsExpanded(false);
  };

  if (!isExpanded) {
    return (
      <button
        type="button"
        className="add-card-btn"
        onClick={() => setIsExpanded(true)}
        aria-label="Add a card"
        data-testid="add-card-button"
      >
        <span aria-hidden="true">+</span>
        <span>Add a card</span>
      </button>
    );
  }

  return (
    <form
      className="add-card-form inline-form"
      onSubmit={handleSubmit}
      data-testid="add-card-form"
    >
      <input
        ref={inputRef}
        type="text"
        className="inline-form-input"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        onKeyDown={handleKeyDown}
        placeholder="Enter card title..."
        disabled={disabled}
        aria-label="Card title"
        data-testid="add-card-input"
      />
      <div className="inline-form-actions">
        <button
          type="submit"
          className="btn btn-primary btn-sm"
          disabled={disabled || !title.trim()}
          data-testid="add-card-submit"
        >
          Add Card
        </button>
        <button
          type="button"
          className="btn btn-secondary btn-sm"
          onClick={handleCancel}
          disabled={disabled}
          data-testid="add-card-cancel"
        >
          Cancel
        </button>
      </div>
    </form>
  );
}

export default AddCardForm;

