import { useState } from 'react';
import Card from './Card';
import AddCardForm from './AddCardForm';

/**
 * Column component displays a single column in the Kanban board.
 *
 * Shows the column header with name and card count, lists all cards
 * sorted by position, and provides the AddCardForm for creating new cards.
 *
 * @param {Object} props
 * @param {Object} props.column - Column data with id, name, cards array
 * @param {Function} props.onCreateCard - Callback to create a card in this column
 * @param {Function} props.onEditCard - Callback when a card is clicked for editing
 * @param {Function} props.onDeleteColumn - Callback to delete this column
 * @param {boolean} props.isLoading - Whether an operation is in progress
 */
function Column({
  column,
  onCreateCard,
  onEditCard,
  onDeleteColumn,
  isLoading = false,
}) {
  const [isCreatingCard, setIsCreatingCard] = useState(false);

  /**
   * Sort cards by position for consistent display order.
   */
  const sortedCards = [...(column.cards || [])].sort((a, b) => {
    // Handle string positions (fractional indexing) and numeric positions
    const posA = typeof a.position === 'string' ? parseFloat(a.position) : a.position;
    const posB = typeof b.position === 'string' ? parseFloat(b.position) : b.position;
    return posA - posB;
  });

  /**
   * Handle card creation from the AddCardForm.
   */
  const handleCreateCard = async (title) => {
    if (onCreateCard) {
      setIsCreatingCard(true);
      try {
        await onCreateCard(column.id, title);
      } finally {
        setIsCreatingCard(false);
      }
    }
  };

  /**
   * Handle column deletion with confirmation.
   */
  const handleDeleteColumn = () => {
    if (onDeleteColumn) {
      // Simple confirmation - in production would use a proper modal
      const confirmed = window.confirm(
        `Delete column "${column.name}" and all its cards?`
      );
      if (confirmed) {
        onDeleteColumn(column.id);
      }
    }
  };

  return (
    <section
      className="column"
      aria-label={`Column: ${column.name}`}
      data-testid={`column-${column.id}`}
    >
      <header className="column-header">
        <div className="column-title">
          <h3>{column.name}</h3>
          <span className="column-count" aria-label={`${sortedCards.length} cards`}>
            {sortedCards.length}
          </span>
        </div>
        <div className="column-actions">
          <button
            type="button"
            className="btn-icon"
            onClick={handleDeleteColumn}
            disabled={isLoading}
            aria-label={`Delete column ${column.name}`}
            title="Delete column"
            data-testid="delete-column-button"
          >
            <span aria-hidden="true">&times;</span>
          </button>
        </div>
      </header>

      <div className="column-cards" role="list" aria-label="Cards">
        {sortedCards.map((card) => (
          <div key={card.id} role="listitem">
            <Card card={card} onEdit={onEditCard} />
          </div>
        ))}
      </div>

      <div style={{ padding: 'var(--space-3)' }}>
        <AddCardForm onSubmit={handleCreateCard} disabled={isCreatingCard} />
      </div>
    </section>
  );
}

export default Column;

import { useState, useEffect, useCallback } from 'react';
import { useApi } from './useApi';

/**
 * Custom hook for managing a single board's data and operations.
 *
 * Fetches board by ID with columns and cards, and provides methods
 * for CRUD operations on columns and cards.
 *
 * @param {string} boardId - The ID of the board to manage
 * @returns {Object} Board state and operation methods
 *
 * @example
 * ```jsx
 * function BoardView({ boardId }) {
 *   const {
 *     board,
 *     loading,
 *     error,
 *     createColumn,
 *     updateCard,
 *     moveCard
 *   } = useBoard(boardId);
 *
 *   if (loading) return <p>Loading...</p>;
 *   if (error) return <p>Error: {error.message}</p>;
 *
 *   return <Board data={board} />;
 * }
 * ```
 */
function useBoard(boardId) {
  const [board, setBoard] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [operationLoading, setOperationLoading] = useState(false);

  const { get, post, put, del, patch } = useApi();

  /**
   * Fetch board data with columns and cards.
   */
  const fetchBoard = useCallback(async () => {
    if (!boardId) {
      setBoard(null);
      setLoading(false);
      return;
    }

    setLoading(true);
    setError(null);

    try {
      const data = await get(`/boards/${boardId}`);
      setBoard(data);
    } catch (err) {
      setError(err);
      setBoard(null);
    } finally {
      setLoading(false);
    }
  }, [boardId, get]);

  /**
   * Fetch board data on mount and when boardId changes.
   */
  useEffect(() => {
    fetchBoard();
  }, [fetchBoard]);

  /**
   * Create a new column in the board.
   *
   * @param {string} name - Column name
   * @returns {Promise<Object>} Created column
   */
  const createColumn = useCallback(
    async (name) => {
      if (!boardId) return null;

      setOperationLoading(true);
      try {
        const newColumn = await post(`/boards/${boardId}/columns`, { name });
        // Optimistically update local state
        setBoard((prev) => {
          if (!prev) return prev;
          return {
            ...prev,
            columns: [...(prev.columns || []), { ...newColumn, cards: [] }],
          };
        });
        return newColumn;
      } catch (err) {
        setError(err);
        throw err;
      } finally {
        setOperationLoading(false);
      }
    },
    [boardId, post]
  );

  /**
   * Update an existing column.
   *
   * @param {string} columnId - Column ID
   * @param {Object} updates - Fields to update (name, position)
   * @returns {Promise<Object>} Updated column
   */
  const updateColumn = useCallback(
    async (columnId, updates) => {
      setOperationLoading(true);
      try {
        const updatedColumn = await put(`/columns/${columnId}`, updates);
        // Update local state
        setBoard((prev) => {
          if (!prev) return prev;
          return {
            ...prev,
            columns: prev.columns.map((col) =>
              col.id === columnId ? { ...col, ...updatedColumn } : col
            ),
          };
        });
        return updatedColumn;
      } catch (err) {
        setError(err);
        throw err;
      } finally {
        setOperationLoading(false);
      }
    },
    [put]
  );

  /**
   * Delete a column and all its cards.
   *
   * @param {string} columnId - Column ID
   * @returns {Promise<void>}
   */
  const deleteColumn = useCallback(
    async (columnId) => {
      setOperationLoading(true);
      try {
        await del(`/columns/${columnId}`);
        // Remove from local state
        setBoard((prev) => {
          if (!prev) return prev;
          return {
            ...prev,
            columns: prev.columns.filter((col) => col.id !== columnId),
          };
        });
      } catch (err) {
        setError(err);
        throw err;
      } finally {
        setOperationLoading(false);
      }
    },
    [del]
  );

  /**
   * Create a new card in a column.
   *
   * @param {string} columnId - Column ID
   * @param {string} title - Card title
   * @param {string} [description] - Optional card description
   * @returns {Promise<Object>} Created card
   */
  const createCard = useCallback(
    async (columnId, title, description = '') => {
      setOperationLoading(true);
      try {
        const newCard = await post(`/columns/${columnId}/cards`, {
          title,
          description,
        });
        // Add to local state
        setBoard((prev) => {
          if (!prev) return prev;
          return {
            ...prev,
            columns: prev.columns.map((col) =>
              col.id === columnId
                ? { ...col, cards: [...(col.cards || []), newCard] }
                : col
            ),
          };
        });
        return newCard;
      } catch (err) {
        setError(err);
        throw err;
      } finally {
        setOperationLoading(false);
      }
    },
    [post]
  );

  /**
   * Update an existing card.
   *
   * @param {string} cardId - Card ID
   * @param {Object} updates - Fields to update (title, description)
   * @returns {Promise<Object>} Updated card
   */
  const updateCard = useCallback(
    async (cardId, updates) => {
      setOperationLoading(true);
      try {
        const updatedCard = await put(`/cards/${cardId}`, updates);
        // Update in local state
        setBoard((prev) => {
          if (!prev) return prev;
          return {
            ...prev,
            columns: prev.columns.map((col) => ({
              ...col,
              cards: col.cards.map((card) =>
                card.id === cardId ? { ...card, ...updatedCard } : card
              ),
            })),
          };
        });
        return updatedCard;
      } catch (err) {
        setError(err);
        throw err;
      } finally {
        setOperationLoading(false);
      }
    },
    [put]
  );

  /**
   * Delete a card.
   *
   * @param {string} cardId - Card ID
   * @returns {Promise<void>}
   */
  const deleteCard = useCallback(
    async (cardId) => {
      setOperationLoading(true);
      try {
        await del(`/cards/${cardId}`);
        // Remove from local state
        setBoard((prev) => {
          if (!prev) return prev;
          return {
            ...prev,
            columns: prev.columns.map((col) => ({
              ...col,
              cards: col.cards.filter((card) => card.id !== cardId),
            })),
          };
        });
      } catch (err) {
        setError(err);
        throw err;
      } finally {
        setOperationLoading(false);
      }
    },
    [del]
  );

  /**
   * Move a card to a new position (potentially in a different column).
   *
   * Note: Drag-and-drop UI is not implemented in this phase.
   * This method is provided for future use or programmatic moves.
   *
   * @param {string} cardId - Card ID
   * @param {string} targetColumnId - Target column ID
   * @param {string|number} position - New position in target column
   * @returns {Promise<Object>} Updated card
   */
  const moveCard = useCallback(
    async (cardId, targetColumnId, position) => {
      setOperationLoading(true);

      // Find the card's current column for optimistic update
      let sourceColumnId = null;
      let cardData = null;

      if (board) {
        for (const col of board.columns) {
          const found = col.cards.find((c) => c.id === cardId);
          if (found) {
            sourceColumnId = col.id;
            cardData = found;
            break;
          }
        }
      }

      try {
        const updatedCard = await patch(`/cards/${cardId}/move`, {
          column_id: targetColumnId,
          position,
        });

        // Update local state
        setBoard((prev) => {
          if (!prev) return prev;

          return {
            ...prev,
            columns: prev.columns.map((col) => {
              // Remove card from source column
              if (col.id === sourceColumnId && sourceColumnId !== targetColumnId) {
                return {
                  ...col,
                  cards: col.cards.filter((c) => c.id !== cardId),
                };
              }
              // Add/update card in target column
              if (col.id === targetColumnId) {
                const existingIndex = col.cards.findIndex((c) => c.id === cardId);
                if (existingIndex >= 0) {
                  // Card was in same column, update position
                  const newCards = col.cards.map((c) =>
                    c.id === cardId ? { ...c, ...updatedCard } : c
                  );
                  return { ...col, cards: newCards };
                } else {
                  // Card moved from different column
                  return {
                    ...col,
                    cards: [...col.cards, { ...cardData, ...updatedCard }],
                  };
                }
              }
              return col;
            }),
          };
        });

        return updatedCard;
      } catch (err) {
        setError(err);
        throw err;
      } finally {
        setOperationLoading(false);
      }
    },
    [patch, board]
  );

  /**
   * Clear any error state.
   */
  const clearError = useCallback(() => {
    setError(null);
  }, []);

  /**
   * Refresh board data from server.
   */
  const refresh = useCallback(() => {
    return fetchBoard();
  }, [fetchBoard]);

  return {
    // Board data
    board,
    loading,
    error,
    operationLoading,

    // Column operations
    createColumn,
    updateColumn,
    deleteColumn,

    // Card operations
    createCard,
    updateCard,
    deleteCard,
    moveCard,

    // Utilities
    clearError,
    refresh,
  };
}

export { useBoard };
export default useBoard;

