import { useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import { useBoard } from '../hooks/useBoard';
import Column from './Column';
import CardModal from './CardModal';

/**
 * Board component displays a single Kanban board with its columns and cards.
 *
 * Uses URL params to get boardId and useBoard hook for data management.
 * Handles column creation and card editing via modal.
 */
function Board() {
  const { boardId } = useParams();
  const {
    board,
    loading,
    error,
    operationLoading,
    createColumn,
    deleteColumn,
    createCard,
    updateCard,
    deleteCard,
  } = useBoard(boardId);

  const [editingCard, setEditingCard] = useState(null);
  const [isAddingColumn, setIsAddingColumn] = useState(false);
  const [newColumnName, setNewColumnName] = useState('');

  /**
   * Handle column creation.
   */
  const handleAddColumn = async (event) => {
    event.preventDefault();
    const trimmedName = newColumnName.trim();
    if (!trimmedName) return;

    try {
      await createColumn(trimmedName);
      setNewColumnName('');
      setIsAddingColumn(false);
    } catch (err) {
      // Error is handled by useBoard hook
    }
  };

  /**
   * Handle card creation in a specific column.
   */
  const handleCreateCard = async (columnId, title) => {
    try {
      await createCard(columnId, title);
    } catch (err) {
      // Error is handled by useBoard hook
    }
  };

  /**
   * Open card edit modal.
   */
  const handleEditCard = (card) => {
    setEditingCard(card);
  };

  /**
   * Save card changes from modal.
   */
  const handleSaveCard = async (updatedCard) => {
    try {
      await updateCard(updatedCard.id, {
        title: updatedCard.title,
        description: updatedCard.description,
      });
      setEditingCard(null);
    } catch (err) {
      // Error is handled by useBoard hook
    }
  };

  /**
   * Delete card from modal.
   */
  const handleDeleteCard = async (cardId) => {
    try {
      await deleteCard(cardId);
      setEditingCard(null);
    } catch (err) {
      // Error is handled by useBoard hook
    }
  };

  /**
   * Handle column deletion.
   */
  const handleDeleteColumn = async (columnId) => {
    try {
      await deleteColumn(columnId);
    } catch (err) {
      // Error is handled by useBoard hook
    }
  };

  /**
   * Sort columns by position.
   */
  const sortedColumns = board?.columns
    ? [...board.columns].sort((a, b) => {
        const posA =
          typeof a.position === 'string' ? parseFloat(a.position) : a.position;
        const posB =
          typeof b.position === 'string' ? parseFloat(b.position) : b.position;
        return posA - posB;
      })
    : [];

  if (loading) {
    return (
      <div className="loading-state" role="status" aria-live="polite">
        <div className="loading-spinner" aria-hidden="true" />
        <p>Loading board...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="error-state" role="alert">
        <p>
          <strong>Error loading board</strong>
        </p>
        <p>{error.message}</p>
        <Link to="/" className="btn btn-primary" style={{ marginTop: '1rem' }}>
          Back to Boards
        </Link>
      </div>
    );
  }

  if (!board) {
    return (
      <div className="empty-state">
        <p>Board not found</p>
        <Link to="/" className="btn btn-primary" style={{ marginTop: '1rem' }}>
          Back to Boards
        </Link>
      </div>
    );
  }

  return (
    <div className="board-view" data-testid="board-view">
      <header className="board-header">
        <Link to="/" className="btn btn-secondary btn-sm" aria-label="Back to boards">
          &larr; Back
        </Link>
        <h2>{board.name}</h2>
      </header>

      <div className="board-columns" data-testid="board-columns">
        {sortedColumns.map((column) => (
          <Column
            key={column.id}
            column={column}
            onCreateCard={handleCreateCard}
            onEditCard={handleEditCard}
            onDeleteColumn={handleDeleteColumn}
            isLoading={operationLoading}
          />
        ))}

        {/* Add Column Button or Form */}
        {isAddingColumn ? (
          <div className="column" style={{ backgroundColor: 'var(--color-bg-elevated)' }}>
            <form onSubmit={handleAddColumn} className="inline-form" style={{ padding: 'var(--space-4)' }}>
              <input
                type="text"
                className="inline-form-input"
                value={newColumnName}
                onChange={(e) => setNewColumnName(e.target.value)}
                placeholder="Enter column name..."
                autoFocus
                disabled={operationLoading}
                aria-label="Column name"
                data-testid="add-column-input"
              />
              <div className="inline-form-actions">
                <button
                  type="submit"
                  className="btn btn-primary btn-sm"
                  disabled={operationLoading || !newColumnName.trim()}
                  data-testid="add-column-submit"
                >
                  Add Column
                </button>
                <button
                  type="button"
                  className="btn btn-secondary btn-sm"
                  onClick={() => {
                    setIsAddingColumn(false);
                    setNewColumnName('');
                  }}
                  disabled={operationLoading}
                >
                  Cancel
                </button>
              </div>
            </form>
          </div>
        ) : (
          <button
            type="button"
            className="add-column-btn"
            onClick={() => setIsAddingColumn(true)}
            disabled={operationLoading}
            data-testid="add-column-button"
          >
            <span aria-hidden="true">+</span>
            Add Column
          </button>
        )}
      </div>

      {/* Card Edit Modal */}
      {editingCard && (
        <CardModal
          card={editingCard}
          onSave={handleSaveCard}
          onDelete={handleDeleteCard}
          onClose={() => setEditingCard(null)}
          isLoading={operationLoading}
        />
      )}
    </div>
  );
}

export default Board;

import { useState, useEffect, useCallback } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useApi } from '../hooks/useApi';

/**
 * BoardList displays all boards as clickable cards with ability to create new boards.
 *
 * Uses useApi hook for data fetching and navigation for board selection.
 */
function BoardList() {
  const [boards, setBoards] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isCreating, setIsCreating] = useState(false);
  const [newBoardName, setNewBoardName] = useState('');
  const [createLoading, setCreateLoading] = useState(false);

  const { get, post } = useApi();
  const navigate = useNavigate();

  /**
   * Fetch all boards on mount.
   */
  const fetchBoards = useCallback(async () => {
    setLoading(true);
    setError(null);

    try {
      const data = await get('/boards');
      setBoards(data || []);
    } catch (err) {
      setError(err);
      setBoards([]);
    } finally {
      setLoading(false);
    }
  }, [get]);

  useEffect(() => {
    fetchBoards();
  }, [fetchBoards]);

  /**
   * Create a new board and navigate to it.
   */
  const handleCreateBoard = async (event) => {
    event.preventDefault();
    const trimmedName = newBoardName.trim();
    if (!trimmedName) return;

    setCreateLoading(true);

    try {
      const newBoard = await post('/boards', { name: trimmedName });
      setBoards((prev) => [...prev, newBoard]);
      setNewBoardName('');
      setIsCreating(false);
      // Navigate to the new board
      navigate(`/boards/${newBoard.id}`);
    } catch (err) {
      setError(err);
    } finally {
      setCreateLoading(false);
    }
  };

  /**
   * Format date for display.
   */
  const formatDate = (dateString) => {
    if (!dateString) return '';
    const date = new Date(dateString);
    return date.toLocaleDateString(undefined, {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
    });
  };

  /**
   * Handle keyboard navigation on board cards.
   */
  const handleBoardKeyDown = (event, boardId) => {
    if (event.key === 'Enter' || event.key === ' ') {
      event.preventDefault();
      navigate(`/boards/${boardId}`);
    }
  };

  if (loading) {
    return (
      <div className="loading-state" role="status" aria-live="polite">
        <div className="loading-spinner" aria-hidden="true" />
        <p>Loading boards...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="error-state" role="alert">
        <p>
          <strong>Error loading boards</strong>
        </p>
        <p>{error.message}</p>
        <button
          className="btn btn-primary"
          onClick={fetchBoards}
          style={{ marginTop: '1rem' }}
        >
          Try Again
        </button>
      </div>
    );
  }

  return (
    <div className="board-list-page" data-testid="board-list">
      <header className="board-list-header">
        <h2>Your Boards</h2>
      </header>

      <div className="board-grid" role="list" aria-label="Boards">
        {boards.map((board) => (
          <div
            key={board.id}
            className="board-card"
            role="listitem"
            tabIndex={0}
            onClick={() => navigate(`/boards/${board.id}`)}
            onKeyDown={(e) => handleBoardKeyDown(e, board.id)}
            aria-label={`Open board: ${board.name}`}
            data-testid={`board-card-${board.id}`}
          >
            <h3>{board.name}</h3>
            <p className="board-card-meta">
              Created {formatDate(board.created_at)}
            </p>
          </div>
        ))}

        {/* Create New Board Card/Form */}
        {isCreating ? (
          <form
            className="create-board-form"
            onSubmit={handleCreateBoard}
            data-testid="create-board-form"
          >
            <label htmlFor="board-name">Board Name</label>
            <input
              id="board-name"
              type="text"
              className="inline-form-input"
              value={newBoardName}
              onChange={(e) => setNewBoardName(e.target.value)}
              placeholder="Enter board name..."
              autoFocus
              disabled={createLoading}
              data-testid="create-board-input"
            />
            <div className="inline-form-actions" style={{ marginTop: 'var(--space-3)' }}>
              <button
                type="submit"
                className="btn btn-primary btn-sm"
                disabled={createLoading || !newBoardName.trim()}
                data-testid="create-board-submit"
              >
                {createLoading ? 'Creating...' : 'Create Board'}
              </button>
              <button
                type="button"
                className="btn btn-secondary btn-sm"
                onClick={() => {
                  setIsCreating(false);
                  setNewBoardName('');
                }}
                disabled={createLoading}
              >
                Cancel
              </button>
            </div>
          </form>
        ) : (
          <button
            type="button"
            className="create-board-card"
            onClick={() => setIsCreating(true)}
            aria-label="Create new board"
            data-testid="create-board-button"
          >
            <span className="icon" aria-hidden="true">
              +
            </span>
            <span>Create New Board</span>
          </button>
        )}
      </div>

      {boards.length === 0 && !isCreating && (
        <p className="text-muted" style={{ textAlign: 'center', marginTop: 'var(--space-4)' }}>
          No boards yet. Create your first board to get started!
        </p>
      )}
    </div>
  );
}

export default BoardList;

