import { render, screen } from '@testing-library/react';
import userEvent from '@testing-library/user-event';
import Card from '../../src/components/Card';

describe('Card component', () => {
  const mockCard = {
    id: 'card-1',
    title: 'Test Card',
    description: 'Test description',
    position: 1,
  };

  it('should render card title', () => {
    render(<Card card={mockCard} />);

    expect(screen.getByRole('heading', { level: 4 })).toHaveTextContent('Test Card');
  });

  it('should render card description when present', () => {
    render(<Card card={mockCard} />);

    expect(screen.getByText('Test description')).toBeInTheDocument();
  });

  it('should not render description when not present', () => {
    const cardWithoutDescription = { id: 'card-2', title: 'No Description', position: 1 };
    render(<Card card={cardWithoutDescription} />);

    expect(screen.queryByText('Test description')).not.toBeInTheDocument();
  });

  it('should call onEdit when card is clicked', async () => {
    const user = userEvent.setup();
    const handleEdit = jest.fn();

    render(<Card card={mockCard} onEdit={handleEdit} />);

    await user.click(screen.getByRole('button'));

    expect(handleEdit).toHaveBeenCalledTimes(1);
    expect(handleEdit).toHaveBeenCalledWith(mockCard);
  });

  it('should call onEdit when Enter key is pressed', async () => {
    const user = userEvent.setup();
    const handleEdit = jest.fn();

    render(<Card card={mockCard} onEdit={handleEdit} />);

    const card = screen.getByRole('button');
    card.focus();
    await user.keyboard('{Enter}');

    expect(handleEdit).toHaveBeenCalledTimes(1);
    expect(handleEdit).toHaveBeenCalledWith(mockCard);
  });

  it('should call onEdit when Space key is pressed', async () => {
    const user = userEvent.setup();
    const handleEdit = jest.fn();

    render(<Card card={mockCard} onEdit={handleEdit} />);

    const card = screen.getByRole('button');
    card.focus();
    await user.keyboard(' ');

    expect(handleEdit).toHaveBeenCalledTimes(1);
    expect(handleEdit).toHaveBeenCalledWith(mockCard);
  });

  it('should be keyboard focusable', () => {
    render(<Card card={mockCard} />);

    const card = screen.getByRole('button');
    expect(card).toHaveAttribute('tabIndex', '0');
  });

  it('should have accessible label', () => {
    render(<Card card={mockCard} />);

    const card = screen.getByRole('button', { name: /edit card: test card/i });
    expect(card).toBeInTheDocument();
  });

  it('should have data-testid attribute', () => {
    render(<Card card={mockCard} />);

    expect(screen.getByTestId('card-card-1')).toBeInTheDocument();
  });
});

import { render, screen, waitFor } from '@testing-library/react';
import userEvent from '@testing-library/user-event';
import AddCardForm from '../../src/components/AddCardForm';

describe('AddCardForm component', () => {
  it('should render add card button when collapsed', () => {
    render(<AddCardForm onSubmit={jest.fn()} />);

    expect(screen.getByRole('button', { name: /add a card/i })).toBeInTheDocument();
  });

  it('should expand to show form when button is clicked', async () => {
    const user = userEvent.setup();
    render(<AddCardForm onSubmit={jest.fn()} />);

    await user.click(screen.getByRole('button', { name: /add a card/i }));

    expect(screen.getByTestId('add-card-form')).toBeInTheDocument();
    expect(screen.getByLabelText(/card title/i)).toBeInTheDocument();
  });

  it('should focus input when form expands', async () => {
    const user = userEvent.setup();
    render(<AddCardForm onSubmit={jest.fn()} />);

    await user.click(screen.getByRole('button', { name: /add a card/i }));

    await waitFor(() => {
      expect(screen.getByTestId('add-card-input')).toHaveFocus();
    });
  });

  it('should call onSubmit with title when form is submitted', async () => {
    const user = userEvent.setup();
    const handleSubmit = jest.fn();

    render(<AddCardForm onSubmit={handleSubmit} />);

    await user.click(screen.getByRole('button', { name: /add a card/i }));
    await user.type(screen.getByTestId('add-card-input'), 'New Card Title');
    await user.click(screen.getByTestId('add-card-submit'));

    expect(handleSubmit).toHaveBeenCalledTimes(1);
    expect(handleSubmit).toHaveBeenCalledWith('New Card Title');
  });

  it('should trim whitespace from title before submitting', async () => {
    const user = userEvent.setup();
    const handleSubmit = jest.fn();

    render(<AddCardForm onSubmit={handleSubmit} />);

    await user.click(screen.getByRole('button', { name: /add a card/i }));
    await user.type(screen.getByTestId('add-card-input'), '  Trimmed Title  ');
    await user.click(screen.getByTestId('add-card-submit'));

    expect(handleSubmit).toHaveBeenCalledWith('Trimmed Title');
  });

  it('should clear input after successful submission', async () => {
    const user = userEvent.setup();
    render(<AddCardForm onSubmit={jest.fn()} />);

    await user.click(screen.getByRole('button', { name: /add a card/i }));
    await user.type(screen.getByTestId('add-card-input'), 'Test');
    await user.click(screen.getByTestId('add-card-submit'));

    expect(screen.getByTestId('add-card-input')).toHaveValue('');
  });

  it('should not submit empty title', async () => {
    const user = userEvent.setup();
    const handleSubmit = jest.fn();

    render(<AddCardForm onSubmit={handleSubmit} />);

    await user.click(screen.getByRole('button', { name: /add a card/i }));
    await user.click(screen.getByTestId('add-card-submit'));

    expect(handleSubmit).not.toHaveBeenCalled();
  });

  it('should collapse when cancel button is clicked', async () => {
    const user = userEvent.setup();
    render(<AddCardForm onSubmit={jest.fn()} />);

    await user.click(screen.getByRole('button', { name: /add a card/i }));
    expect(screen.getByTestId('add-card-form')).toBeInTheDocument();

    await user.click(screen.getByTestId('add-card-cancel'));

    expect(screen.queryByTestId('add-card-form')).not.toBeInTheDocument();
    expect(screen.getByRole('button', { name: /add a card/i })).toBeInTheDocument();
  });

  it('should collapse when Escape key is pressed', async () => {
    const user = userEvent.setup();
    render(<AddCardForm onSubmit={jest.fn()} />);

    await user.click(screen.getByRole('button', { name: /add a card/i }));
    await user.keyboard('{Escape}');

    expect(screen.queryByTestId('add-card-form')).not.toBeInTheDocument();
  });

  it('should disable form when disabled prop is true', async () => {
    const user = userEvent.setup();
    render(<AddCardForm onSubmit={jest.fn()} disabled />);

    // First expand the form
    await user.click(screen.getByRole('button', { name: /add a card/i }));

    expect(screen.getByTestId('add-card-input')).toBeDisabled();
    expect(screen.getByTestId('add-card-submit')).toBeDisabled();
    expect(screen.getByTestId('add-card-cancel')).toBeDisabled();
  });

  it('should have submit button disabled when input is empty', async () => {
    const user = userEvent.setup();
    render(<AddCardForm onSubmit={jest.fn()} />);

    await user.click(screen.getByRole('button', { name: /add a card/i }));

    expect(screen.getByTestId('add-card-submit')).toBeDisabled();
  });

  it('should enable submit button when input has text', async () => {
    const user = userEvent.setup();
    render(<AddCardForm onSubmit={jest.fn()} />);

    await user.click(screen.getByRole('button', { name: /add a card/i }));
    await user.type(screen.getByTestId('add-card-input'), 'Text');

    expect(screen.getByTestId('add-card-submit')).toBeEnabled();
  });
});

import { render, screen, waitFor } from '@testing-library/react';
import userEvent from '@testing-library/user-event';
import CardModal from '../../src/components/CardModal';

describe('CardModal component', () => {
  const mockCard = {
    id: 'card-1',
    title: 'Test Card',
    description: 'Test description',
    position: 1,
  };

  const defaultProps = {
    card: mockCard,
    onSave: jest.fn(),
    onDelete: jest.fn(),
    onClose: jest.fn(),
    isLoading: false,
  };

  beforeEach(() => {
    jest.clearAllMocks();
  });

  it('should render modal with card title', () => {
    render(<CardModal {...defaultProps} />);

    expect(screen.getByRole('dialog')).toBeInTheDocument();
    expect(screen.getByTestId('card-title-input')).toHaveValue('Test Card');
  });

  it('should render modal with card description', () => {
    render(<CardModal {...defaultProps} />);

    expect(screen.getByTestId('card-description-input')).toHaveValue('Test description');
  });

  it('should focus title input on mount', async () => {
    render(<CardModal {...defaultProps} />);

    await waitFor(() => {
      expect(screen.getByTestId('card-title-input')).toHaveFocus();
    });
  });

  it('should call onSave with updated data when form is submitted', async () => {
    const user = userEvent.setup();
    const handleSave = jest.fn();

    render(<CardModal {...defaultProps} onSave={handleSave} />);

    const titleInput = screen.getByTestId('card-title-input');
    await user.clear(titleInput);
    await user.type(titleInput, 'Updated Title');

    const descInput = screen.getByTestId('card-description-input');
    await user.clear(descInput);
    await user.type(descInput, 'Updated description');

    await user.click(screen.getByTestId('card-save-button'));

    expect(handleSave).toHaveBeenCalledTimes(1);
    expect(handleSave).toHaveBeenCalledWith({
      ...mockCard,
      title: 'Updated Title',
      description: 'Updated description',
    });
