    await waitFor(() => {
      expect(screen.getByTestId('column-col-1')).toBeInTheDocument();
    });

    await user.click(screen.getAllByTestId('delete-column-button')[0]);

    await waitFor(() => {
      expect(global.fetch).toHaveBeenCalledTimes(2);
    });
  });

  it('should close card modal when close button is clicked', async () => {
    const user = userEvent.setup();
    global.fetch.mockResolvedValueOnce({
      ok: true,
      status: 200,
      headers: { get: () => 'application/json' },
      json: () => Promise.resolve(mockBoard),
    });

    renderBoard();

    await waitFor(() => {
      expect(screen.getByTestId('card-card-1')).toBeInTheDocument();
    });

    await user.click(screen.getByTestId('card-card-1'));
    expect(screen.getByRole('dialog')).toBeInTheDocument();

    await user.click(screen.getByTestId('modal-close'));

    await waitFor(() => {
      expect(screen.queryByRole('dialog')).not.toBeInTheDocument();
    });
  });

  it('should sort columns by position', async () => {
    const boardWithUnsortedColumns = {
      ...mockBoard,
      columns: [
        { id: 'col-2', name: 'Second', position: 2, cards: [] },
        { id: 'col-1', name: 'First', position: 1, cards: [] },
        { id: 'col-3', name: 'Third', position: 3, cards: [] },
      ],
    };

    global.fetch.mockResolvedValueOnce({
      ok: true,
      status: 200,
      headers: { get: () => 'application/json' },
      json: () => Promise.resolve(boardWithUnsortedColumns),
    });

    renderBoard();

    await waitFor(() => {
      expect(screen.getByText('First')).toBeInTheDocument();
    });

    const columns = screen.getAllByRole('region');
    expect(columns[0]).toHaveAccessibleName(/column: first/i);
    expect(columns[1]).toHaveAccessibleName(/column: second/i);
    expect(columns[2]).toHaveAccessibleName(/column: third/i);
  });

  it('should have proper data-testid attributes', async () => {
    global.fetch.mockResolvedValueOnce({
      ok: true,
      status: 200,
      headers: { get: () => 'application/json' },
      json: () => Promise.resolve(mockBoard),
    });

    renderBoard();

    await waitFor(() => {
      expect(screen.getByTestId('board-view')).toBeInTheDocument();
      expect(screen.getByTestId('board-columns')).toBeInTheDocument();
    });
  });
});

