import { render, screen } from '@testing-library/react';
import userEvent from '@testing-library/user-event';
import Column from '../../src/components/Column';

// Mock window.confirm for delete confirmation
beforeEach(() => {
  jest.spyOn(window, 'confirm').mockReturnValue(true);
});

afterEach(() => {
  jest.restoreAllMocks();
});

describe('Column component', () => {
  const mockColumn = {
    id: 'col-1',
    name: 'To Do',
    position: 1,
    cards: [
      { id: 'card-1', title: 'Card 1', description: 'Desc 1', position: 1 },
      { id: 'card-2', title: 'Card 2', description: 'Desc 2', position: 2 },
    ],
  };

  it('should render column name', () => {
    render(<Column column={mockColumn} />);

    expect(screen.getByRole('heading', { level: 3 })).toHaveTextContent('To Do');
  });

  it('should render card count', () => {
    render(<Column column={mockColumn} />);

    expect(screen.getByText('2')).toBeInTheDocument();
    expect(screen.getByLabelText('2 cards')).toBeInTheDocument();
  });

  it('should render all cards in the column', () => {
    render(<Column column={mockColumn} />);

    expect(screen.getByText('Card 1')).toBeInTheDocument();
    expect(screen.getByText('Card 2')).toBeInTheDocument();
  });

  it('should sort cards by position', () => {
    const columnWithUnsortedCards = {
      ...mockColumn,
      cards: [
        { id: 'card-2', title: 'Card 2', position: 2 },
        { id: 'card-1', title: 'Card 1', position: 1 },
        { id: 'card-3', title: 'Card 3', position: 3 },
      ],
    };

    render(<Column column={columnWithUnsortedCards} />);

    const cards = screen.getAllByRole('listitem');
    expect(cards).toHaveLength(3);
    // Cards should be sorted by position
    expect(cards[0]).toHaveTextContent('Card 1');
    expect(cards[1]).toHaveTextContent('Card 2');
    expect(cards[2]).toHaveTextContent('Card 3');
  });

  it('should call onEditCard when card is clicked', async () => {
    const user = userEvent.setup();
    const handleEditCard = jest.fn();

    render(<Column column={mockColumn} onEditCard={handleEditCard} />);

    await user.click(screen.getByTestId('card-card-1'));

    expect(handleEditCard).toHaveBeenCalledTimes(1);
    expect(handleEditCard).toHaveBeenCalledWith(mockColumn.cards[0]);
  });

  it('should render add card form', () => {
    render(<Column column={mockColumn} />);

    expect(screen.getByRole('button', { name: /add a card/i })).toBeInTheDocument();
  });

  it('should call onCreateCard when card is created', async () => {
    const user = userEvent.setup();
    const handleCreateCard = jest.fn().mockResolvedValue({ id: 'new-card' });

    render(<Column column={mockColumn} onCreateCard={handleCreateCard} />);

    await user.click(screen.getByRole('button', { name: /add a card/i }));
    await user.type(screen.getByTestId('add-card-input'), 'New Card');
    await user.click(screen.getByTestId('add-card-submit'));

    expect(handleCreateCard).toHaveBeenCalledWith('col-1', 'New Card');
  });

  it('should call onDeleteColumn when delete button is clicked and confirmed', async () => {
    const user = userEvent.setup();
    const handleDeleteColumn = jest.fn();

    render(<Column column={mockColumn} onDeleteColumn={handleDeleteColumn} />);

    await user.click(screen.getByTestId('delete-column-button'));

    expect(window.confirm).toHaveBeenCalledWith(
      'Delete column "To Do" and all its cards?'
    );
    expect(handleDeleteColumn).toHaveBeenCalledWith('col-1');
  });

  it('should not call onDeleteColumn when delete is cancelled', async () => {
    const user = userEvent.setup();
    const handleDeleteColumn = jest.fn();
    window.confirm.mockReturnValueOnce(false);

    render(<Column column={mockColumn} onDeleteColumn={handleDeleteColumn} />);

    await user.click(screen.getByTestId('delete-column-button'));

    expect(handleDeleteColumn).not.toHaveBeenCalled();
  });

  it('should handle empty cards array', () => {
    const emptyColumn = { ...mockColumn, cards: [] };

    render(<Column column={emptyColumn} />);

    expect(screen.getByText('0')).toBeInTheDocument();
    // Cards container should still exist
    expect(screen.getByRole('list', { name: /cards/i })).toBeInTheDocument();
  });

  it('should handle missing cards array', () => {
    const columnWithoutCards = { id: 'col-1', name: 'Empty', position: 1 };

    render(<Column column={columnWithoutCards} />);

    expect(screen.getByText('0')).toBeInTheDocument();
  });

  it('should have proper accessibility attributes', () => {
    render(<Column column={mockColumn} />);

    expect(screen.getByRole('region', { name: /column: to do/i })).toBeInTheDocument();
    expect(screen.getByRole('list', { name: /cards/i })).toBeInTheDocument();
  });

  it('should disable delete button when isLoading is true', () => {
    render(<Column column={mockColumn} isLoading />);

    expect(screen.getByTestId('delete-column-button')).toBeDisabled();
  });
});

