      ok: true,
      status: 200,
      headers: { get: () => 'application/json' },
      json: () => Promise.resolve(mockBoard),
    });

    renderBoard();

    await waitFor(() => {
      expect(screen.getByTestId('column-col-1')).toBeInTheDocument();
    });

    expect(screen.getByText('Task 1')).toBeInTheDocument();
  });

  it('should show add column button', async () => {
    global.fetch.mockResolvedValueOnce({
      ok: true,
      status: 200,
      headers: { get: () => 'application/json' },
      json: () => Promise.resolve(mockBoard),
    });

    renderBoard();

    await waitFor(() => {
      expect(screen.getByTestId('add-column-button')).toBeInTheDocument();
    });
  });

  it('should expand to show add column form when button is clicked', async () => {
    const user = userEvent.setup();
    global.fetch.mockResolvedValueOnce({
      ok: true,
      status: 200,
      headers: { get: () => 'application/json' },
      json: () => Promise.resolve(mockBoard),
    });

    renderBoard();

    await waitFor(() => {
      expect(screen.getByTestId('add-column-button')).toBeInTheDocument();
    });

    await user.click(screen.getByTestId('add-column-button'));

    expect(screen.getByTestId('add-column-input')).toBeInTheDocument();
    expect(screen.getByTestId('add-column-submit')).toBeInTheDocument();
  });

  it('should create column when form is submitted', async () => {
    const user = userEvent.setup();
    const newColumn = { id: 'col-new', name: 'Done', position: 3, cards: [] };

