import { renderHook, waitFor, act } from '@testing-library/react';
import { useBoard } from '../../src/hooks/useBoard';

describe('useBoard hook', () => {
  const mockBoard = {
    id: 'board-1',
    name: 'Test Board',
    columns: [
      {
        id: 'col-1',
        name: 'To Do',
        position: 1,
        cards: [
          { id: 'card-1', title: 'Task 1', description: 'Desc 1', position: 1 },
        ],
      },
    ],
  };

  beforeEach(() => {
    jest.clearAllMocks();
    global.fetch.mockReset();
  });

  it('should fetch board data on mount', async () => {
    global.fetch.mockResolvedValueOnce({
      ok: true,
      status: 200,
      headers: { get: () => 'application/json' },
      json: () => Promise.resolve(mockBoard),
    });

    const { result } = renderHook(() => useBoard('board-1'));

    expect(result.current.loading).toBe(true);

    await waitFor(() => {
      expect(result.current.loading).toBe(false);
    });

    expect(result.current.board).toEqual(mockBoard);
    expect(result.current.error).toBeNull();
  });

  it('should handle fetch error', async () => {
    global.fetch.mockRejectedValueOnce(new Error('Network error'));

    const { result } = renderHook(() => useBoard('board-1'));

    await waitFor(() => {
      expect(result.current.loading).toBe(false);
    });

    expect(result.current.board).toBeNull();
    expect(result.current.error).toBeTruthy();
  });

  it('should not fetch when boardId is falsy', async () => {
    const { result } = renderHook(() => useBoard(null));

    expect(result.current.loading).toBe(false);
    expect(result.current.board).toBeNull();
    expect(global.fetch).not.toHaveBeenCalled();
  });

  it('should create a column', async () => {
    const newColumn = { id: 'col-2', name: 'Done', position: 2 };

    global.fetch
      .mockResolvedValueOnce({
        ok: true,
        status: 200,
        headers: { get: () => 'application/json' },
        json: () => Promise.resolve(mockBoard),
      })
      .mockResolvedValueOnce({
        ok: true,
        status: 201,
        headers: { get: () => 'application/json' },
        json: () => Promise.resolve(newColumn),
      });

    const { result } = renderHook(() => useBoard('board-1'));

    await waitFor(() => {
      expect(result.current.loading).toBe(false);
    });

    await act(async () => {
      await result.current.createColumn('Done');
    });

    expect(result.current.board.columns).toHaveLength(2);
    expect(result.current.board.columns[1].name).toBe('Done');
  });

  it('should delete a column', async () => {
    global.fetch
      .mockResolvedValueOnce({
        ok: true,
        status: 200,
        headers: { get: () => 'application/json' },
        json: () => Promise.resolve(mockBoard),
      })
      .mockResolvedValueOnce({
        ok: true,
        status: 204,
        headers: { get: () => null },
      });

    const { result } = renderHook(() => useBoard('board-1'));

    await waitFor(() => {
      expect(result.current.loading).toBe(false);
    });

    await act(async () => {
      await result.current.deleteColumn('col-1');
    });

    expect(result.current.board.columns).toHaveLength(0);
  });

  it('should create a card', async () => {
    const newCard = { id: 'card-2', title: 'New Task', description: '', position: 2 };

    global.fetch
      .mockResolvedValueOnce({
        ok: true,
        status: 200,
        headers: { get: () => 'application/json' },
        json: () => Promise.resolve(mockBoard),
      })
      .mockResolvedValueOnce({
        ok: true,
        status: 201,
        headers: { get: () => 'application/json' },
        json: () => Promise.resolve(newCard),
      });

    const { result } = renderHook(() => useBoard('board-1'));

    await waitFor(() => {
      expect(result.current.loading).toBe(false);
    });

    await act(async () => {
      await result.current.createCard('col-1', 'New Task');
    });

    expect(result.current.board.columns[0].cards).toHaveLength(2);
    expect(result.current.board.columns[0].cards[1].title).toBe('New Task');
  });

  it('should update a card', async () => {
    const updatedCard = { id: 'card-1', title: 'Updated Task', description: 'Updated Desc' };

    global.fetch
      .mockResolvedValueOnce({
        ok: true,
        status: 200,
        headers: { get: () => 'application/json' },
        json: () => Promise.resolve(mockBoard),
      })
      .mockResolvedValueOnce({
        ok: true,
        status: 200,
        headers: { get: () => 'application/json' },
        json: () => Promise.resolve(updatedCard),
      });

    const { result } = renderHook(() => useBoard('board-1'));

    await waitFor(() => {
      expect(result.current.loading).toBe(false);
    });

    await act(async () => {
      await result.current.updateCard('card-1', { title: 'Updated Task', description: 'Updated Desc' });
    });

    expect(result.current.board.columns[0].cards[0].title).toBe('Updated Task');
    expect(result.current.board.columns[0].cards[0].description).toBe('Updated Desc');
  });

  it('should delete a card', async () => {
    global.fetch
      .mockResolvedValueOnce({
        ok: true,
        status: 200,
        headers: { get: () => 'application/json' },
        json: () => Promise.resolve(mockBoard),
      })
      .mockResolvedValueOnce({
        ok: true,
        status: 204,
        headers: { get: () => null },
      });

    const { result } = renderHook(() => useBoard('board-1'));

    await waitFor(() => {
      expect(result.current.loading).toBe(false);
    });

    await act(async () => {
      await result.current.deleteCard('card-1');
    });

    expect(result.current.board.columns[0].cards).toHaveLength(0);
  });

  it('should provide clearError function', async () => {
    global.fetch.mockRejectedValueOnce(new Error('Network error'));

    const { result } = renderHook(() => useBoard('board-1'));

    await waitFor(() => {
      expect(result.current.error).toBeTruthy();
    });

    act(() => {
      result.current.clearError();
    });

    expect(result.current.error).toBeNull();
  });

  it('should provide refresh function', async () => {
    global.fetch.mockResolvedValue({
      ok: true,
      status: 200,
      headers: { get: () => 'application/json' },
      json: () => Promise.resolve(mockBoard),
    });

    const { result } = renderHook(() => useBoard('board-1'));

    await waitFor(() => {
      expect(result.current.loading).toBe(false);
    });

    expect(global.fetch).toHaveBeenCalledTimes(1);

    await act(async () => {
      await result.current.refresh();
    });

    expect(global.fetch).toHaveBeenCalledTimes(2);
  });

  it('should track operationLoading state', async () => {
    global.fetch
      .mockResolvedValueOnce({
        ok: true,
        status: 200,
        headers: { get: () => 'application/json' },
        json: () => Promise.resolve(mockBoard),
      })
      .mockImplementationOnce(
        () =>
          new Promise((resolve) =>
            setTimeout(
              () =>
                resolve({
                  ok: true,
                  status: 201,
                  headers: { get: () => 'application/json' },
                  json: () => Promise.resolve({ id: 'col-2', name: 'New', position: 2 }),
                }),
              100
            )
          )
      );

    const { result } = renderHook(() => useBoard('board-1'));

    await waitFor(() => {
      expect(result.current.loading).toBe(false);
    });

    expect(result.current.operationLoading).toBe(false);

    // Start operation - don't await
    let createPromise;
    act(() => {
      createPromise = result.current.createColumn('New');
    });

    // Check operationLoading is true during operation
    expect(result.current.operationLoading).toBe(true);

    // Wait for operation to complete
    await act(async () => {
      await createPromise;
    });

    expect(result.current.operationLoading).toBe(false);
  });
});

import { render, screen, waitFor } from '@testing-library/react';
import userEvent from '@testing-library/user-event';
import App from '../src/App';

describe('App component', () => {
  beforeEach(() => {
    jest.clearAllMocks();
    global.fetch.mockReset();
    // Default mock for board list
    global.fetch.mockResolvedValue({
      ok: true,
      status: 200,
      headers: { get: () => 'application/json' },
      json: () => Promise.resolve([]),
    });
  });

  it('should render the application header', async () => {
    render(<App />);

    expect(screen.getByRole('heading', { level: 1 })).toHaveTextContent(
      'Kanban Task Board'
    );
  });

  it('should render navigation with Boards and Reports links', async () => {
    render(<App />);

    const nav = screen.getByRole('navigation', { name: /main navigation/i });
    expect(nav).toBeInTheDocument();

    expect(screen.getByRole('link', { name: /boards/i })).toBeInTheDocument();
    expect(screen.getByRole('link', { name: /reports/i })).toBeInTheDocument();
  });

  it('should have Boards link as active by default', async () => {
    render(<App />);

    await waitFor(() => {
      const boardsLink = screen.getByRole('link', { name: /boards/i });
      expect(boardsLink).toHaveClass('active');
    });
  });

  it('should render main content area', async () => {
    render(<App />);

    expect(screen.getByRole('main')).toBeInTheDocument();
  });

  it('should render BoardList on root route', async () => {
    render(<App />);

    await waitFor(() => {
      expect(screen.getByTestId('board-list')).toBeInTheDocument();
    });
  });

  it('should navigate to Reports page when Reports link is clicked', async () => {
    const user = userEvent.setup();
    render(<App />);

    const reportsLink = screen.getByRole('link', { name: /reports/i });
    await user.click(reportsLink);

    await waitFor(() => {
      expect(screen.getByTestId('reports-page')).toBeInTheDocument();
    });
  });

  it('should mark Reports link as active on reports route', async () => {
    const user = userEvent.setup();
    render(<App />);

    const reportsLink = screen.getByRole('link', { name: /reports/i });
    await user.click(reportsLink);

    await waitFor(() => {
      expect(reportsLink).toHaveClass('active');
    });
  });

  it('should navigate back to Boards from Reports', async () => {
    const user = userEvent.setup();
    render(<App />);

    await user.click(screen.getByRole('link', { name: /reports/i }));
    await waitFor(() => {
      expect(screen.getByTestId('reports-page')).toBeInTheDocument();
    });

    await user.click(screen.getByRole('link', { name: /boards/i }));
    await waitFor(() => {
      expect(screen.getByTestId('board-list')).toBeInTheDocument();
    });
  });

  it('should be accessible with proper heading hierarchy', async () => {
    render(<App />);

    // Main heading
    const h1 = screen.getByRole('heading', { level: 1 });
    expect(h1).toBeInTheDocument();
    expect(h1).toHaveTextContent('Kanban Task Board');
  });

  it('should have accessible navigation landmark', async () => {
    render(<App />);

    const nav = screen.getByRole('navigation', { name: /main navigation/i });
    expect(nav).toBeInTheDocument();
  });

  it('should have accessible main landmark', async () => {
    render(<App />);

    const main = screen.getByRole('main');
    expect(main).toBeInTheDocument();
  });
});

