import App from '../src/App';

describe('App component', () => {
  it('should render the application header', () => {
    render(<App />);

    expect(screen.getByRole('heading', { level: 1 })).toHaveTextContent(
      'Kanban Task Board'
    );
  });

  it('should render navigation with Board and Reports links', () => {
    render(<App />);

    const nav = screen.getByRole('navigation', { name: /main navigation/i });
    expect(nav).toBeInTheDocument();

    expect(screen.getByRole('link', { name: /board/i })).toBeInTheDocument();
    expect(screen.getByRole('link', { name: /reports/i })).toBeInTheDocument();
  });

  it('should have Board link as active by default', () => {
    render(<App />);

    const boardLink = screen.getByRole('link', { name: /board/i });
    expect(boardLink).toHaveClass('active');
  });

  it('should render main content area', () => {
    render(<App />);

    expect(screen.getByRole('main')).toBeInTheDocument();
  });

  it('should display welcome message', () => {
    render(<App />);

    expect(screen.getByText('Welcome to Kanban Task Board')).toBeInTheDocument();
  });

  it('should be accessible with proper heading hierarchy', () => {
    render(<App />);

    // Main heading
    const h1 = screen.getByRole('heading', { level: 1 });
    expect(h1).toBeInTheDocument();

    // Section heading (screen reader only)
    const h2 = screen.getByRole('heading', { level: 2 });
    expect(h2).toBeInTheDocument();
  });

  it('should have a labeled section for content', () => {
    render(<App />);

    const section = screen.getByRole('region', {
      name: /application content/i,
    });
    expect(section).toBeInTheDocument();
  });
});

