it('should handle createColumn error', async () => {
  global.fetch
    .mockResolvedValueOnce({
      ok: true,
      status: 200,
      headers: { get: () => 'application/json' },
      json: () => Promise.resolve(mockBoard),
    })
    .mockRejectedValueOnce(new Error('Create failed'));

  const { result } = renderHook(() => useBoard('board-1'));

  await waitFor(() => {
    expect(result.current.loading).toBe(false);
  });

  await expect(
    act(async () => {
      await result.current.createColumn('New');
    })
  ).rejects.toThrow();

  expect(result.current.error).toBeTruthy();
});

it('should handle updateColumn', async () => {
  const updatedColumn = { id: 'col-1', name: 'Updated Column', position: 1 };

  global.fetch
    .mockResolvedValueOnce({
      ok: true,
      status: 200,
      headers: { get: () => 'application/json' },
      json: () => Promise.resolve(mockBoard),
    })
    .mockResolvedValueOnce({
      ok: true,
      status: 200,
      headers: { get: () => 'application/json' },
      json: () => Promise.resolve(updatedColumn),
    });

  const { result } = renderHook(() => useBoard('board-1'));

  await waitFor(() => {
    expect(result.current.loading).toBe(false);
  });

  await act(async () => {
    await result.current.updateColumn('col-1', { name: 'Updated Column' });
  });

  expect(result.current.board.columns[0].name).toBe('Updated Column');
});

it('should handle moveCard within same column', async () => {
  const boardWithMultipleCards = {
    ...mockBoard,
    columns: [
      {
        id: 'col-1',
        name: 'To Do',
        position: 1,
        cards: [
          { id: 'card-1', title: 'Task 1', position: 1 },
          { id: 'card-2', title: 'Task 2', position: 2 },
        ],
      },
    ],
  };

  const movedCard = { id: 'card-1', column_id: 'col-1', position: 2 };

  global.fetch
    .mockResolvedValueOnce({
      ok: true,
      status: 200,
      headers: { get: () => 'application/json' },
      json: () => Promise.resolve(boardWithMultipleCards),
    })
    .mockResolvedValueOnce({
      ok: true,
      status: 200,
      headers: { get: () => 'application/json' },
      json: () => Promise.resolve(movedCard),
    });

  const { result } = renderHook(() => useBoard('board-1'));

  await waitFor(() => {
    expect(result.current.loading).toBe(false);
  });

  await act(async () => {
