import { useState, useCallback } from 'react';

/**
 * Base URL for API requests, configured via environment variable.
 * Falls back to localhost:3001/api for local development.
 */
const API_BASE_URL = import.meta.env.VITE_API_URL || 'http://localhost:3001/api';

/**
 * Custom error class for API errors with additional context.
 */
class ApiError extends Error {
  constructor(message, code, status) {
    super(message);
    this.name = 'ApiError';
    this.code = code;
    this.status = status;
  }
}

/**
 * Parses the response body as JSON and handles API error responses.
 *
 * @param {Response} response - Fetch Response object
 * @returns {Promise<any>} Parsed response data
 * @throws {ApiError} When the response indicates an error
 */
async function parseResponse(response) {
  // Handle 204 No Content responses
  if (response.status === 204) {
    return null;
  }

  const contentType = response.headers.get('content-type');

  // Handle non-JSON responses
  if (!contentType || !contentType.includes('application/json')) {
    if (!response.ok) {
      throw new ApiError(
        `Request failed with status ${response.status}`,
        'REQUEST_FAILED',
        response.status
      );
    }
    return response.text();
  }

  const data = await response.json();

  if (!response.ok) {
    // Extract error details from response body
    const errorMessage = data?.error?.message || data?.message || 'Request failed';
    const errorCode = data?.error?.code || 'UNKNOWN_ERROR';
    throw new ApiError(errorMessage, errorCode, response.status);
  }

  return data;
}

/**
 * Makes an HTTP request to the API.
 *
 * @param {string} endpoint - API endpoint (relative to base URL)
 * @param {Object} options - Fetch options
 * @param {string} [options.method='GET'] - HTTP method
 * @param {Object} [options.body] - Request body (will be JSON stringified)
 * @param {Object} [options.headers] - Additional headers
 * @returns {Promise<any>} Response data
 */
async function request(endpoint, options = {}) {
  const { method = 'GET', body, headers = {} } = options;

  const url = endpoint.startsWith('http') ? endpoint : `${API_BASE_URL}${endpoint}`;

  const fetchOptions = {
    method,
    headers: {
      'Content-Type': 'application/json',
      ...headers,
    },
  };

  if (body && method !== 'GET' && method !== 'HEAD') {
    fetchOptions.body = JSON.stringify(body);
  }

  const response = await fetch(url, fetchOptions);
  return parseResponse(response);
}

/**
 * Hook for making API requests with loading and error state management.
 *
 * Returns an object with HTTP method functions (get, post, put, patch, del)
 * and state (loading, error, data).
 *
 * @returns {Object} API methods and state
 *
 * @example
 * ```jsx
 * function MyComponent() {
 *   const { get, post, loading, error, data } = useApi();
 *
 *   useEffect(() => {
 *     get('/boards');
 *   }, [get]);
 *
 *   if (loading) return <p>Loading...</p>;
 *   if (error) return <p>Error: {error.message}</p>;
 *   return <pre>{JSON.stringify(data, null, 2)}</pre>;
 * }
 * ```
 */
function useApi() {
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [data, setData] = useState(null);

  /**
   * Wraps an API call with loading and error state management.
   */
  const execute = useCallback(async (apiCall) => {
    setLoading(true);
    setError(null);

    try {
      const result = await apiCall();
      setData(result);
      return result;
    } catch (err) {
      const apiError =
        err instanceof ApiError
          ? err
          : new ApiError(err.message || 'Network error', 'NETWORK_ERROR', 0);
      setError(apiError);
      throw apiError;
    } finally {
      setLoading(false);
    }
  }, []);

  /**
   * Makes a GET request.
   * @param {string} endpoint - API endpoint
   * @returns {Promise<any>} Response data
   */
  const get = useCallback(
    (endpoint) => execute(() => request(endpoint, { method: 'GET' })),
    [execute]
  );

  /**
   * Makes a POST request.
   * @param {string} endpoint - API endpoint
   * @param {Object} body - Request body
   * @returns {Promise<any>} Response data
   */
  const post = useCallback(
    (endpoint, body) => execute(() => request(endpoint, { method: 'POST', body })),
    [execute]
  );

  /**
   * Makes a PUT request.
   * @param {string} endpoint - API endpoint
   * @param {Object} body - Request body
   * @returns {Promise<any>} Response data
   */
  const put = useCallback(
    (endpoint, body) => execute(() => request(endpoint, { method: 'PUT', body })),
    [execute]
  );

  /**
   * Makes a PATCH request.
   * @param {string} endpoint - API endpoint
   * @param {Object} body - Request body
   * @returns {Promise<any>} Response data
   */
  const patch = useCallback(
    (endpoint, body) => execute(() => request(endpoint, { method: 'PATCH', body })),
    [execute]
  );

  /**
   * Makes a DELETE request.
   * Named 'del' to avoid conflict with JavaScript reserved word.
   * @param {string} endpoint - API endpoint
   * @returns {Promise<any>} Response data
   */
  const del = useCallback(
    (endpoint) => execute(() => request(endpoint, { method: 'DELETE' })),
    [execute]
  );

  /**
   * Clears the current error state.
   */
  const clearError = useCallback(() => {
    setError(null);
  }, []);

  /**
   * Resets all state to initial values.
   */
  const reset = useCallback(() => {
    setLoading(false);
    setError(null);
    setData(null);
  }, []);

  return {
    // HTTP methods
    get,
    post,
    put,
    patch,
    del,
    // State
    loading,
    error,
    data,
    // Utilities
    clearError,
    reset,
  };
}

export { useApi, ApiError, request, API_BASE_URL };
export default useApi;

