/**
 * Environment variable utilities for accessing Vite environment variables.
 * This module abstracts the import.meta.env access to allow for easier
 * testing and mocking.
 */

/**
 * Gets an environment variable value from Vite's import.meta.env.
 * Provides a fallback value if the variable is not set.
 *
 * @param {string} key - The environment variable key (e.g., 'VITE_API_URL')
 * @param {string} [fallback=''] - Default value if the variable is not set
 * @returns {string} The environment variable value or fallback
 */
export function getEnvVar(key, fallback = '') {
  // In Vite, environment variables are available via import.meta.env
  // This syntax is transformed at build time by Vite
  const env = import.meta.env || {};
  return env[key] ?? fallback;
}

/**
 * Checks if the current environment is development mode.
 *
 * @returns {boolean} True if in development mode
 */
export function isDev() {
  return import.meta.env?.DEV ?? false;
}

/**
 * Checks if the current environment is production mode.
 *
 * @returns {boolean} True if in production mode
 */
export function isProd() {
  return import.meta.env?.PROD ?? false;
}

