/**
 * Mock implementation of env.js for Jest testing.
 * Returns predictable values for environment variables.
 */

const mockEnv = {
  VITE_API_URL: 'http://localhost:3001/api',
  DEV: true,
  PROD: false,
};

export function getEnvVar(key, fallback = '') {
  return mockEnv[key] ?? fallback;
}

export function isDev() {
  return mockEnv.DEV;
}

export function isProd() {
  return mockEnv.PROD;
}

// Allow tests to modify mock values
export function __setMockEnv(key, value) {
  mockEnv[key] = value;
}

export function __resetMockEnv() {
  mockEnv.VITE_API_URL = 'http://localhost:3001/api';
  mockEnv.DEV = true;
  mockEnv.PROD = false;
}

