import { renderHook, act, waitFor } from '@testing-library/react';
import { useApi, ApiError, request, API_BASE_URL } from '../src/hooks/useApi';

describe('useApi hook', () => {
  beforeEach(() => {
    global.fetch.mockReset();
  });

  describe('initial state', () => {
    it('should have initial loading state as false', () => {
      const { result } = renderHook(() => useApi());
      expect(result.current.loading).toBe(false);
    });

    it('should have initial error state as null', () => {
      const { result } = renderHook(() => useApi());
      expect(result.current.error).toBeNull();
    });

    it('should have initial data state as null', () => {
      const { result } = renderHook(() => useApi());
      expect(result.current.data).toBeNull();
    });
  });

  describe('GET requests', () => {
    it('should make GET request and return data', async () => {
      const mockData = [{ id: 1, name: 'Board 1' }];
      global.fetch.mockResolvedValueOnce({
        ok: true,
        status: 200,
        headers: new Headers({ 'content-type': 'application/json' }),
        json: () => Promise.resolve(mockData),
      });

      const { result } = renderHook(() => useApi());

      let response;
      await act(async () => {
        response = await result.current.get('/boards');
      });

      expect(response).toEqual(mockData);
      expect(result.current.data).toEqual(mockData);
      expect(result.current.loading).toBe(false);
      expect(result.current.error).toBeNull();
      expect(global.fetch).toHaveBeenCalledWith(
        `${API_BASE_URL}/boards`,
        expect.objectContaining({ method: 'GET' })
      );
    });

    it('should set loading state during request', async () => {
      let resolvePromise;
      global.fetch.mockReturnValueOnce(
        new Promise((resolve) => {
          resolvePromise = resolve;
        })
      );

      const { result } = renderHook(() => useApi());

      act(() => {
        result.current.get('/boards');
      });

      expect(result.current.loading).toBe(true);

      await act(async () => {
        resolvePromise({
          ok: true,
          status: 200,
          headers: new Headers({ 'content-type': 'application/json' }),
          json: () => Promise.resolve([]),
        });
      });

      expect(result.current.loading).toBe(false);
    });

    it('should handle GET request errors', async () => {
      global.fetch.mockResolvedValueOnce({
        ok: false,
        status: 404,
        headers: new Headers({ 'content-type': 'application/json' }),
        json: () =>
          Promise.resolve({
            error: { code: 'NOT_FOUND', message: 'Board not found' },
          }),
      });

      const { result } = renderHook(() => useApi());

      let caughtError;
      await act(async () => {
        try {
          await result.current.get('/boards/999');
        } catch (error) {
          caughtError = error;
        }
      });

      expect(caughtError).toBeDefined();
      expect(caughtError.message).toBe('Board not found');
      expect(result.current.error).toBeInstanceOf(ApiError);
      expect(result.current.error.code).toBe('NOT_FOUND');
      expect(result.current.error.status).toBe(404);
      expect(result.current.loading).toBe(false);
    });
  });

  describe('POST requests', () => {
    it('should make POST request with body', async () => {
      const requestBody = { name: 'New Board' };
      const mockResponse = { id: 1, name: 'New Board', created_at: '2026-01-16' };

      global.fetch.mockResolvedValueOnce({
        ok: true,
        status: 201,
        headers: new Headers({ 'content-type': 'application/json' }),
        json: () => Promise.resolve(mockResponse),
      });

      const { result } = renderHook(() => useApi());

      let response;
      await act(async () => {
        response = await result.current.post('/boards', requestBody);
      });

      expect(response).toEqual(mockResponse);
      expect(global.fetch).toHaveBeenCalledWith(
        `${API_BASE_URL}/boards`,
        expect.objectContaining({
          method: 'POST',
          body: JSON.stringify(requestBody),
        })
      );
    });
  });

  describe('PUT requests', () => {
    it('should make PUT request with body', async () => {
      const requestBody = { name: 'Updated Board' };
      const mockResponse = { id: 1, name: 'Updated Board', updated_at: '2026-01-16' };

      global.fetch.mockResolvedValueOnce({
        ok: true,
        status: 200,
        headers: new Headers({ 'content-type': 'application/json' }),
        json: () => Promise.resolve(mockResponse),
      });

      const { result } = renderHook(() => useApi());

      let response;
      await act(async () => {
        response = await result.current.put('/boards/1', requestBody);
      });

      expect(response).toEqual(mockResponse);
      expect(global.fetch).toHaveBeenCalledWith(
        `${API_BASE_URL}/boards/1`,
        expect.objectContaining({
          method: 'PUT',
          body: JSON.stringify(requestBody),
        })
      );
    });
  });

  describe('PATCH requests', () => {
    it('should make PATCH request with body', async () => {
      const requestBody = { columnId: 2, position: 1024 };
      const mockResponse = { id: 1, column_id: 2, position: 1024 };

      global.fetch.mockResolvedValueOnce({
        ok: true,
        status: 200,
        headers: new Headers({ 'content-type': 'application/json' }),
        json: () => Promise.resolve(mockResponse),
      });

      const { result } = renderHook(() => useApi());

      let response;
      await act(async () => {
        response = await result.current.patch('/cards/1/move', requestBody);
      });

      expect(response).toEqual(mockResponse);
      expect(global.fetch).toHaveBeenCalledWith(
        `${API_BASE_URL}/cards/1/move`,
        expect.objectContaining({
          method: 'PATCH',
          body: JSON.stringify(requestBody),
        })
      );
    });
  });

  describe('DELETE requests', () => {
    it('should make DELETE request', async () => {
      global.fetch.mockResolvedValueOnce({
        ok: true,
        status: 204,
        headers: new Headers(),
      });

      const { result } = renderHook(() => useApi());

      let response;
      await act(async () => {
        response = await result.current.del('/boards/1');
      });

      expect(response).toBeNull();
      expect(global.fetch).toHaveBeenCalledWith(
        `${API_BASE_URL}/boards/1`,
        expect.objectContaining({ method: 'DELETE' })
      );
    });
  });

  describe('error handling', () => {
    it('should handle network errors', async () => {
      global.fetch.mockRejectedValueOnce(new Error('Network error'));

      const { result } = renderHook(() => useApi());

      await expect(
        act(async () => {
          await result.current.get('/boards');
        })
      ).rejects.toThrow('Network error');

      expect(result.current.error).toBeInstanceOf(ApiError);
      expect(result.current.error.code).toBe('NETWORK_ERROR');
    });

    it('should handle non-JSON error responses', async () => {
      global.fetch.mockResolvedValueOnce({
        ok: false,
        status: 500,
        headers: new Headers({ 'content-type': 'text/html' }),
      });

      const { result } = renderHook(() => useApi());

      await expect(
        act(async () => {
          await result.current.get('/boards');
        })
      ).rejects.toThrow('Request failed with status 500');
    });
  });

  describe('utility functions', () => {
    it('should clear error with clearError', async () => {
      global.fetch.mockRejectedValueOnce(new Error('Network error'));

      const { result } = renderHook(() => useApi());

      await expect(
        act(async () => {
          await result.current.get('/boards');
        })
      ).rejects.toThrow();

      expect(result.current.error).not.toBeNull();

      act(() => {
        result.current.clearError();
      });

      expect(result.current.error).toBeNull();
    });

    it('should reset all state with reset', async () => {
      const mockData = [{ id: 1 }];
      global.fetch.mockResolvedValueOnce({
        ok: true,
        status: 200,
        headers: new Headers({ 'content-type': 'application/json' }),
        json: () => Promise.resolve(mockData),
      });

      const { result } = renderHook(() => useApi());

      await act(async () => {
        await result.current.get('/boards');
      });

      expect(result.current.data).toEqual(mockData);

      act(() => {
        result.current.reset();
      });

      expect(result.current.data).toBeNull();
      expect(result.current.error).toBeNull();
      expect(result.current.loading).toBe(false);
    });
  });
});

describe('request function', () => {
  it('should include Content-Type header', async () => {
    global.fetch.mockResolvedValueOnce({
      ok: true,
      status: 200,
      headers: new Headers({ 'content-type': 'application/json' }),
      json: () => Promise.resolve({}),
    });

    await request('/test');

    expect(global.fetch).toHaveBeenCalledWith(
      expect.any(String),
      expect.objectContaining({
        headers: expect.objectContaining({
          'Content-Type': 'application/json',
        }),
      })
    );
  });

  it('should handle absolute URLs', async () => {
    global.fetch.mockResolvedValueOnce({
      ok: true,
      status: 200,
      headers: new Headers({ 'content-type': 'application/json' }),
      json: () => Promise.resolve({}),
    });

    await request('http://other-api.com/endpoint');

    expect(global.fetch).toHaveBeenCalledWith(
      'http://other-api.com/endpoint',
      expect.any(Object)
    );
  });
});

describe('ApiError', () => {
  it('should create error with message, code, and status', () => {
    const error = new ApiError('Not found', 'NOT_FOUND', 404);

    expect(error.message).toBe('Not found');
    expect(error.code).toBe('NOT_FOUND');
    expect(error.status).toBe(404);
    expect(error.name).toBe('ApiError');
    expect(error).toBeInstanceOf(Error);
  });
});

