/**
 * Unit tests for board routes.
 * Tests all CRUD operations with mocked database using supertest.
 */
const request = require('supertest');
const express = require('express');
const { errorHandler } = require('../src/middleware/errorHandler');
const boardRoutes = require('../src/routes/boards');
const Board = require('../src/models/Board');

// Mock the Board model
jest.mock('../src/models/Board');

// Setup test app
const createApp = () => {
  const app = express();
  app.use(express.json());
  app.use('/api/boards', boardRoutes);
  app.use(errorHandler);
  return app;
};

describe('Board Routes', () => {
  let app;

  beforeEach(() => {
    app = createApp();
    jest.clearAllMocks();
  });

  describe('GET /api/boards', () => {
    it('should return empty array when no boards exist', async () => {
      Board.findAll.mockResolvedValue([]);

      const response = await request(app).get('/api/boards');

      expect(response.status).toBe(200);
      expect(response.body).toEqual([]);
      expect(Board.findAll).toHaveBeenCalled();
    });

    it('should return array of boards', async () => {
      const mockBoards = [
        { id: 1, name: 'Board 1', created_at: '2024-01-01T00:00:00Z', updated_at: '2024-01-01T00:00:00Z' },
        { id: 2, name: 'Board 2', created_at: '2024-01-02T00:00:00Z', updated_at: '2024-01-02T00:00:00Z' }
      ];
      Board.findAll.mockResolvedValue(mockBoards);

      const response = await request(app).get('/api/boards');

      expect(response.status).toBe(200);
      expect(response.body).toEqual(mockBoards);
    });

    it('should handle database errors', async () => {
      Board.findAll.mockRejectedValue(new Error('Database connection failed'));

      const response = await request(app).get('/api/boards');

      expect(response.status).toBe(500);
      expect(response.body.error.code).toBe('INTERNAL_ERROR');
    });
  });

  describe('POST /api/boards', () => {
    it('should create a board with valid name', async () => {
      const mockBoard = { id: 1, name: 'New Board', created_at: '2024-01-01T00:00:00Z', updated_at: '2024-01-01T00:00:00Z' };
      Board.create.mockResolvedValue(mockBoard);

      const response = await request(app)
        .post('/api/boards')
        .send({ name: 'New Board' });

      expect(response.status).toBe(201);
      expect(response.body).toEqual(mockBoard);
      expect(Board.create).toHaveBeenCalledWith('New Board');
    });

    it('should trim whitespace from name', async () => {
      const mockBoard = { id: 1, name: 'Trimmed' };
      Board.create.mockResolvedValue(mockBoard);

      const response = await request(app)
        .post('/api/boards')
        .send({ name: '  Trimmed  ' });

      expect(response.status).toBe(201);
      expect(Board.create).toHaveBeenCalledWith('Trimmed');
    });

    it('should return 400 when name is missing', async () => {
      const response = await request(app)
        .post('/api/boards')
        .send({});

      expect(response.status).toBe(400);
      expect(response.body.error.code).toBe('VALIDATION_ERROR');
      expect(response.body.error.message).toContain('Name is required');
    });

    it('should return 400 when name is not a string', async () => {
      const response = await request(app)
        .post('/api/boards')
        .send({ name: 123 });

      expect(response.status).toBe(400);
      expect(response.body.error.code).toBe('VALIDATION_ERROR');
    });

    it('should return 400 when name is empty after trim', async () => {
      const response = await request(app)
        .post('/api/boards')
        .send({ name: '   ' });

      expect(response.status).toBe(400);
      expect(response.body.error.message).toContain('empty');
    });

    it('should return 400 when name exceeds 255 characters', async () => {
      const response = await request(app)
        .post('/api/boards')
        .send({ name: 'a'.repeat(256) });

      expect(response.status).toBe(400);
      expect(response.body.error.message).toContain('255');
    });

    it('should handle database errors', async () => {
      Board.create.mockRejectedValue(new Error('Insert failed'));

      const response = await request(app)
        .post('/api/boards')
        .send({ name: 'Test' });

      expect(response.status).toBe(500);
    });
  });

  describe('GET /api/boards/:id', () => {
    it('should return board with columns and cards', async () => {
      const mockBoard = {
        id: 1,
        name: 'Test Board',
        created_at: '2024-01-01T00:00:00Z',
        updated_at: '2024-01-01T00:00:00Z',
        columns: [
          {
            id: 1,
            name: 'To Do',
            position: 1024,
            cards: [
              { id: 1, title: 'Task 1', description: null, position: 1024 }
            ]
          }
        ]
      };
      Board.findByIdWithColumnsAndCards.mockResolvedValue(mockBoard);

      const response = await request(app).get('/api/boards/1');

      expect(response.status).toBe(200);
      expect(response.body).toEqual(mockBoard);
      expect(Board.findByIdWithColumnsAndCards).toHaveBeenCalledWith(1);
    });

    it('should return 404 when board not found', async () => {
      Board.findByIdWithColumnsAndCards.mockResolvedValue(null);

      const response = await request(app).get('/api/boards/999');

      expect(response.status).toBe(404);
      expect(response.body.error.code).toBe('NOT_FOUND');
      expect(response.body.error.message).toContain('999');
    });

    it('should return 400 for invalid board ID', async () => {
      const response = await request(app).get('/api/boards/abc');

      expect(response.status).toBe(400);
      expect(response.body.error.code).toBe('VALIDATION_ERROR');
    });

    it('should handle database errors', async () => {
      Board.findByIdWithColumnsAndCards.mockRejectedValue(new Error('Query failed'));

      const response = await request(app).get('/api/boards/1');

      expect(response.status).toBe(500);
    });
  });

  describe('PUT /api/boards/:id', () => {
    it('should update board name', async () => {
      const mockBoard = { id: 1, name: 'Updated Name', updated_at: '2024-01-01T00:00:00Z' };
      Board.update.mockResolvedValue(mockBoard);

      const response = await request(app)
        .put('/api/boards/1')
        .send({ name: 'Updated Name' });

      expect(response.status).toBe(200);
      expect(response.body).toEqual(mockBoard);
      expect(Board.update).toHaveBeenCalledWith(1, 'Updated Name');
    });

    it('should return 404 when board not found', async () => {
      Board.update.mockResolvedValue(null);

      const response = await request(app)
        .put('/api/boards/999')
        .send({ name: 'New Name' });

      expect(response.status).toBe(404);
      expect(response.body.error.code).toBe('NOT_FOUND');
    });

    it('should return 400 when name is missing', async () => {
      const response = await request(app)
        .put('/api/boards/1')
        .send({});

      expect(response.status).toBe(400);
      expect(response.body.error.code).toBe('VALIDATION_ERROR');
    });

    it('should return 400 for invalid board ID', async () => {
      const response = await request(app)
        .put('/api/boards/xyz')
        .send({ name: 'Test' });

      expect(response.status).toBe(400);
    });

    it('should return 400 when name is empty', async () => {
      const response = await request(app)
        .put('/api/boards/1')
        .send({ name: '' });

      expect(response.status).toBe(400);
    });

    it('should return 400 when name exceeds 255 characters', async () => {
      const response = await request(app)
        .put('/api/boards/1')
        .send({ name: 'x'.repeat(256) });

      expect(response.status).toBe(400);
      expect(response.body.error.message).toContain('255');
    });
  });

  describe('DELETE /api/boards/:id', () => {
    it('should delete board successfully', async () => {
      Board.deleteById.mockResolvedValue(true);

      const response = await request(app).delete('/api/boards/1');

      expect(response.status).toBe(204);
      expect(Board.deleteById).toHaveBeenCalledWith(1);
    });

    it('should return 404 when board not found', async () => {
      Board.deleteById.mockResolvedValue(false);

      const response = await request(app).delete('/api/boards/999');

      expect(response.status).toBe(404);
      expect(response.body.error.code).toBe('NOT_FOUND');
    });

    it('should return 400 for invalid board ID', async () => {
      const response = await request(app).delete('/api/boards/invalid');

      expect(response.status).toBe(400);
    });

    it('should handle database errors', async () => {
      Board.deleteById.mockRejectedValue(new Error('Delete failed'));

      const response = await request(app).delete('/api/boards/1');

      expect(response.status).toBe(500);
    });
  });
});

describe('Board Model', () => {
  beforeEach(() => {
    jest.clearAllMocks();
  });

  describe('findAll', () => {
    it('should be called when listing boards', async () => {
      Board.findAll.mockResolvedValue([]);
      await Board.findAll();
      expect(Board.findAll).toHaveBeenCalledTimes(1);
    });
  });

  describe('findById', () => {
    it('should return board when found', async () => {
      const mockBoard = { id: 1, name: 'Test' };
      Board.findById.mockResolvedValue(mockBoard);

      const result = await Board.findById(1);
      expect(result).toEqual(mockBoard);
    });

    it('should return null when not found', async () => {
      Board.findById.mockResolvedValue(null);

      const result = await Board.findById(999);
      expect(result).toBeNull();
    });
  });

  describe('create', () => {
    it('should create board with name', async () => {
      const mockBoard = { id: 1, name: 'New Board' };
      Board.create.mockResolvedValue(mockBoard);

      const result = await Board.create('New Board');
      expect(Board.create).toHaveBeenCalledWith('New Board');
      expect(result).toEqual(mockBoard);
    });
  });

  describe('update', () => {
    it('should update board name', async () => {
      const mockBoard = { id: 1, name: 'Updated' };
      Board.update.mockResolvedValue(mockBoard);

      const result = await Board.update(1, 'Updated');
      expect(Board.update).toHaveBeenCalledWith(1, 'Updated');
      expect(result).toEqual(mockBoard);
    });
  });

  describe('deleteById', () => {
    it('should return true when deleted', async () => {
      Board.deleteById.mockResolvedValue(true);

      const result = await Board.deleteById(1);
      expect(result).toBe(true);
    });

    it('should return false when not found', async () => {
      Board.deleteById.mockResolvedValue(false);

      const result = await Board.deleteById(999);
      expect(result).toBe(false);
    });
  });
});

