/**
 * Unit tests for column routes.
 * Tests create, update, and delete operations with mocked database using supertest.
 */
const request = require('supertest');
const express = require('express');
const { errorHandler } = require('../src/middleware/errorHandler');
const columnRoutes = require('../src/routes/columns');
const Column = require('../src/models/Column');

// Mock the Column model
jest.mock('../src/models/Column');

// Setup test app
const createApp = () => {
  const app = express();
  app.use(express.json());
  app.use('/api', columnRoutes);
  app.use(errorHandler);
  return app;
};

describe('Column Routes', () => {
  let app;

  beforeEach(() => {
    app = createApp();
    jest.clearAllMocks();
  });

  describe('POST /api/boards/:boardId/columns', () => {
    it('should create a column with valid name', async () => {
      const mockColumn = { id: 1, board_id: 1, name: 'To Do', position: 1024, created_at: '2024-01-01T00:00:00Z' };
      Column.boardExists.mockResolvedValue(true);
      Column.create.mockResolvedValue(mockColumn);

      const response = await request(app)
        .post('/api/boards/1/columns')
        .send({ name: 'To Do' });

      expect(response.status).toBe(201);
      expect(response.body).toEqual(mockColumn);
      expect(Column.boardExists).toHaveBeenCalledWith(1);
      expect(Column.create).toHaveBeenCalledWith(1, 'To Do');
    });

    it('should return 404 when board not found', async () => {
      Column.boardExists.mockResolvedValue(false);

      const response = await request(app)
        .post('/api/boards/999/columns')
        .send({ name: 'To Do' });

      expect(response.status).toBe(404);
      expect(response.body.error.code).toBe('NOT_FOUND');
      expect(response.body.error.message).toContain('999');
    });

    it('should return 400 when name is missing', async () => {
      Column.boardExists.mockResolvedValue(true);

      const response = await request(app)
        .post('/api/boards/1/columns')
        .send({});

      expect(response.status).toBe(400);
      expect(response.body.error.code).toBe('VALIDATION_ERROR');
    });

    it('should return 400 when board ID is invalid', async () => {
      const response = await request(app)
        .post('/api/boards/abc/columns')
        .send({ name: 'To Do' });

      expect(response.status).toBe(400);
    });

    it('should return 400 when name is empty after trim', async () => {
      Column.boardExists.mockResolvedValue(true);

      const response = await request(app)
        .post('/api/boards/1/columns')
        .send({ name: '   ' });

      expect(response.status).toBe(400);
      expect(response.body.error.message).toContain('empty');
    });

    it('should return 400 when name exceeds 255 characters', async () => {
      Column.boardExists.mockResolvedValue(true);

      const response = await request(app)
        .post('/api/boards/1/columns')
        .send({ name: 'a'.repeat(256) });

      expect(response.status).toBe(400);
      expect(response.body.error.message).toContain('255');
    });

    it('should trim whitespace from name', async () => {
      const mockColumn = { id: 1, board_id: 1, name: 'Trimmed', position: 1024 };
      Column.boardExists.mockResolvedValue(true);
      Column.create.mockResolvedValue(mockColumn);

      const response = await request(app)
        .post('/api/boards/1/columns')
        .send({ name: '  Trimmed  ' });

      expect(response.status).toBe(201);
      expect(Column.create).toHaveBeenCalledWith(1, 'Trimmed');
    });

    it('should handle database errors', async () => {
      Column.boardExists.mockResolvedValue(true);
      Column.create.mockRejectedValue(new Error('Insert failed'));

      const response = await request(app)
        .post('/api/boards/1/columns')
        .send({ name: 'Test' });

      expect(response.status).toBe(500);
    });
  });

  describe('PUT /api/columns/:id', () => {
    it('should update column name', async () => {
      const mockColumn = { id: 1, board_id: 1, name: 'Updated', position: 1024 };
      Column.update.mockResolvedValue(mockColumn);

      const response = await request(app)
        .put('/api/columns/1')
        .send({ name: 'Updated' });

      expect(response.status).toBe(200);
      expect(response.body).toEqual(mockColumn);
      expect(Column.update).toHaveBeenCalledWith(1, { name: 'Updated' });
    });

    it('should update column position', async () => {
      const mockColumn = { id: 1, board_id: 1, name: 'To Do', position: 2048 };
      Column.update.mockResolvedValue(mockColumn);

      const response = await request(app)
        .put('/api/columns/1')
        .send({ position: 2048 });

      expect(response.status).toBe(200);
      expect(Column.update).toHaveBeenCalledWith(1, { position: 2048 });
    });

    it('should update both name and position', async () => {
      const mockColumn = { id: 1, board_id: 1, name: 'New Name', position: 2048 };
      Column.update.mockResolvedValue(mockColumn);

      const response = await request(app)
        .put('/api/columns/1')
        .send({ name: 'New Name', position: 2048 });

      expect(response.status).toBe(200);
      expect(Column.update).toHaveBeenCalledWith(1, { name: 'New Name', position: 2048 });
    });

    it('should return 404 when column not found', async () => {
      Column.update.mockResolvedValue(null);

      const response = await request(app)
        .put('/api/columns/999')
        .send({ name: 'Test' });

      expect(response.status).toBe(404);
      expect(response.body.error.code).toBe('NOT_FOUND');
    });

    it('should return 400 when no fields provided', async () => {
      const response = await request(app)
        .put('/api/columns/1')
        .send({});

      expect(response.status).toBe(400);
    });

    it('should return 400 for invalid column ID', async () => {
      const response = await request(app)
        .put('/api/columns/invalid')
        .send({ name: 'Test' });

      expect(response.status).toBe(400);
    });

    it('should return 400 for non-string name', async () => {
      const response = await request(app)
        .put('/api/columns/1')
        .send({ name: 123 });

      expect(response.status).toBe(400);
    });

    it('should return 400 for non-numeric position', async () => {
      const response = await request(app)
        .put('/api/columns/1')
        .send({ position: 'abc' });

      expect(response.status).toBe(400);
    });

    it('should return 400 for negative position', async () => {
      const response = await request(app)
        .put('/api/columns/1')
        .send({ position: -1 });

      expect(response.status).toBe(400);
      expect(response.body.error.message).toContain('non-negative');
    });

    it('should return 400 for empty name', async () => {
      const response = await request(app)
        .put('/api/columns/1')
        .send({ name: '   ' });

      expect(response.status).toBe(400);
    });

    it('should return 400 for name exceeding 255 chars', async () => {
      const response = await request(app)
        .put('/api/columns/1')
        .send({ name: 'x'.repeat(256) });

      expect(response.status).toBe(400);
    });
  });

  describe('DELETE /api/columns/:id', () => {
    it('should delete column successfully', async () => {
      Column.deleteById.mockResolvedValue(true);

      const response = await request(app).delete('/api/columns/1');

      expect(response.status).toBe(204);
      expect(Column.deleteById).toHaveBeenCalledWith(1);
    });

    it('should return 404 when column not found', async () => {
      Column.deleteById.mockResolvedValue(false);

      const response = await request(app).delete('/api/columns/999');

      expect(response.status).toBe(404);
      expect(response.body.error.code).toBe('NOT_FOUND');
    });

    it('should return 400 for invalid column ID', async () => {
      const response = await request(app).delete('/api/columns/invalid');

      expect(response.status).toBe(400);
    });

    it('should handle database errors', async () => {
      Column.deleteById.mockRejectedValue(new Error('Delete failed'));

      const response = await request(app).delete('/api/columns/1');

      expect(response.status).toBe(500);
    });
  });
});

describe('Column Model', () => {
  beforeEach(() => {
    jest.clearAllMocks();
  });

  describe('findByBoardId', () => {
    it('should return columns for board', async () => {
      const mockColumns = [
        { id: 1, board_id: 1, name: 'To Do', position: 1024 },
        { id: 2, board_id: 1, name: 'Done', position: 2048 }
      ];
      Column.findByBoardId.mockResolvedValue(mockColumns);

      const result = await Column.findByBoardId(1);
      expect(result).toEqual(mockColumns);
    });
  });

  describe('findById', () => {
    it('should return column when found', async () => {
      const mockColumn = { id: 1, name: 'To Do' };
      Column.findById.mockResolvedValue(mockColumn);

      const result = await Column.findById(1);
      expect(result).toEqual(mockColumn);
    });

    it('should return null when not found', async () => {
      Column.findById.mockResolvedValue(null);

      const result = await Column.findById(999);
      expect(result).toBeNull();
    });
  });

  describe('getMaxPosition', () => {
    it('should return max position', async () => {
      Column.getMaxPosition.mockResolvedValue(2048);

      const result = await Column.getMaxPosition(1);
      expect(result).toBe(2048);
    });

    it('should return 0 when no columns', async () => {
      Column.getMaxPosition.mockResolvedValue(0);

      const result = await Column.getMaxPosition(1);
      expect(result).toBe(0);
    });
  });

  describe('create', () => {
    it('should create column', async () => {
      const mockColumn = { id: 1, board_id: 1, name: 'New', position: 1024 };
      Column.create.mockResolvedValue(mockColumn);

      const result = await Column.create(1, 'New');
      expect(result).toEqual(mockColumn);
    });
  });

  describe('update', () => {
    it('should update column', async () => {
      const mockColumn = { id: 1, name: 'Updated', position: 2048 };
      Column.update.mockResolvedValue(mockColumn);

      const result = await Column.update(1, { name: 'Updated', position: 2048 });
      expect(result).toEqual(mockColumn);
    });
  });

  describe('deleteById', () => {
    it('should return true when deleted', async () => {
      Column.deleteById.mockResolvedValue(true);

      const result = await Column.deleteById(1);
      expect(result).toBe(true);
    });
  });

  describe('boardExists', () => {
    it('should return true when board exists', async () => {
      Column.boardExists.mockResolvedValue(true);

      const result = await Column.boardExists(1);
      expect(result).toBe(true);
    });

    it('should return false when board not exists', async () => {
      Column.boardExists.mockResolvedValue(false);

      const result = await Column.boardExists(999);
      expect(result).toBe(false);
    });
  });

  describe('POSITION_INCREMENT', () => {
    it('should be defined', () => {
      expect(Column.POSITION_INCREMENT).toBeDefined();
    });
  });
});

