/**
 * Unit tests for card routes.
 * Tests create, update, delete, and move operations with mocked database using supertest.
 */
const request = require('supertest');
const express = require('express');
const { errorHandler } = require('../src/middleware/errorHandler');
const cardRoutes = require('../src/routes/cards');
const Card = require('../src/models/Card');

// Mock the Card model
jest.mock('../src/models/Card');

// Setup test app
const createApp = () => {
  const app = express();
  app.use(express.json());
  app.use('/api', cardRoutes);
  app.use(errorHandler);
  return app;
};

describe('Card Routes', () => {
  let app;

  beforeEach(() => {
    app = createApp();
    jest.clearAllMocks();
  });

  describe('POST /api/columns/:columnId/cards', () => {
    it('should create a card with valid title', async () => {
      const mockCard = {
        id: 1,
        column_id: 1,
        title: 'New Task',
        description: null,
        position: 1024,
        created_at: '2024-01-01T00:00:00Z',
        updated_at: '2024-01-01T00:00:00Z'
      };
      Card.columnExists.mockResolvedValue(true);
      Card.create.mockResolvedValue(mockCard);

      const response = await request(app)
        .post('/api/columns/1/cards')
        .send({ title: 'New Task' });

      expect(response.status).toBe(201);
      expect(response.body).toEqual(mockCard);
      expect(Card.columnExists).toHaveBeenCalledWith(1);
      expect(Card.create).toHaveBeenCalledWith(1, 'New Task', null);
    });

    it('should create a card with title and description', async () => {
      const mockCard = {
        id: 1,
        column_id: 1,
        title: 'New Task',
        description: 'Task description',
        position: 1024
      };
      Card.columnExists.mockResolvedValue(true);
      Card.create.mockResolvedValue(mockCard);

      const response = await request(app)
        .post('/api/columns/1/cards')
        .send({ title: 'New Task', description: 'Task description' });

      expect(response.status).toBe(201);
      expect(Card.create).toHaveBeenCalledWith(1, 'New Task', 'Task description');
    });

    it('should return 404 when column not found', async () => {
      Card.columnExists.mockResolvedValue(false);

      const response = await request(app)
        .post('/api/columns/999/cards')
        .send({ title: 'Task' });

      expect(response.status).toBe(404);
      expect(response.body.error.code).toBe('NOT_FOUND');
    });

    it('should return 400 when title is missing', async () => {
      Card.columnExists.mockResolvedValue(true);

      const response = await request(app)
        .post('/api/columns/1/cards')
        .send({});

      expect(response.status).toBe(400);
      expect(response.body.error.code).toBe('VALIDATION_ERROR');
    });

    it('should return 400 when column ID is invalid', async () => {
      const response = await request(app)
        .post('/api/columns/abc/cards')
        .send({ title: 'Task' });

      expect(response.status).toBe(400);
    });

    it('should return 400 when title is empty after trim', async () => {
      Card.columnExists.mockResolvedValue(true);

      const response = await request(app)
        .post('/api/columns/1/cards')
        .send({ title: '   ' });

      expect(response.status).toBe(400);
      expect(response.body.error.message).toContain('empty');
    });

    it('should return 400 when title exceeds 255 characters', async () => {
      Card.columnExists.mockResolvedValue(true);

      const response = await request(app)
        .post('/api/columns/1/cards')
        .send({ title: 'a'.repeat(256) });

      expect(response.status).toBe(400);
      expect(response.body.error.message).toContain('255');
    });

    it('should return 400 when description is not a string', async () => {
      Card.columnExists.mockResolvedValue(true);

      const response = await request(app)
        .post('/api/columns/1/cards')
        .send({ title: 'Task', description: 123 });

      expect(response.status).toBe(400);
    });

    it('should trim whitespace from title and description', async () => {
      const mockCard = { id: 1, title: 'Trimmed', description: 'Also trimmed', position: 1024 };
      Card.columnExists.mockResolvedValue(true);
      Card.create.mockResolvedValue(mockCard);

      const response = await request(app)
        .post('/api/columns/1/cards')
        .send({ title: '  Trimmed  ', description: '  Also trimmed  ' });

      expect(response.status).toBe(201);
      expect(Card.create).toHaveBeenCalledWith(1, 'Trimmed', 'Also trimmed');
    });

    it('should convert empty description to null', async () => {
      const mockCard = { id: 1, title: 'Task', description: null, position: 1024 };
      Card.columnExists.mockResolvedValue(true);
      Card.create.mockResolvedValue(mockCard);

      const response = await request(app)
        .post('/api/columns/1/cards')
        .send({ title: 'Task', description: '   ' });

      expect(response.status).toBe(201);
      expect(Card.create).toHaveBeenCalledWith(1, 'Task', null);
    });

    it('should handle database errors', async () => {
      Card.columnExists.mockResolvedValue(true);
      Card.create.mockRejectedValue(new Error('Insert failed'));

      const response = await request(app)
        .post('/api/columns/1/cards')
        .send({ title: 'Test' });

      expect(response.status).toBe(500);
    });
  });

  describe('PUT /api/cards/:id', () => {
    it('should update card title', async () => {
      const mockCard = { id: 1, title: 'Updated', description: null };
      Card.update.mockResolvedValue(mockCard);

      const response = await request(app)
        .put('/api/cards/1')
        .send({ title: 'Updated' });

      expect(response.status).toBe(200);
      expect(response.body).toEqual(mockCard);
      expect(Card.update).toHaveBeenCalledWith(1, { title: 'Updated' });
    });

    it('should update card description', async () => {
      const mockCard = { id: 1, title: 'Task', description: 'New description' };
      Card.update.mockResolvedValue(mockCard);

      const response = await request(app)
        .put('/api/cards/1')
        .send({ description: 'New description' });

      expect(response.status).toBe(200);
      expect(Card.update).toHaveBeenCalledWith(1, { description: 'New description' });
    });

    it('should update both title and description', async () => {
      const mockCard = { id: 1, title: 'New Title', description: 'New description' };
      Card.update.mockResolvedValue(mockCard);

      const response = await request(app)
        .put('/api/cards/1')
        .send({ title: 'New Title', description: 'New description' });

      expect(response.status).toBe(200);
      expect(Card.update).toHaveBeenCalledWith(1, { title: 'New Title', description: 'New description' });
    });

    it('should allow setting description to null', async () => {
      const mockCard = { id: 1, title: 'Task', description: null };
      Card.update.mockResolvedValue(mockCard);

      const response = await request(app)
        .put('/api/cards/1')
        .send({ description: null });

      expect(response.status).toBe(200);
      expect(Card.update).toHaveBeenCalledWith(1, { description: null });
    });

    it('should return 404 when card not found', async () => {
      Card.update.mockResolvedValue(null);

      const response = await request(app)
        .put('/api/cards/999')
        .send({ title: 'Test' });

      expect(response.status).toBe(404);
      expect(response.body.error.code).toBe('NOT_FOUND');
    });

    it('should return 400 when no fields provided', async () => {
      const response = await request(app)
        .put('/api/cards/1')
        .send({});

      expect(response.status).toBe(400);
    });

    it('should return 400 for invalid card ID', async () => {
      const response = await request(app)
        .put('/api/cards/invalid')
        .send({ title: 'Test' });

      expect(response.status).toBe(400);
    });

    it('should return 400 for non-string title', async () => {
      const response = await request(app)
        .put('/api/cards/1')
        .send({ title: 123 });

      expect(response.status).toBe(400);
    });

    it('should return 400 for non-string description', async () => {
      const response = await request(app)
        .put('/api/cards/1')
        .send({ description: 123 });

      expect(response.status).toBe(400);
    });

    it('should return 400 for empty title', async () => {
      const response = await request(app)
        .put('/api/cards/1')
        .send({ title: '  ' });

      expect(response.status).toBe(400);
    });

    it('should return 400 for title exceeding 255 chars', async () => {
      const response = await request(app)
        .put('/api/cards/1')
        .send({ title: 'x'.repeat(256) });

      expect(response.status).toBe(400);
    });
  });

  describe('DELETE /api/cards/:id', () => {
    it('should delete card successfully', async () => {
      Card.deleteById.mockResolvedValue(true);

      const response = await request(app).delete('/api/cards/1');

      expect(response.status).toBe(204);
      expect(Card.deleteById).toHaveBeenCalledWith(1);
    });

    it('should return 404 when card not found', async () => {
      Card.deleteById.mockResolvedValue(false);

      const response = await request(app).delete('/api/cards/999');

      expect(response.status).toBe(404);
      expect(response.body.error.code).toBe('NOT_FOUND');
    });

    it('should return 400 for invalid card ID', async () => {
      const response = await request(app).delete('/api/cards/invalid');

      expect(response.status).toBe(400);
    });

    it('should handle database errors', async () => {
      Card.deleteById.mockRejectedValue(new Error('Delete failed'));

      const response = await request(app).delete('/api/cards/1');

      expect(response.status).toBe(500);
    });
  });

  describe('PATCH /api/cards/:id/move', () => {
    it('should move card to new column and position', async () => {
      const mockCard = { id: 1, column_id: 2, title: 'Task', description: null, position: 512 };
      Card.findById.mockResolvedValue({ id: 1, column_id: 1, position: 1024 });
      Card.columnExists.mockResolvedValue(true);
      Card.move.mockResolvedValue(mockCard);

      const response = await request(app)
        .patch('/api/cards/1/move')
        .send({ columnId: 2, position: 512 });

      expect(response.status).toBe(200);
      expect(response.body).toEqual({
        id: 1,
        column_id: 2,
        position: 512
      });
      expect(Card.findById).toHaveBeenCalledWith(1);
      expect(Card.columnExists).toHaveBeenCalledWith(2);
      expect(Card.move).toHaveBeenCalledWith(1, 2, 512);
    });

    it('should return 404 when card not found', async () => {
      Card.findById.mockResolvedValue(null);

      const response = await request(app)
        .patch('/api/cards/999/move')
        .send({ columnId: 1, position: 1024 });

      expect(response.status).toBe(404);
      expect(response.body.error.code).toBe('NOT_FOUND');
    });

    it('should return 404 when target column not found', async () => {
      Card.findById.mockResolvedValue({ id: 1, column_id: 1 });
      Card.columnExists.mockResolvedValue(false);

      const response = await request(app)
        .patch('/api/cards/1/move')
        .send({ columnId: 999, position: 1024 });

      expect(response.status).toBe(404);
      expect(response.body.error.message).toContain('999');
    });

    it('should return 400 when columnId is missing', async () => {
      Card.findById.mockResolvedValue({ id: 1, column_id: 1 });

      const response = await request(app)
        .patch('/api/cards/1/move')
        .send({ position: 1024 });

      expect(response.status).toBe(400);
      expect(response.body.error.message).toContain('columnId');
    });

    it('should return 400 when position is missing', async () => {
      Card.findById.mockResolvedValue({ id: 1, column_id: 1 });
      Card.columnExists.mockResolvedValue(true);

      const response = await request(app)
        .patch('/api/cards/1/move')
        .send({ columnId: 2 });

      expect(response.status).toBe(400);
      expect(response.body.error.message).toContain('position');
    });

    it('should return 400 for invalid card ID', async () => {
      const response = await request(app)
        .patch('/api/cards/invalid/move')
        .send({ columnId: 1, position: 1024 });

      expect(response.status).toBe(400);
    });

    it('should return 400 for invalid columnId', async () => {
      Card.findById.mockResolvedValue({ id: 1, column_id: 1 });

      const response = await request(app)
        .patch('/api/cards/1/move')
        .send({ columnId: 'abc', position: 1024 });

      expect(response.status).toBe(400);
    });

    it('should return 400 for non-numeric position', async () => {
      Card.findById.mockResolvedValue({ id: 1, column_id: 1 });
      Card.columnExists.mockResolvedValue(true);

      const response = await request(app)
        .patch('/api/cards/1/move')
        .send({ columnId: 2, position: 'high' });

      expect(response.status).toBe(400);
    });

    it('should return 400 for negative position', async () => {
      Card.findById.mockResolvedValue({ id: 1, column_id: 1 });
      Card.columnExists.mockResolvedValue(true);

      const response = await request(app)
        .patch('/api/cards/1/move')
        .send({ columnId: 2, position: -1 });

      expect(response.status).toBe(400);
      expect(response.body.error.message).toContain('non-negative');
    });

    it('should handle database errors', async () => {
      Card.findById.mockResolvedValue({ id: 1, column_id: 1 });
      Card.columnExists.mockResolvedValue(true);
      Card.move.mockRejectedValue(new Error('Update failed'));

      const response = await request(app)
        .patch('/api/cards/1/move')
        .send({ columnId: 2, position: 512 });

      expect(response.status).toBe(500);
    });
  });
});

describe('Card Model', () => {
  beforeEach(() => {
    jest.clearAllMocks();
  });

  describe('findByColumnId', () => {
    it('should return cards for column', async () => {
      const mockCards = [
        { id: 1, column_id: 1, title: 'Task 1', position: 1024 },
        { id: 2, column_id: 1, title: 'Task 2', position: 2048 }
      ];
      Card.findByColumnId.mockResolvedValue(mockCards);

      const result = await Card.findByColumnId(1);
      expect(result).toEqual(mockCards);
    });
  });

  describe('findById', () => {
    it('should return card when found', async () => {
      const mockCard = { id: 1, title: 'Task' };
      Card.findById.mockResolvedValue(mockCard);

      const result = await Card.findById(1);
      expect(result).toEqual(mockCard);
    });

    it('should return null when not found', async () => {
      Card.findById.mockResolvedValue(null);

      const result = await Card.findById(999);
      expect(result).toBeNull();
    });
  });

  describe('getMaxPosition', () => {
    it('should return max position', async () => {
      Card.getMaxPosition.mockResolvedValue(2048);

      const result = await Card.getMaxPosition(1);
      expect(result).toBe(2048);
    });

    it('should return 0 when no cards', async () => {
      Card.getMaxPosition.mockResolvedValue(0);

      const result = await Card.getMaxPosition(1);
      expect(result).toBe(0);
    });
  });

  describe('create', () => {
    it('should create card with title only', async () => {
      const mockCard = { id: 1, title: 'New', description: null, position: 1024 };
      Card.create.mockResolvedValue(mockCard);

      const result = await Card.create(1, 'New', null);
      expect(result).toEqual(mockCard);
    });

    it('should create card with title and description', async () => {
      const mockCard = { id: 1, title: 'New', description: 'Desc', position: 1024 };
      Card.create.mockResolvedValue(mockCard);

      const result = await Card.create(1, 'New', 'Desc');
      expect(result).toEqual(mockCard);
    });
  });

  describe('update', () => {
    it('should update card', async () => {
      const mockCard = { id: 1, title: 'Updated', description: 'New desc' };
      Card.update.mockResolvedValue(mockCard);

      const result = await Card.update(1, { title: 'Updated', description: 'New desc' });
      expect(result).toEqual(mockCard);
    });
  });

  describe('move', () => {
    it('should move card to new column and position', async () => {
      const mockCard = { id: 1, column_id: 2, position: 512 };
      Card.move.mockResolvedValue(mockCard);

      const result = await Card.move(1, 2, 512);
      expect(result).toEqual(mockCard);
    });
  });

  describe('deleteById', () => {
    it('should return true when deleted', async () => {
      Card.deleteById.mockResolvedValue(true);

      const result = await Card.deleteById(1);
      expect(result).toBe(true);
    });
  });

  describe('columnExists', () => {
    it('should return true when column exists', async () => {
      Card.columnExists.mockResolvedValue(true);

      const result = await Card.columnExists(1);
      expect(result).toBe(true);
    });

    it('should return false when column not exists', async () => {
      Card.columnExists.mockResolvedValue(false);

      const result = await Card.columnExists(999);
      expect(result).toBe(false);
    });
  });

  describe('calculatePosition', () => {
    it('should calculate position', async () => {
      Card.calculatePosition.mockResolvedValue(1536);

      const result = await Card.calculatePosition(1, 1024, 2048);
      expect(result).toBe(1536);
    });
  });

  describe('POSITION_INCREMENT', () => {
    it('should be defined', () => {
      expect(Card.POSITION_INCREMENT).toBeDefined();
    });
  });
});

