## Accessibility Patterns

### Accessible Form

```tsx
import { useState, useId, type FormEvent } from 'react';

import styles from './LoginForm.module.css';

interface FormErrors {
  email?: string;
  password?: string;
}

export function LoginForm({ onSubmit }: { onSubmit: (data: FormData) => void }) {
  const [errors, setErrors] = useState<FormErrors>({});
  const emailId = useId();
  const passwordId = useId();

  const handleSubmit = (e: FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    const formData = new FormData(e.currentTarget);
    const newErrors: FormErrors = {};

    if (!formData.get('email')) {
      newErrors.email = 'Email is required';
    }
    if (!formData.get('password')) {
      newErrors.password = 'Password is required';
    }

    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
      return;
    }

    setErrors({});
    onSubmit(formData);
  };

  return (
    <form
      onSubmit={handleSubmit}
      className={styles.form}
      aria-labelledby="login-heading"
      noValidate
    >
      <h2 id="login-heading">Login</h2>

      <div className={styles.field}>
        <label htmlFor={emailId}>
          Email <span aria-label="required">*</span>
        </label>
        <input
          id={emailId}
          type="email"
          name="email"
          autoComplete="email"
          aria-invalid={!!errors.email}
          aria-describedby={errors.email ? `${emailId}-error` : undefined}
          required
        />
        {errors.email && (
          <span id={`${emailId}-error`} className={styles.error} role="alert">
            {errors.email}
          </span>
        )}
      </div>

      <div className={styles.field}>
        <label htmlFor={passwordId}>
          Password <span aria-label="required">*</span>
        </label>
        <input
          id={passwordId}
          type="password"
          name="password"
          autoComplete="current-password"
          aria-invalid={!!errors.password}
          aria-describedby={errors.password ? `${passwordId}-error` : undefined}
          required
        />
        {errors.password && (
          <span id={`${passwordId}-error`} className={styles.error} role="alert">
            {errors.password}
          </span>
        )}
      </div>

      <button type="submit" className={styles.submit}>
        Login
      </button>
    </form>
  );
}
```

### Accessible Dropdown

```tsx
import { useState, useRef, useCallback, type KeyboardEvent } from 'react';

import styles from './Dropdown.module.css';

interface DropdownProps {
  options: string[];
  value: string;
  onChange: (value: string) => void;
  label: string;
}

export function Dropdown({ options, value, onChange, label }: DropdownProps) {
  const [isOpen, setIsOpen] = useState(false);
  const [focusedIndex, setFocusedIndex] = useState(0);
  const buttonRef = useRef<HTMLButtonElement>(null);
  const menuRef = useRef<HTMLUListElement>(null);

  const handleKeyDown = useCallback(
    (e: KeyboardEvent) => {
      switch (e.key) {
        case 'ArrowDown':
          e.preventDefault();
          if (!isOpen) {
            setIsOpen(true);
          } else {
            setFocusedIndex((prev) =>
              prev < options.length - 1 ? prev + 1 : 0
            );
          }
          break;
        case 'ArrowUp':
          e.preventDefault();
          setFocusedIndex((prev) =>
            prev > 0 ? prev - 1 : options.length - 1
          );
          break;
        case 'Enter':
        case ' ':
          e.preventDefault();
          if (isOpen) {
            onChange(options[focusedIndex]);
            setIsOpen(false);
            buttonRef.current?.focus();
          } else {
            setIsOpen(true);
          }
          break;
        case 'Escape':
          setIsOpen(false);
          buttonRef.current?.focus();
          break;
        case 'Tab':
          setIsOpen(false);
          break;
      }
    },
    [isOpen, focusedIndex, options, onChange]
  );

  return (
    <div className={styles.dropdown}>
      <span id="dropdown-label" className="sr-only">
        {label}
      </span>
      <button
        ref={buttonRef}
        type="button"
        className={styles.trigger}
        aria-haspopup="listbox"
        aria-expanded={isOpen}
        aria-labelledby="dropdown-label"
        onClick={() => setIsOpen(!isOpen)}
        onKeyDown={handleKeyDown}
      >
        {value || 'Select option'}
      </button>

      {isOpen && (
        <ul
          ref={menuRef}
          role="listbox"
          className={styles.menu}
          aria-labelledby="dropdown-label"
          onKeyDown={handleKeyDown}
        >
          {options.map((option, index) => (
            <li
              key={option}
              role="option"
              aria-selected={option === value}
              className={`${styles.option} ${
                index === focusedIndex ? styles.focused : ''
              }`}
              onClick={() => {
                onChange(option);
                setIsOpen(false);
                buttonRef.current?.focus();
              }}
            >
              {option}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
```

