    case ACTIONS.SET_LOADING:
      return { ...state, loading: action.payload };

    case ACTIONS.SET_ERROR:
      return { ...state, error: action.payload, loading: false };

    case ACTIONS.ADD_COLUMN:
      return {
        ...state,
        board: {
          ...state.board,
          columns: [...state.board.columns, action.payload],
        },
      };

    case ACTIONS.UPDATE_COLUMN:
      return {
        ...state,
        board: {
          ...state.board,
          columns: state.board.columns.map((col) =>
            col.id === action.payload.id ? { ...col, ...action.payload } : col
          ),
        },
      };

    case ACTIONS.DELETE_COLUMN:
      return {
        ...state,
        board: {
          ...state.board,
          columns: state.board.columns.filter((col) => col.id !== action.payload),
        },
      };

    case ACTIONS.ADD_CARD: {
      const { columnId, card } = action.payload;
      return {
        ...state,
        board: {
          ...state.board,
          columns: state.board.columns.map((col) =>
            col.id === columnId
              ? { ...col, cards: [...(col.cards || []), card] }
              : col
          ),
        },
      };
    }

    case ACTIONS.UPDATE_CARD: {
      const { cardId, updates } = action.payload;
      return {
        ...state,
        board: {
          ...state.board,
          columns: state.board.columns.map((col) => ({
            ...col,
            cards: (col.cards || []).map((card) =>
              card.id === cardId ? { ...card, ...updates } : card
            ),
          })),
        },
      };
    }

    case ACTIONS.DELETE_CARD: {
      const cardId = action.payload;
      return {
        ...state,
        board: {
          ...state.board,
          columns: state.board.columns.map((col) => ({
            ...col,
            cards: (col.cards || []).filter((card) => card.id !== cardId),
          })),
        },
      };
    }

    case ACTIONS.MOVE_CARD: {
      const { cardId, fromColumnId, toColumnId, newPosition } = action.payload;
      let movedCard = null;

      // Find and remove the card from its current column
      const columnsAfterRemove = state.board.columns.map((col) => {
        if (col.id === fromColumnId) {
          const cardIndex = col.cards.findIndex((c) => c.id === cardId);
          if (cardIndex !== -1) {
            movedCard = { ...col.cards[cardIndex], position: newPosition, column_id: toColumnId };
          }
          return {
            ...col,
            cards: col.cards.filter((c) => c.id !== cardId),
          };
        }
        return col;
      });

      // Add the card to the target column
      if (movedCard) {
        return {
          ...state,
          board: {
            ...state.board,
            columns: columnsAfterRemove.map((col) => {
              if (col.id === toColumnId) {
                const cards = [...col.cards, movedCard].sort(
                  (a, b) => a.position - b.position
                );
                return { ...col, cards };
              }
              return col;
            }),
          },
        };
      }

      return state;
    }

    default:
      return state;
  }
}

/**
 * Custom hook for managing board state and operations.
 * Provides CRUD operations for columns and cards with optimistic updates.
 *
 * @param {number|string} boardId - The ID of the board to load
 * @returns {Object} Board state and action methods
 *
 * @example
 * const { board, loading, error, actions } = useBoard(1);
 * await actions.createColumn('To Do');
 * await actions.createCard(columnId, 'New Task', 'Description');
 * await actions.moveCard(cardId, targetColumnId, newPosition);
 */
export function useBoard(boardId) {
  const [state, dispatch] = useReducer(boardReducer, initialState);
  const { get, post, put, patch, del } = useApi();

  /**
   * Fetches the board data from the API
   */
  const fetchBoard = useCallback(async () => {
    if (!boardId) return;

    dispatch({ type: ACTIONS.SET_LOADING, payload: true });
    try {
      const data = await get(`/boards/${boardId}`);
      // Ensure each column has a cards array
      const boardWithCards = {
        ...data,
        columns: (data.columns || []).map((col) => ({
          ...col,
          cards: col.cards || [],
        })),
      };
      dispatch({ type: ACTIONS.SET_BOARD, payload: boardWithCards });
    } catch (err) {
      dispatch({ type: ACTIONS.SET_ERROR, payload: err.message });
    }
  }, [boardId, get]);

  // Fetch board on mount and when boardId changes
  useEffect(() => {
    fetchBoard();
  }, [fetchBoard]);

  /**
   * Creates a new column in the board
   * @param {string} name - Column name
   */
  const createColumn = useCallback(
    async (name) => {
      try {
        const column = await post(`/boards/${boardId}/columns`, { name });
        dispatch({
          type: ACTIONS.ADD_COLUMN,
          payload: { ...column, cards: [] },
        });
        return column;
      } catch (err) {
        dispatch({ type: ACTIONS.SET_ERROR, payload: err.message });
        throw err;
      }
    },
    [boardId, post]
  );

  /**
   * Updates a column's properties
   * @param {number} columnId - Column ID
   * @param {Object} updates - Properties to update
   */
  const updateColumn = useCallback(
    async (columnId, updates) => {
      // Optimistic update
      dispatch({
        type: ACTIONS.UPDATE_COLUMN,
        payload: { id: columnId, ...updates },
      });

      try {
        await put(`/columns/${columnId}`, updates);
      } catch (err) {
        // Rollback on error by refetching
        fetchBoard();
        throw err;
      }
    },
    [put, fetchBoard]
  );

  /**
   * Deletes a column and all its cards
   * @param {number} columnId - Column ID
   */
  const deleteColumn = useCallback(
    async (columnId) => {
      // Store for rollback
      const previousBoard = state.board;

      // Optimistic update
      dispatch({ type: ACTIONS.DELETE_COLUMN, payload: columnId });

      try {
        await del(`/columns/${columnId}`);
      } catch (err) {
        // Rollback on error
        dispatch({ type: ACTIONS.SET_BOARD, payload: previousBoard });
        throw err;
      }
    },
    [del, state.board]
  );

  /**
   * Creates a new card in a column
   * @param {number} columnId - Target column ID
   * @param {string} title - Card title
   * @param {string} [description] - Card description
   */
  const createCard = useCallback(
    async (columnId, title, description = '') => {
      try {
        const card = await post(`/columns/${columnId}/cards`, {
          title,
          description,
        });
        dispatch({
          type: ACTIONS.ADD_CARD,
          payload: { columnId, card },
        });
        return card;
      } catch (err) {
        dispatch({ type: ACTIONS.SET_ERROR, payload: err.message });
        throw err;
      }
    },
    [post]
  );

  /**
   * Updates a card's properties
   * @param {number} cardId - Card ID
   * @param {Object} updates - Properties to update
   */
  const updateCard = useCallback(
    async (cardId, updates) => {
      // Optimistic update
      dispatch({
        type: ACTIONS.UPDATE_CARD,
        payload: { cardId, updates },
      });

      try {
        await put(`/cards/${cardId}`, updates);
      } catch (err) {
        // Rollback on error by refetching
        fetchBoard();
        throw err;
      }
    },
    [put, fetchBoard]
  );

  /**
   * Deletes a card
   * @param {number} cardId - Card ID
   */
  const deleteCard = useCallback(
    async (cardId) => {
      // Store for rollback
      const previousBoard = state.board;

      // Optimistic update
      dispatch({ type: ACTIONS.DELETE_CARD, payload: cardId });

      try {
        await del(`/cards/${cardId}`);
      } catch (err) {
        // Rollback on error
        dispatch({ type: ACTIONS.SET_BOARD, payload: previousBoard });
        throw err;
      }
    },
    [del, state.board]
  );

  /**
   * Moves a card to a new column and/or position
   * @param {number} cardId - Card ID
   * @param {number} targetColumnId - Target column ID
   * @param {number} targetPosition - New position value
   */
  const moveCard = useCallback(
    async (cardId, targetColumnId, targetPosition) => {
      // Find the source column
      let fromColumnId = null;
      for (const col of state.board.columns) {
        if (col.cards.some((c) => c.id === cardId)) {
          fromColumnId = col.id;
          break;
        }
      }

      if (!fromColumnId) return;

      // Store for rollback
      const previousBoard = state.board;

      // Optimistic update
      dispatch({
        type: ACTIONS.MOVE_CARD,
        payload: {
          cardId,
          fromColumnId,
          toColumnId: targetColumnId,
          newPosition: targetPosition,
        },
      });

      try {
        await patch(`/cards/${cardId}/move`, {
          columnId: targetColumnId,
          position: targetPosition,
        });
      } catch (err) {
        // Rollback on error
        dispatch({ type: ACTIONS.SET_BOARD, payload: previousBoard });
        throw err;
      }
    },
    [patch, state.board]
  );

  return {
    board: state.board,
    loading: state.loading,
    error: state.error,
    refetch: fetchBoard,
    actions: {
      createColumn,
      updateColumn,
      deleteColumn,
      createCard,
      updateCard,
      deleteCard,
      moveCard,
    },
  };
}

import { useState, useCallback, useRef } from 'react';

/**
 * Custom hook for managing HTML5 drag-and-drop operations.
 * Provides drag state, handlers, and position calculation utilities.
 *
 * @param {Object} options - Configuration options
 * @param {Function} options.onDrop - Callback when a card is dropped
 * @returns {Object} Drag state and handler generators
 *
 * @example
 * const { isDragging, draggedItem, createDragHandlers, createDropHandlers } = useDragDrop({
 *   onDrop: (cardId, targetColumnId, position) => moveCard(cardId, targetColumnId, position)
 * });
 *
 * // On draggable card:
 * const dragHandlers = createDragHandlers(card);
 * <div {...dragHandlers}>...</div>
 *
 * // On drop zone (column):
 * const dropHandlers = createDropHandlers(columnId, cards);
 * <div {...dropHandlers}>...</div>
 */
export function useDragDrop({ onDrop }) {
  const [isDragging, setIsDragging] = useState(false);
  const [draggedItem, setDraggedItem] = useState(null);
  const [dropTargetId, setDropTargetId] = useState(null);
  const dragImageRef = useRef(null);

  /**
   * Calculates the new position for a dropped card based on drop location
   * @param {number} dropY - Y coordinate of drop
   * @param {Array} cards - Cards in the target column
   * @param {HTMLElement} columnElement - The column DOM element
   * @returns {number} Calculated position value
   */
  const calculatePosition = useCallback((dropY, cards, columnElement) => {
    if (!cards || cards.length === 0) {
      return 1024; // First card in empty column
    }

    const cardElements = columnElement.querySelectorAll('[data-card-id]');
    const sortedCards = [...cards].sort((a, b) => a.position - b.position);

    // Find insertion point based on Y coordinate
    for (let i = 0; i < cardElements.length; i++) {
      const rect = cardElements[i].getBoundingClientRect();
      const midpoint = rect.top + rect.height / 2;

      if (dropY < midpoint) {
        // Insert before this card
        if (i === 0) {
          return sortedCards[0].position / 2;
        }
        return (sortedCards[i - 1].position + sortedCards[i].position) / 2;
      }
    }

    // Insert at the end
    return sortedCards[sortedCards.length - 1].position + 1024;
  }, []);

  /**
   * Creates drag event handlers for a draggable card
   * @param {Object} card - The card being made draggable
   * @returns {Object} Event handler props
   */
  const createDragHandlers = useCallback(
    (card) => ({
      draggable: true,
      'data-card-id': card.id,

      onDragStart: (e) => {
        setIsDragging(true);
        setDraggedItem(card);

        // Set drag data for cross-browser compatibility
        e.dataTransfer.setData('text/plain', JSON.stringify({
          cardId: card.id,
          columnId: card.column_id,
        }));
        e.dataTransfer.effectAllowed = 'move';

        // Create custom drag image
        const element = e.currentTarget;
        if (element) {
          // Clone the element for drag image
          const clone = element.cloneNode(true);
          clone.style.position = 'absolute';
          clone.style.top = '-1000px';
          clone.style.opacity = '0.8';
          clone.style.transform = 'rotate(3deg)';
          document.body.appendChild(clone);
          dragImageRef.current = clone;
          e.dataTransfer.setDragImage(clone, 20, 20);
        }
      },

      onDragEnd: () => {
        setIsDragging(false);
        setDraggedItem(null);
        setDropTargetId(null);

        // Clean up drag image
        if (dragImageRef.current) {
          document.body.removeChild(dragImageRef.current);
          dragImageRef.current = null;
        }
      },
    }),
    []
  );

  /**
   * Creates drop event handlers for a column drop zone
   * @param {number} columnId - The column ID
   * @param {Array} cards - Cards currently in the column
   * @returns {Object} Event handler props
   */
  const createDropHandlers = useCallback(
    (columnId, cards) => ({
      onDragOver: (e) => {
        e.preventDefault();
        e.dataTransfer.dropEffect = 'move';
        setDropTargetId(columnId);
      },

      onDragEnter: (e) => {
        e.preventDefault();
        setDropTargetId(columnId);
      },

      onDragLeave: (e) => {
        // Only clear if leaving the column entirely
        const relatedTarget = e.relatedTarget;
        if (!e.currentTarget.contains(relatedTarget)) {
          setDropTargetId(null);
        }
      },

      onDrop: (e) => {
        e.preventDefault();
        setDropTargetId(null);

        try {
          const data = JSON.parse(e.dataTransfer.getData('text/plain'));
          const { cardId } = data;

          // Filter out the dragged card from position calculation
          const filteredCards = cards.filter((c) => c.id !== cardId);
          const position = calculatePosition(e.clientY, filteredCards, e.currentTarget);

          if (onDrop) {
            onDrop(cardId, columnId, position);
          }
        } catch (err) {
          // Invalid drag data, ignore
        }
      },
    }),
    [calculatePosition, onDrop]
  );

  return {
    isDragging,
    draggedItem,
    dropTargetId,
    createDragHandlers,
    createDropHandlers,
  };
}

