/**
 * Kanban Board Styles
 *
 * Design System: Clean, professional workspace aesthetic
 * - Warm neutral palette with subtle color accents
 * - Generous spacing for visual breathing room
 * - Soft shadows for depth without heaviness
 * - Typography: DM Sans for modern, readable text
 */

/* ================================
   CSS Variables (Design Tokens)
   ================================ */
:root {
  /* Primary Colors */
  --color-primary: #4f46e5;
  --color-primary-hover: #4338ca;
  --color-primary-light: #e0e7ff;

  /* Neutral Palette - Warm Grays */
  --color-bg: #f8f7f4;
  --color-bg-elevated: #ffffff;
  --color-bg-column: #f1f0ed;
  --color-bg-card: #ffffff;
  --color-bg-hover: #f5f4f1;

  /* Text Colors */
  --color-text: #1f1f1f;
  --color-text-secondary: #6b6966;
  --color-text-muted: #9a9895;
  --color-text-inverse: #ffffff;

  /* Border Colors */
  --color-border: #e5e4e1;
  --color-border-focus: #4f46e5;

  /* Feedback Colors */
  --color-success: #059669;
  --color-error: #dc2626;
  --color-warning: #d97706;

  /* Drop Zone Colors */
  --color-drop-zone: #dbeafe;
  --color-drop-zone-border: #93c5fd;

  /* Spacing Scale */
  --space-1: 0.25rem;
  --space-2: 0.5rem;
  --space-3: 0.75rem;
  --space-4: 1rem;
  --space-5: 1.25rem;
  --space-6: 1.5rem;
  --space-8: 2rem;
  --space-10: 2.5rem;
  --space-12: 3rem;

  /* Border Radius */
  --radius-sm: 4px;
  --radius-md: 6px;
  --radius-lg: 8px;
  --radius-xl: 12px;

  /* Shadows - Soft and Layered */
  --shadow-sm: 0 1px 2px rgba(0, 0, 0, 0.04);
  --shadow-md: 0 2px 4px rgba(0, 0, 0, 0.04), 0 4px 8px rgba(0, 0, 0, 0.04);
  --shadow-lg: 0 4px 8px rgba(0, 0, 0, 0.04), 0 8px 16px rgba(0, 0, 0, 0.06);
  --shadow-xl: 0 8px 16px rgba(0, 0, 0, 0.06), 0 16px 32px rgba(0, 0, 0, 0.08);

  /* Transitions */
  --transition-fast: 120ms ease-out;
  --transition-normal: 200ms ease-out;
  --transition-slow: 300ms ease-out;

  /* Layout */
  --column-width: 300px;
  --column-min-height: 200px;
  --header-height: 64px;
}

/* ================================
   Base Reset & Typography
   ================================ */
*,
*::before,
*::after {
  box-sizing: border-box;
  margin: 0;
  padding: 0;
}

html {
  font-size: 16px;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}

body {
  font-family: 'DM Sans', -apple-system, BlinkMacSystemFont, 'Segoe UI', sans-serif;
  font-size: 0.9375rem;
  line-height: 1.5;
  color: var(--color-text);
  background-color: var(--color-bg);
  min-height: 100vh;
}

/* Focus Styles for Accessibility */
:focus-visible {
  outline: 2px solid var(--color-border-focus);
  outline-offset: 2px;
}

/* ================================
   App Layout
   ================================ */
.app {
  min-height: 100vh;
  display: flex;
  flex-direction: column;
}

.app-header {
  height: var(--header-height);
  background-color: var(--color-bg-elevated);
  border-bottom: 1px solid var(--color-border);
  display: flex;
  align-items: center;
  padding: 0 var(--space-6);
  position: sticky;
  top: 0;
  z-index: 100;
}

.app-title {
  font-size: 1.25rem;
  font-weight: 600;
  color: var(--color-text);
  letter-spacing: -0.02em;
}

.app-nav {
  display: flex;
  gap: var(--space-4);
  margin-left: var(--space-8);
}

.app-nav-link {
  color: var(--color-text-secondary);
  text-decoration: none;
  font-weight: 500;
  padding: var(--space-2) var(--space-3);
  border-radius: var(--radius-md);
  transition: color var(--transition-fast), background-color var(--transition-fast);
}

.app-nav-link:hover {
  color: var(--color-text);
  background-color: var(--color-bg-hover);
}

.app-nav-link.active {
  color: var(--color-primary);
  background-color: var(--color-primary-light);
}

.app-main {
  flex: 1;
  display: flex;
  flex-direction: column;
}

/* ================================
   Board List (Home)
   ================================ */
.board-list {
  padding: var(--space-8);
  max-width: 800px;
  margin: 0 auto;
}

.board-list-header {
  display: flex;
  align-items: center;
  justify-content: space-between;
  margin-bottom: var(--space-6);
}

.board-list-title {
  font-size: 1.5rem;
  font-weight: 600;
  letter-spacing: -0.02em;
}

.board-list-grid {
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(280px, 1fr));
  gap: var(--space-4);
}

.board-list-item {
  background: var(--color-bg-elevated);
  border: 1px solid var(--color-border);
  border-radius: var(--radius-lg);
  padding: var(--space-5);
  cursor: pointer;
  transition: transform var(--transition-fast), box-shadow var(--transition-fast);
}

.board-list-item:hover {
  transform: translateY(-2px);
  box-shadow: var(--shadow-md);
}

.board-list-item-name {
  font-weight: 600;
  margin-bottom: var(--space-2);
}

.board-list-item-meta {
  font-size: 0.875rem;
  color: var(--color-text-muted);
}

/* ================================
   Board Container
   ================================ */
.board-container {
  flex: 1;
  display: flex;
  flex-direction: column;
  overflow: hidden;
}

.board-header {
  padding: var(--space-4) var(--space-6);
  background: var(--color-bg-elevated);
  border-bottom: 1px solid var(--color-border);
  display: flex;
  align-items: center;
  gap: var(--space-4);
}

.board-title {
  font-size: 1.125rem;
  font-weight: 600;
  letter-spacing: -0.01em;
}

.board-title-input {
  font-size: 1.125rem;
  font-weight: 600;
  letter-spacing: -0.01em;
  border: none;
  background: transparent;
  padding: var(--space-1) var(--space-2);
  margin: calc(-1 * var(--space-1)) calc(-1 * var(--space-2));
  border-radius: var(--radius-sm);
  color: var(--color-text);
}

.board-title-input:hover {
  background-color: var(--color-bg-hover);
}

.board-title-input:focus {
  background-color: var(--color-bg-elevated);
  box-shadow: 0 0 0 2px var(--color-primary-light);
}

/* ================================
   Board (Columns Container)
   ================================ */
.board {
  flex: 1;
  display: flex;
  gap: var(--space-4);
  padding: var(--space-6);
  overflow-x: auto;
  overflow-y: hidden;
  align-items: flex-start;
}

/* Custom Scrollbar */
.board::-webkit-scrollbar {
  height: 8px;
}

.board::-webkit-scrollbar-track {
  background: var(--color-bg);
  border-radius: var(--radius-sm);
}

.board::-webkit-scrollbar-thumb {
  background: var(--color-border);
  border-radius: var(--radius-sm);
}

.board::-webkit-scrollbar-thumb:hover {
  background: var(--color-text-muted);
}

/* ================================
   Column
   ================================ */
.column {
  flex: 0 0 var(--column-width);
  min-height: var(--column-min-height);
  max-height: calc(100vh - var(--header-height) - 140px);
  background-color: var(--color-bg-column);
  border-radius: var(--radius-lg);
  display: flex;
  flex-direction: column;
  transition: background-color var(--transition-normal);
}

.column.drop-target {
  background-color: var(--color-drop-zone);
  outline: 2px dashed var(--color-drop-zone-border);
  outline-offset: -2px;
}

.column-header {
  padding: var(--space-4);
  display: flex;
  align-items: center;
  justify-content: space-between;
  gap: var(--space-2);
}

.column-title {
  font-weight: 600;
  font-size: 0.875rem;
  color: var(--color-text);
  letter-spacing: 0.01em;
  text-transform: uppercase;
}

.column-title-input {
  font-weight: 600;
  font-size: 0.875rem;
  letter-spacing: 0.01em;
  text-transform: uppercase;
  border: none;
  background: transparent;
  padding: var(--space-1) var(--space-2);
  margin: calc(-1 * var(--space-1)) calc(-1 * var(--space-2));
  border-radius: var(--radius-sm);
  color: var(--color-text);
  flex: 1;
  min-width: 0;
}

.column-title-input:hover {
  background-color: rgba(0, 0, 0, 0.04);
}

.column-title-input:focus {
  background-color: var(--color-bg-elevated);
  box-shadow: 0 0 0 2px var(--color-primary-light);
}

.column-count {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  min-width: 24px;
  height: 24px;
  padding: 0 var(--space-2);
  background-color: rgba(0, 0, 0, 0.06);
  border-radius: var(--radius-xl);
  font-size: 0.75rem;
  font-weight: 600;
  color: var(--color-text-secondary);
}

.column-actions {
  display: flex;
  gap: var(--space-1);
}

.column-body {
  flex: 1;
  padding: 0 var(--space-3);
  overflow-y: auto;
  min-height: 80px;
}

/* Cards List Scrollbar */
.column-body::-webkit-scrollbar {
  width: 6px;
}

.column-body::-webkit-scrollbar-track {
  background: transparent;
}

.column-body::-webkit-scrollbar-thumb {
  background: rgba(0, 0, 0, 0.1);
  border-radius: var(--radius-sm);
}

.column-body::-webkit-scrollbar-thumb:hover {
  background: rgba(0, 0, 0, 0.2);
}

.column-footer {
  padding: var(--space-3);
}

/* ================================
   Card
   ================================ */
.card {
  background-color: var(--color-bg-card);
  border: 1px solid var(--color-border);
  border-radius: var(--radius-md);
  padding: var(--space-3);
  margin-bottom: var(--space-2);
  cursor: grab;
  transition: transform var(--transition-fast), box-shadow var(--transition-fast),
    border-color var(--transition-fast);
  user-select: none;
}

.card:hover {
  border-color: var(--color-text-muted);
  box-shadow: var(--shadow-sm);
}

.card:active {
  cursor: grabbing;
}

.card.dragging {
  opacity: 0.5;
  transform: scale(0.98);
  box-shadow: var(--shadow-lg);
}

.card-title {
  font-weight: 500;
  font-size: 0.875rem;
  color: var(--color-text);
  line-height: 1.4;
  margin-bottom: var(--space-1);
  word-break: break-word;
}

.card-description {
  font-size: 0.8125rem;
  color: var(--color-text-secondary);
  line-height: 1.4;
  display: -webkit-box;
  -webkit-line-clamp: 2;
  -webkit-box-orient: vertical;
  overflow: hidden;
}

/* ================================
   Add Card Form
   ================================ */
.add-card-btn {
  width: 100%;
  padding: var(--space-2) var(--space-3);
  background: transparent;
  border: none;
  border-radius: var(--radius-md);
  color: var(--color-text-secondary);
  font-size: 0.875rem;
  font-weight: 500;
  text-align: left;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: var(--space-2);
  transition: background-color var(--transition-fast), color var(--transition-fast);
}

.add-card-btn:hover {
  background-color: rgba(0, 0, 0, 0.04);
  color: var(--color-text);
}

.add-card-btn svg {
  width: 16px;
  height: 16px;
}

.add-card-form {
  background-color: var(--color-bg-card);
  border: 1px solid var(--color-border);
  border-radius: var(--radius-md);
  padding: var(--space-3);
  margin-bottom: var(--space-2);
}

.add-card-input {
  width: 100%;
  border: none;
  background: transparent;
  font-size: 0.875rem;
  color: var(--color-text);
  resize: none;
  font-family: inherit;
  line-height: 1.4;
}

.add-card-input::placeholder {
  color: var(--color-text-muted);
}

.add-card-input:focus {
  outline: none;
}

.add-card-actions {
  display: flex;
  gap: var(--space-2);
  margin-top: var(--space-2);
}

/* ================================
   Add Column Button
   ================================ */
.add-column-btn {
  flex: 0 0 var(--column-width);
  min-height: 80px;
  background-color: rgba(0, 0, 0, 0.03);
  border: 2px dashed var(--color-border);
  border-radius: var(--radius-lg);
  display: flex;
  align-items: center;
  justify-content: center;
  gap: var(--space-2);
  color: var(--color-text-secondary);
  font-weight: 500;
  cursor: pointer;
  transition: background-color var(--transition-fast), border-color var(--transition-fast),
    color var(--transition-fast);
}

.add-column-btn:hover {
  background-color: rgba(0, 0, 0, 0.05);
  border-color: var(--color-text-muted);
  color: var(--color-text);
}

.add-column-btn svg {
  width: 20px;
  height: 20px;
}

/* ================================
   Modal Overlay
   ================================ */
.modal-overlay {
  position: fixed;
  inset: 0;
  background-color: rgba(0, 0, 0, 0.5);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
  animation: fadeIn var(--transition-normal);
}

@keyframes fadeIn {
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
}

.modal {
  background-color: var(--color-bg-elevated);
  border-radius: var(--radius-xl);
  box-shadow: var(--shadow-xl);
  width: 90%;
  max-width: 500px;
  max-height: 90vh;
  overflow: auto;
  animation: slideUp var(--transition-normal);
}

@keyframes slideUp {
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

.modal-header {
  padding: var(--space-5);
  border-bottom: 1px solid var(--color-border);
  display: flex;
  align-items: center;
  justify-content: space-between;
}

.modal-title {
  font-size: 1.125rem;
  font-weight: 600;
}

.modal-close-btn {
  width: 32px;
  height: 32px;
  border: none;
  background: transparent;
  border-radius: var(--radius-md);
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  color: var(--color-text-secondary);
  transition: background-color var(--transition-fast), color var(--transition-fast);
}

.modal-close-btn:hover {
  background-color: var(--color-bg-hover);
  color: var(--color-text);
}

.modal-close-btn svg {
  width: 20px;
  height: 20px;
}

.modal-body {
  padding: var(--space-5);
}

.modal-footer {
  padding: var(--space-4) var(--space-5);
  border-top: 1px solid var(--color-border);
  display: flex;
  justify-content: space-between;
  gap: var(--space-3);
}

/* ================================
   Form Elements
   ================================ */
.form-group {
  margin-bottom: var(--space-4);
}

.form-label {
  display: block;
  font-size: 0.875rem;
  font-weight: 500;
  color: var(--color-text);
  margin-bottom: var(--space-2);
}

.form-input,
.form-textarea {
  width: 100%;
  padding: var(--space-3);
  border: 1px solid var(--color-border);
  border-radius: var(--radius-md);
  font-size: 0.9375rem;
  font-family: inherit;
  color: var(--color-text);
  background-color: var(--color-bg-elevated);
  transition: border-color var(--transition-fast), box-shadow var(--transition-fast);
}

.form-input::placeholder,
.form-textarea::placeholder {
  color: var(--color-text-muted);
}

.form-input:hover,
.form-textarea:hover {
  border-color: var(--color-text-muted);
}

.form-input:focus,
.form-textarea:focus {
  outline: none;
  border-color: var(--color-border-focus);
  box-shadow: 0 0 0 3px var(--color-primary-light);
}

.form-textarea {
  min-height: 120px;
  resize: vertical;
}

/* ================================
   Buttons
   ================================ */
.btn {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  gap: var(--space-2);
  padding: var(--space-2) var(--space-4);
  border-radius: var(--radius-md);
  font-size: 0.875rem;
  font-weight: 500;
  font-family: inherit;
  cursor: pointer;
  transition: background-color var(--transition-fast), color var(--transition-fast),
    box-shadow var(--transition-fast);
  border: none;
}

.btn:disabled {
  opacity: 0.5;
  cursor: not-allowed;
}

.btn-primary {
  background-color: var(--color-primary);
  color: var(--color-text-inverse);
}

.btn-primary:hover:not(:disabled) {
  background-color: var(--color-primary-hover);
}

.btn-secondary {
  background-color: transparent;
  color: var(--color-text-secondary);
  border: 1px solid var(--color-border);
}

.btn-secondary:hover:not(:disabled) {
  background-color: var(--color-bg-hover);
  color: var(--color-text);
  border-color: var(--color-text-muted);
}

.btn-danger {
  background-color: transparent;
  color: var(--color-error);
}

.btn-danger:hover:not(:disabled) {
  background-color: rgba(220, 38, 38, 0.1);
}

.btn-icon {
  width: 28px;
  height: 28px;
  padding: 0;
  background: transparent;
  border: none;
  border-radius: var(--radius-sm);
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  color: var(--color-text-muted);
  transition: background-color var(--transition-fast), color var(--transition-fast);
}

.btn-icon:hover {
  background-color: rgba(0, 0, 0, 0.06);
  color: var(--color-text);
}

.btn-icon svg {
  width: 16px;
  height: 16px;
}

/* ================================
   Loading State
   ================================ */
.loading {
  display: flex;
  align-items: center;
  justify-content: center;
  padding: var(--space-12);
}

.loading-spinner {
  width: 32px;
  height: 32px;
  border: 3px solid var(--color-border);
  border-top-color: var(--color-primary);
  border-radius: 50%;
  animation: spin 0.8s linear infinite;
}

@keyframes spin {
  to {
    transform: rotate(360deg);
  }
}

/* ================================
   Error State
   ================================ */
.error {
  padding: var(--space-6);
  text-align: center;
  color: var(--color-error);
}

.error-message {
  font-weight: 500;
  margin-bottom: var(--space-3);
}

.error-retry {
  color: var(--color-primary);
  background: none;
  border: none;
  cursor: pointer;
  font-weight: 500;
  text-decoration: underline;
}

/* ================================
   Empty State
   ================================ */
.empty-state {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: var(--space-12);
  text-align: center;
  color: var(--color-text-secondary);
}

.empty-state-icon {
  width: 64px;
  height: 64px;
  margin-bottom: var(--space-4);
  opacity: 0.5;
}

.empty-state-title {
  font-weight: 600;
  font-size: 1.125rem;
  margin-bottom: var(--space-2);
  color: var(--color-text);
}

.empty-state-description {
  margin-bottom: var(--space-4);
}

/* ================================
   Responsive Design
   ================================ */
@media (max-width: 768px) {
  :root {
    --column-width: 280px;
  }

  .app-header {
    padding: 0 var(--space-4);
  }

  .board {
    padding: var(--space-4);
    gap: var(--space-3);
  }

  .board-header {
    padding: var(--space-3) var(--space-4);
  }

  .modal {
    width: 95%;
    margin: var(--space-4);
  }
}

/* ================================
   Screen Reader Only
   ================================ */
.sr-only {
  position: absolute;
  width: 1px;
  height: 1px;
  padding: 0;
  margin: -1px;
  overflow: hidden;
  clip: rect(0, 0, 0, 0);
  white-space: nowrap;
  border: 0;
}

/* ================================
   Utility Classes
   ================================ */
.flex {
  display: flex;
}

.flex-col {
  flex-direction: column;
}

.items-center {
  align-items: center;
}

.justify-between {
  justify-content: space-between;
}

.gap-2 {
  gap: var(--space-2);
}

.gap-4 {
  gap: var(--space-4);
}

.w-full {
  width: 100%;
}

.text-center {
  text-align: center;
}

toolUseResult.content:
/**
 * Kanban Board Styles
 *
 * Design System: Clean, professional workspace aesthetic
 * - Warm neutral palette with subtle color accents
 * - Generous spacing for visual breathing room
 * - Soft shadows for depth without heaviness
 * - Typography: DM Sans for modern, readable text
 */

/* ================================
   CSS Variables (Design Tokens)
   ================================ */
:root {
  /* Primary Colors */
  --color-primary: #4f46e5;
  --color-primary-hover: #4338ca;
  --color-primary-light: #e0e7ff;

  /* Neutral Palette - Warm Grays */
  --color-bg: #f8f7f4;
  --color-bg-elevated: #ffffff;
  --color-bg-column: #f1f0ed;
  --color-bg-card: #ffffff;
  --color-bg-hover: #f5f4f1;

  /* Text Colors */
  --color-text: #1f1f1f;
  --color-text-secondary: #6b6966;
  --color-text-muted: #9a9895;
  --color-text-inverse: #ffffff;

  /* Border Colors */
  --color-border: #e5e4e1;
  --color-border-focus: #4f46e5;

  /* Feedback Colors */
  --color-success: #059669;
  --color-error: #dc2626;
  --color-warning: #d97706;

  /* Drop Zone Colors */
  --color-drop-zone: #dbeafe;
  --color-drop-zone-border: #93c5fd;

  /* Spacing Scale */
  --space-1: 0.25rem;
  --space-2: 0.5rem;
  --space-3: 0.75rem;
  --space-4: 1rem;
  --space-5: 1.25rem;
  --space-6: 1.5rem;
  --space-8: 2rem;
  --space-10: 2.5rem;
  --space-12: 3rem;

  /* Border Radius */
  --radius-sm: 4px;
  --radius-md: 6px;
  --radius-lg: 8px;
  --radius-xl: 12px;

  /* Shadows - Soft and Layered */
  --shadow-sm: 0 1px 2px rgba(0, 0, 0, 0.04);
  --shadow-md: 0 2px 4px rgba(0, 0, 0, 0.04), 0 4px 8px rgba(0, 0, 0, 0.04);
  --shadow-lg: 0 4px 8px rgba(0, 0, 0, 0.04), 0 8px 16px rgba(0, 0, 0, 0.06);
  --shadow-xl: 0 8px 16px rgba(0, 0, 0, 0.06), 0 16px 32px rgba(0, 0, 0, 0.08);

  /* Transitions */
  --transition-fast: 120ms ease-out;
  --transition-normal: 200ms ease-out;
  --transition-slow: 300ms ease-out;

  /* Layout */
  --column-width: 300px;
  --column-min-height: 200px;
  --header-height: 64px;
}

/* ================================
   Base Reset & Typography
   ================================ */
*,
*::before,
*::after {
  box-sizing: border-box;
  margin: 0;
  padding: 0;
}

html {
  font-size: 16px;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}

body {
  font-family: 'DM Sans', -apple-system, BlinkMacSystemFont, 'Segoe UI', sans-serif;
  font-size: 0.9375rem;
  line-height: 1.5;
  color: var(--color-text);
  background-color: var(--color-bg);
  min-height: 100vh;
}

/* Focus Styles for Accessibility */
:focus-visible {
  outline: 2px solid var(--color-border-focus);
  outline-offset: 2px;
}

/* ================================
   App Layout
   ================================ */
.app {
  min-height: 100vh;
  display: flex;
  flex-direction: column;
}

.app-header {
  height: var(--header-height);
  background-color: var(--color-bg-elevated);
  border-bottom: 1px solid var(--color-border);
  display: flex;
  align-items: center;
  padding: 0 var(--space-6);
  position: sticky;
  top: 0;
  z-index: 100;
}

.app-title {
  font-size: 1.25rem;
  font-weight: 600;
  color: var(--color-text);
  letter-spacing: -0.02em;
}

.app-nav {
  display: flex;
  gap: var(--space-4);
  margin-left: var(--space-8);
}

.app-nav-link {
  color: var(--color-text-secondary);
  text-decoration: none;
  font-weight: 500;
  padding: var(--space-2) var(--space-3);
  border-radius: var(--radius-md);
  transition: color var(--transition-fast), background-color var(--transition-fast);
}

.app-nav-link:hover {
  color: var(--color-text);
  background-color: var(--color-bg-hover);
}

.app-nav-link.active {
  color: var(--color-primary);
  background-color: var(--color-primary-light);
}

.app-main {
  flex: 1;
  display: flex;
  flex-direction: column;
}

/* ================================
   Board List (Home)
   ================================ */
.board-list {
  padding: var(--space-8);
  max-width: 800px;
  margin: 0 auto;
}

.board-list-header {
  display: flex;
  align-items: center;
  justify-content: space-between;
  margin-bottom: var(--space-6);
}

.board-list-title {
  font-size: 1.5rem;
  font-weight: 600;
  letter-spacing: -0.02em;
}

.board-list-grid {
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(280px, 1fr));
  gap: var(--space-4);
}

.board-list-item {
  background: var(--color-bg-elevated);
  border: 1px solid var(--color-border);
  border-radius: var(--radius-lg);
  padding: var(--space-5);
  cursor: pointer;
  transition: transform var(--transition-fast), box-shadow var(--transition-fast);
}

.board-list-item:hover {
  transform: translateY(-2px);
  box-shadow: var(--shadow-md);
}

.board-list-item-name {
  font-weight: 600;
  margin-bottom: var(--space-2);
}

.board-list-item-meta {
  font-size: 0.875rem;
  color: var(--color-text-muted);
}

/* ================================
   Board Container
   ================================ */
.board-container {
  flex: 1;
  display: flex;
  flex-direction: column;
  overflow: hidden;
}

.board-header {
  padding: var(--space-4) var(--space-6);
  background: var(--color-bg-elevated);
  border-bottom: 1px solid var(--color-border);
  display: flex;
  align-items: center;
  gap: var(--space-4);
}

.board-title {
  font-size: 1.125rem;
  font-weight: 600;
  letter-spacing: -0.01em;
}

.board-title-input {
  font-size: 1.125rem;
  font-weight: 600;
  letter-spacing: -0.01em;
  border: none;
  background: transparent;
  padding: var(--space-1) var(--space-2);
  margin: calc(-1 * var(--space-1)) calc(-1 * var(--space-2));
  border-radius: var(--radius-sm);
  color: var(--color-text);
}

.board-title-input:hover {
  background-color: var(--color-bg-hover);
}

.board-title-input:focus {
  background-color: var(--color-bg-elevated);
  box-shadow: 0 0 0 2px var(--color-primary-light);
}

/* ================================
   Board (Columns Container)
   ================================ */
.board {
  flex: 1;
  display: flex;
  gap: var(--space-4);
  padding: var(--space-6);
  overflow-x: auto;
  overflow-y: hidden;
  align-items: flex-start;
}

/* Custom Scrollbar */
.board::-webkit-scrollbar {
  height: 8px;
}

.board::-webkit-scrollbar-track {
  background: var(--color-bg);
  border-radius: var(--radius-sm);
}

.board::-webkit-scrollbar-thumb {
  background: var(--color-border);
  border-radius: var(--radius-sm);
}

.board::-webkit-scrollbar-thumb:hover {
  background: var(--color-text-muted);
}

/* ================================
   Column
   ================================ */
.column {
  flex: 0 0 var(--column-width);
  min-height: var(--column-min-height);
  max-height: calc(100vh - var(--header-height) - 140px);
  background-color: var(--color-bg-column);
  border-radius: var(--radius-lg);
  display: flex;
  flex-direction: column;
  transition: background-color var(--transition-normal);
}

.column.drop-target {
  background-color: var(--color-drop-zone);
  outline: 2px dashed var(--color-drop-zone-border);
  outline-offset: -2px;
}

.column-header {
  padding: var(--space-4);
  display: flex;
  align-items: center;
  justify-content: space-between;
  gap: var(--space-2);
}

.column-title {
  font-weight: 600;
  font-size: 0.875rem;
  color: var(--color-text);
  letter-spacing: 0.01em;
  text-transform: uppercase;
}

.column-title-input {
  font-weight: 600;
  font-size: 0.875rem;
  letter-spacing: 0.01em;
  text-transform: uppercase;
  border: none;
  background: transparent;
  padding: var(--space-1) var(--space-2);
  margin: calc(-1 * var(--space-1)) calc(-1 * var(--space-2));
  border-radius: var(--radius-sm);
  color: var(--color-text);
  flex: 1;
  min-width: 0;
}

.column-title-input:hover {
  background-color: rgba(0, 0, 0, 0.04);
}

.column-title-input:focus {
  background-color: var(--color-bg-elevated);
  box-shadow: 0 0 0 2px var(--color-primary-light);
}

.column-count {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  min-width: 24px;
  height: 24px;
  padding: 0 var(--space-2);
  background-color: rgba(0, 0, 0, 0.06);
  border-radius: var(--radius-xl);
  font-size: 0.75rem;
  font-weight: 600;
  color: var(--color-text-secondary);
}

.column-actions {
  display: flex;
  gap: var(--space-1);
}

.column-body {
  flex: 1;
  padding: 0 var(--space-3);
  overflow-y: auto;
  min-height: 80px;
}

/* Cards List Scrollbar */
.column-body::-webkit-scrollbar {
  width: 6px;
}

.column-body::-webkit-scrollbar-track {
  background: transparent;
}

.column-body::-webkit-scrollbar-thumb {
  background: rgba(0, 0, 0, 0.1);
  border-radius: var(--radius-sm);
}

.column-body::-webkit-scrollbar-thumb:hover {
  background: rgba(0, 0, 0, 0.2);
}

.column-footer {
  padding: var(--space-3);
}

/* ================================
   Card
   ================================ */
.card {
  background-color: var(--color-bg-card);
  border: 1px solid var(--color-border);
  border-radius: var(--radius-md);
  padding: var(--space-3);
  margin-bottom: var(--space-2);
  cursor: grab;
  transition: transform var(--transition-fast), box-shadow var(--transition-fast),
    border-color var(--transition-fast);
  user-select: none;
}

.card:hover {
  border-color: var(--color-text-muted);
  box-shadow: var(--shadow-sm);
}

.card:active {
  cursor: grabbing;
}

.card.dragging {
  opacity: 0.5;
  transform: scale(0.98);
  box-shadow: var(--shadow-lg);
}

.card-title {
  font-weight: 500;
  font-size: 0.875rem;
  color: var(--color-text);
  line-height: 1.4;
  margin-bottom: var(--space-1);
  word-break: break-word;
}

.card-description {
  font-size: 0.8125rem;
  color: var(--color-text-secondary);
  line-height: 1.4;
  display: -webkit-box;
  -webkit-line-clamp: 2;
  -webkit-box-orient: vertical;
  overflow: hidden;
}

/* ================================
   Add Card Form
   ================================ */
.add-card-btn {
  width: 100%;
  padding: var(--space-2) var(--space-3);
  background: transparent;
  border: none;
  border-radius: var(--radius-md);
  color: var(--color-text-secondary);
  font-size: 0.875rem;
  font-weight: 500;
  text-align: left;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: var(--space-2);
  transition: background-color var(--transition-fast), color var(--transition-fast);
}

.add-card-btn:hover {
  background-color: rgba(0, 0, 0, 0.04);
  color: var(--color-text);
}

.add-card-btn svg {
  width: 16px;
  height: 16px;
}

.add-card-form {
  background-color: var(--color-bg-card);
  border: 1px solid var(--color-border);
  border-radius: var(--radius-md);
  padding: var(--space-3);
  margin-bottom: var(--space-2);
}

.add-card-input {
  width: 100%;
  border: none;
  background: transparent;
  font-size: 0.875rem;
  color: var(--color-text);
  resize: none;
  font-family: inherit;
  line-height: 1.4;
}

.add-card-input::placeholder {
  color: var(--color-text-muted);
}

.add-card-input:focus {
  outline: none;
}

.add-card-actions {
  display: flex;
  gap: var(--space-2);
  margin-top: var(--space-2);
}

/* ================================
   Add Column Button
   ================================ */
.add-column-btn {
  flex: 0 0 var(--column-width);
  min-height: 80px;
  background-color: rgba(0, 0, 0, 0.03);
  border: 2px dashed var(--color-border);
  border-radius: var(--radius-lg);
  display: flex;
  align-items: center;
  justify-content: center;
  gap: var(--space-2);
  color: var(--color-text-secondary);
  font-weight: 500;
  cursor: pointer;
  transition: background-color var(--transition-fast), border-color var(--transition-fast),
    color var(--transition-fast);
}

.add-column-btn:hover {
  background-color: rgba(0, 0, 0, 0.05);
  border-color: var(--color-text-muted);
  color: var(--color-text);
}

.add-column-btn svg {
  width: 20px;
  height: 20px;
}

/* ================================
   Modal Overlay
   ================================ */
.modal-overlay {
  position: fixed;
  inset: 0;
  background-color: rgba(0, 0, 0, 0.5);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
  animation: fadeIn var(--transition-normal);
}

@keyframes fadeIn {
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
}

.modal {
  background-color: var(--color-bg-elevated);
  border-radius: var(--radius-xl);
  box-shadow: var(--shadow-xl);
  width: 90%;
  max-width: 500px;
  max-height: 90vh;
  overflow: auto;
  animation: slideUp var(--transition-normal);
}

@keyframes slideUp {
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

.modal-header {
  padding: var(--space-5);
  border-bottom: 1px solid var(--color-border);
  display: flex;
  align-items: center;
  justify-content: space-between;
}

.modal-title {
  font-size: 1.125rem;
  font-weight: 600;
}

.modal-close-btn {
  width: 32px;
  height: 32px;
  border: none;
  background: transparent;
  border-radius: var(--radius-md);
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  color: var(--color-text-secondary);
  transition: background-color var(--transition-fast), color var(--transition-fast);
}

.modal-close-btn:hover {
  background-color: var(--color-bg-hover);
  color: var(--color-text);
}

.modal-close-btn svg {
  width: 20px;
  height: 20px;
}

.modal-body {
  padding: var(--space-5);
}

.modal-footer {
  padding: var(--space-4) var(--space-5);
  border-top: 1px solid var(--color-border);
  display: flex;
  justify-content: space-between;
  gap: var(--space-3);
}

/* ================================
   Form Elements
   ================================ */
.form-group {
  margin-bottom: var(--space-4);
}

.form-label {
  display: block;
  font-size: 0.875rem;
  font-weight: 500;
  color: var(--color-text);
  margin-bottom: var(--space-2);
}

.form-input,
.form-textarea {
  width: 100%;
  padding: var(--space-3);
  border: 1px solid var(--color-border);
  border-radius: var(--radius-md);
  font-size: 0.9375rem;
  font-family: inherit;
  color: var(--color-text);
  background-color: var(--color-bg-elevated);
  transition: border-color var(--transition-fast), box-shadow var(--transition-fast);
}

.form-input::placeholder,
.form-textarea::placeholder {
  color: var(--color-text-muted);
}

.form-input:hover,
.form-textarea:hover {
  border-color: var(--color-text-muted);
}

.form-input:focus,
.form-textarea:focus {
  outline: none;
  border-color: var(--color-border-focus);
  box-shadow: 0 0 0 3px var(--color-primary-light);
}

.form-textarea {
  min-height: 120px;
  resize: vertical;
}

/* ================================
   Buttons
   ================================ */
.btn {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  gap: var(--space-2);
  padding: var(--space-2) var(--space-4);
  border-radius: var(--radius-md);
  font-size: 0.875rem;
  font-weight: 500;
  font-family: inherit;
  cursor: pointer;
  transition: background-color var(--transition-fast), color var(--transition-fast),
    box-shadow var(--transition-fast);
  border: none;
}

.btn:disabled {
  opacity: 0.5;
  cursor: not-allowed;
}

.btn-primary {
  background-color: var(--color-primary);
  color: var(--color-text-inverse);
}

.btn-primary:hover:not(:disabled) {
  background-color: var(--color-primary-hover);
}

.btn-secondary {
  background-color: transparent;
  color: var(--color-text-secondary);
  border: 1px solid var(--color-border);
}

.btn-secondary:hover:not(:disabled) {
  background-color: var(--color-bg-hover);
  color: var(--color-text);
  border-color: var(--color-text-muted);
}

.btn-danger {
  background-color: transparent;
  color: var(--color-error);
}

.btn-danger:hover:not(:disabled) {
  background-color: rgba(220, 38, 38, 0.1);
}

.btn-icon {
  width: 28px;
  height: 28px;
  padding: 0;
  background: transparent;
  border: none;
  border-radius: var(--radius-sm);
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  color: var(--color-text-muted);
  transition: background-color var(--transition-fast), color var(--transition-fast);
}

.btn-icon:hover {
  background-color: rgba(0, 0, 0, 0.06);
  color: var(--color-text);
}

.btn-icon svg {
  width: 16px;
  height: 16px;
}

/* ================================
   Loading State
   ================================ */
.loading {
  display: flex;
  align-items: center;
  justify-content: center;
  padding: var(--space-12);
}

.loading-spinner {
  width: 32px;
  height: 32px;
  border: 3px solid var(--color-border);
  border-top-color: var(--color-primary);
  border-radius: 50%;
  animation: spin 0.8s linear infinite;
}

@keyframes spin {
  to {
    transform: rotate(360deg);
  }
}

/* ================================
   Error State
   ================================ */
.error {
  padding: var(--space-6);
  text-align: center;
  color: var(--color-error);
}

.error-message {
  font-weight: 500;
  margin-bottom: var(--space-3);
}

.error-retry {
  color: var(--color-primary);
  background: none;
  border: none;
  cursor: pointer;
  font-weight: 500;
  text-decoration: underline;
}

/* ================================
   Empty State
   ================================ */
.empty-state {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: var(--space-12);
  text-align: center;
  color: var(--color-text-secondary);
}

.empty-state-icon {
  width: 64px;
  height: 64px;
  margin-bottom: var(--space-4);
  opacity: 0.5;
}

.empty-state-title {
  font-weight: 600;
  font-size: 1.125rem;
  margin-bottom: var(--space-2);
  color: var(--color-text);
}

.empty-state-description {
  margin-bottom: var(--space-4);
}

