  }

  .app-header {
    padding: 0 var(--space-4);
  }

  .board {
    padding: var(--space-4);
    gap: var(--space-3);
  }

  .board-header {
    padding: var(--space-3) var(--space-4);
  }

  .modal {
    width: 95%;
    margin: var(--space-4);
  }
}

/* ================================
   Screen Reader Only
   ================================ */
.sr-only {
  position: absolute;
  width: 1px;
  height: 1px;
  padding: 0;
  margin: -1px;
  overflow: hidden;
  clip: rect(0, 0, 0, 0);
  white-space: nowrap;
  border: 0;
}

/* ================================
   Utility Classes
   ================================ */
.flex {
  display: flex;
}

.flex-col {
  flex-direction: column;
}

.items-center {
  align-items: center;
}

.justify-between {
  justify-content: space-between;
}

.gap-2 {
  gap: var(--space-2);
}

.gap-4 {
  gap: var(--space-4);
}

.w-full {
  width: 100%;
}

.text-center {
  text-align: center;
}

