import { StrictMode } from 'react';
import { createRoot } from 'react-dom/client';
import App from './App';
import './styles/kanban.css';

const root = createRoot(document.getElementById('root'));

root.render(
  <StrictMode>
    <App />
  </StrictMode>
);

import { useState, useEffect, useCallback } from 'react';
import { useApi } from './hooks/useApi';
import Board from './components/Board';

/**
 * Main application component.
 * Manages board selection and displays either the board list or a selected board.
 */
function App() {
  const [boards, setBoards] = useState([]);
  const [selectedBoardId, setSelectedBoardId] = useState(null);
  const [isCreating, setIsCreating] = useState(false);
  const [newBoardName, setNewBoardName] = useState('');
  const { get, post, del, loading, error } = useApi();

  /**
   * Fetches all boards from the API
   */
  const fetchBoards = useCallback(async () => {
    try {
      const data = await get('/boards');
      setBoards(data);
    } catch (err) {
      // Error is handled by useApi
    }
  }, [get]);

  // Load boards on mount
  useEffect(() => {
    fetchBoards();
  }, [fetchBoards]);

  /**
   * Creates a new board with the given name
   */
  const handleCreateBoard = async (e) => {
    e.preventDefault();
    if (!newBoardName.trim()) return;

    try {
      const board = await post('/boards', { name: newBoardName.trim() });
      setBoards((prev) => [...prev, board]);
      setNewBoardName('');
      setIsCreating(false);
      setSelectedBoardId(board.id);
    } catch (err) {
      // Error is handled by useApi
    }
  };

  /**
   * Deletes a board by ID
   */
  const handleDeleteBoard = async (boardId) => {
    if (!window.confirm('Are you sure you want to delete this board? All columns and cards will be deleted.')) {
      return;
    }

    try {
      await del(`/boards/${boardId}`);
      setBoards((prev) => prev.filter((b) => b.id !== boardId));
      if (selectedBoardId === boardId) {
        setSelectedBoardId(null);
      }
    } catch (err) {
      // Error is handled by useApi
    }
  };

  /**
   * Navigates back to the board list
   */
  const handleBackToList = () => {
    setSelectedBoardId(null);
  };

  // Show selected board
  if (selectedBoardId) {
    return (
      <div className="app">
        <Board
          boardId={selectedBoardId}
          onBack={handleBackToList}
          onDelete={() => handleDeleteBoard(selectedBoardId)}
        />
      </div>
    );
  }

  // Show board list
  return (
    <div className="app">
      <header className="app-header">
        <h1 className="app-title">Kanban Board</h1>
      </header>

      <main className="app-main">
        <div className="board-list">
          <div className="board-list-header">
            <h2 className="board-list-title">Your Boards</h2>
            {!isCreating && (
              <button
                className="btn btn-primary"
                onClick={() => setIsCreating(true)}
                aria-label="Create new board"
              >
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  viewBox="0 0 20 20"
                  fill="currentColor"
                  width="16"
                  height="16"
                  aria-hidden="true"
                >
                  <path d="M10.75 4.75a.75.75 0 00-1.5 0v4.5h-4.5a.75.75 0 000 1.5h4.5v4.5a.75.75 0 001.5 0v-4.5h4.5a.75.75 0 000-1.5h-4.5v-4.5z" />
                </svg>
                New Board
              </button>
            )}
          </div>

          {isCreating && (
            <form onSubmit={handleCreateBoard} className="add-card-form" style={{ marginBottom: 'var(--space-4)' }}>
              <input
                type="text"
                value={newBoardName}
                onChange={(e) => setNewBoardName(e.target.value)}
                placeholder="Enter board name..."
                className="form-input"
                autoFocus
                aria-label="New board name"
              />
              <div className="add-card-actions">
                <button type="submit" className="btn btn-primary" disabled={!newBoardName.trim()}>
                  Create
                </button>
                <button
                  type="button"
                  className="btn btn-secondary"
                  onClick={() => {
                    setIsCreating(false);
                    setNewBoardName('');
                  }}
                >
                  Cancel
                </button>
              </div>
            </form>
          )}

          {loading && (
            <div className="loading" role="status" aria-label="Loading boards">
              <div className="loading-spinner" />
            </div>
          )}

          {error && (
            <div className="error" role="alert">
              <p className="error-message">Failed to load boards</p>
              <button className="error-retry" onClick={fetchBoards}>
                Try again
              </button>
            </div>
          )}

          {!loading && !error && boards.length === 0 && (
            <div className="empty-state">
              <svg
                className="empty-state-icon"
                xmlns="http://www.w3.org/2000/svg"
                fill="none"
                viewBox="0 0 24 24"
                stroke="currentColor"
                aria-hidden="true"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={1.5}
                  d="M9 17V7m0 10a2 2 0 01-2 2H5a2 2 0 01-2-2V7a2 2 0 012-2h2a2 2 0 012 2m0 10a2 2 0 002 2h2a2 2 0 002-2M9 7a2 2 0 012-2h2a2 2 0 012 2m0 10V7m0 10a2 2 0 002 2h2a2 2 0 002-2V7a2 2 0 00-2-2h-2a2 2 0 00-2 2"
                />
              </svg>
              <h3 className="empty-state-title">No boards yet</h3>
              <p className="empty-state-description">
                Create your first board to start organizing tasks
              </p>
              <button className="btn btn-primary" onClick={() => setIsCreating(true)}>
                Create Board
              </button>
            </div>
          )}

          {!loading && !error && boards.length > 0 && (
            <div className="board-list-grid">
              {boards.map((board) => (
                <article
                  key={board.id}
                  className="board-list-item"
                  onClick={() => setSelectedBoardId(board.id)}
                  onKeyDown={(e) => {
                    if (e.key === 'Enter' || e.key === ' ') {
                      e.preventDefault();
                      setSelectedBoardId(board.id);
                    }
                  }}
                  tabIndex={0}
                  role="button"
                  aria-label={`Open ${board.name} board`}
                >
                  <h3 className="board-list-item-name">{board.name}</h3>
                  <p className="board-list-item-meta">
                    Created {new Date(board.created_at).toLocaleDateString()}
                  </p>
                </article>
              ))}
            </div>
          )}
        </div>
      </main>
    </div>
  );
}

export default App;

