import { memo } from 'react';

/**
 * Card component representing a single task card.
 * Draggable and clickable to open the edit modal.
 *
 * @param {Object} props - Component props
 * @param {Object} props.card - Card data (id, title, description, position)
 * @param {Function} props.onClick - Handler for card click (opens modal)
 * @param {Object} props.dragHandlers - Drag event handlers from useDragDrop
 * @param {boolean} props.isDragging - Whether this card is currently being dragged
 */
function Card({ card, onClick, dragHandlers, isDragging }) {
  const handleClick = () => {
    if (onClick) {
      onClick(card);
    }
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter' || e.key === ' ') {
      e.preventDefault();
      handleClick();
    }
  };

  return (
    <article
      className={`card ${isDragging ? 'dragging' : ''}`}
      onClick={handleClick}
      onKeyDown={handleKeyDown}
      tabIndex={0}
      role="button"
      aria-label={`Edit card: ${card.title}`}
      {...dragHandlers}
    >
      <h4 className="card-title">{card.title}</h4>
      {card.description && (
        <p className="card-description">{card.description}</p>
      )}
    </article>
  );
}

// Memoize to prevent unnecessary re-renders during drag operations
export default memo(Card, (prevProps, nextProps) => {
  return (
    prevProps.card.id === nextProps.card.id &&
    prevProps.card.title === nextProps.card.title &&
    prevProps.card.description === nextProps.card.description &&
    prevProps.card.position === nextProps.card.position &&
    prevProps.isDragging === nextProps.isDragging
  );
});

import { useState, useRef, useEffect } from 'react';

/**
 * Inline form for quickly adding new cards to a column.
 * Shows as a button initially, expands to form on click.
 *
 * @param {Object} props - Component props
 * @param {Function} props.onSubmit - Callback when form is submitted (receives title)
 * @param {Function} props.onCancel - Callback when form is cancelled
 */
export default function AddCardForm({ onSubmit, onCancel }) {
  const [isOpen, setIsOpen] = useState(false);
  const [title, setTitle] = useState('');
  const inputRef = useRef(null);

  // Focus input when form opens
  useEffect(() => {
    if (isOpen && inputRef.current) {
      inputRef.current.focus();
    }
  }, [isOpen]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (title.trim()) {
      onSubmit(title.trim());
      setTitle('');
      // Keep form open for quick multiple card creation
    }
  };

  const handleCancel = () => {
    setIsOpen(false);
    setTitle('');
    if (onCancel) {
      onCancel();
    }
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Escape') {
      handleCancel();
    }
  };

  if (!isOpen) {
    return (
      <button
        className="add-card-btn"
        onClick={() => setIsOpen(true)}
        type="button"
        aria-label="Add a card"
      >
        <svg
          xmlns="http://www.w3.org/2000/svg"
          viewBox="0 0 20 20"
          fill="currentColor"
          aria-hidden="true"
        >
          <path d="M10.75 4.75a.75.75 0 00-1.5 0v4.5h-4.5a.75.75 0 000 1.5h4.5v4.5a.75.75 0 001.5 0v-4.5h4.5a.75.75 0 000-1.5h-4.5v-4.5z" />
        </svg>
        Add a card
      </button>
    );
  }

  return (
    <form onSubmit={handleSubmit} className="add-card-form">
      <textarea
        ref={inputRef}
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        onKeyDown={handleKeyDown}
        placeholder="Enter a title for this card..."
        className="add-card-input"
        rows={2}
        aria-label="Card title"
      />
      <div className="add-card-actions">
        <button
          type="submit"
          className="btn btn-primary"
          disabled={!title.trim()}
        >
          Add Card
        </button>
        <button
          type="button"
          className="btn btn-secondary"
          onClick={handleCancel}
        >
          Cancel
        </button>
      </div>
    </form>
  );
}

import { useState, useEffect, useRef, useCallback } from 'react';

/**
 * Modal dialog for editing card details.
 * Supports title and description editing, and card deletion.
 *
 * @param {Object} props - Component props
 * @param {Object} props.card - Card data to edit
 * @param {Function} props.onSave - Callback to save changes (receives { title, description })
 * @param {Function} props.onDelete - Callback to delete the card
 * @param {Function} props.onClose - Callback to close the modal
 */
export default function CardModal({ card, onSave, onDelete, onClose }) {
  const [title, setTitle] = useState(card?.title || '');
  const [description, setDescription] = useState(card?.description || '');
  const [isDeleting, setIsDeleting] = useState(false);
  const modalRef = useRef(null);
  const titleInputRef = useRef(null);

  // Focus title input on mount
  useEffect(() => {
    if (titleInputRef.current) {
      titleInputRef.current.focus();
      titleInputRef.current.select();
    }
  }, []);

  // Handle escape key to close modal
  useEffect(() => {
    const handleKeyDown = (e) => {
      if (e.key === 'Escape') {
        onClose();
      }
    };

    document.addEventListener('keydown', handleKeyDown);
    return () => document.removeEventListener('keydown', handleKeyDown);
  }, [onClose]);

  // Handle click outside modal to close
  const handleOverlayClick = (e) => {
    if (e.target === e.currentTarget) {
      handleSave();
    }
  };

  /**
   * Saves changes and closes modal
   */
  const handleSave = useCallback(() => {
    if (title.trim() && (title !== card.title || description !== card.description)) {
      onSave({
        title: title.trim(),
        description: description.trim(),
      });
    }
    onClose();
  }, [title, description, card.title, card.description, onSave, onClose]);

  /**
   * Handles delete button click with confirmation
   */
  const handleDelete = () => {
    if (isDeleting) {
      onDelete();
      onClose();
    } else {
      setIsDeleting(true);
    }
  };

  /**
   * Cancels delete confirmation
   */
  const handleCancelDelete = () => {
    setIsDeleting(false);
  };

  if (!card) return null;

  return (
    <div
      className="modal-overlay"
      onClick={handleOverlayClick}
      role="dialog"
      aria-modal="true"
      aria-labelledby="modal-title"
    >
      <div className="modal" ref={modalRef} role="document">
        <div className="modal-header">
          <h2 id="modal-title" className="modal-title">Edit Card</h2>
          <button
            className="modal-close-btn"
            onClick={handleSave}
            aria-label="Close modal"
          >
            <svg
              xmlns="http://www.w3.org/2000/svg"
              viewBox="0 0 20 20"
              fill="currentColor"
              aria-hidden="true"
            >
              <path d="M6.28 5.22a.75.75 0 00-1.06 1.06L8.94 10l-3.72 3.72a.75.75 0 101.06 1.06L10 11.06l3.72 3.72a.75.75 0 101.06-1.06L11.06 10l3.72-3.72a.75.75 0 00-1.06-1.06L10 8.94 6.28 5.22z" />
            </svg>
          </button>
        </div>

        <div className="modal-body">
          <div className="form-group">
            <label htmlFor="card-title" className="form-label">
              Title
            </label>
            <input
              ref={titleInputRef}
              id="card-title"
              type="text"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              className="form-input"
              placeholder="Enter card title..."
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="card-description" className="form-label">
              Description
            </label>
            <textarea
              id="card-description"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              className="form-textarea"
              placeholder="Add a more detailed description..."
              rows={4}
            />
          </div>
        </div>

        <div className="modal-footer">
          {isDeleting ? (
            <div className="flex gap-2">
              <button
                className="btn btn-danger"
                onClick={handleDelete}
                aria-label="Confirm delete"
              >
                Confirm Delete
              </button>
              <button
                className="btn btn-secondary"
                onClick={handleCancelDelete}
              >
                Cancel
              </button>
            </div>
          ) : (
            <button
              className="btn btn-danger"
              onClick={handleDelete}
              aria-label="Delete card"
            >
              <svg
                xmlns="http://www.w3.org/2000/svg"
                viewBox="0 0 20 20"
                fill="currentColor"
                width="16"
                height="16"
                aria-hidden="true"
              >
                <path
                  fillRule="evenodd"
                  d="M8.75 1A2.75 2.75 0 006 3.75v.443c-.795.077-1.584.176-2.365.298a.75.75 0 10.23 1.482l.149-.022.841 10.518A2.75 2.75 0 007.596 19h4.807a2.75 2.75 0 002.742-2.53l.841-10.519.149.023a.75.75 0 00.23-1.482A41.03 41.03 0 0014 4.193V3.75A2.75 2.75 0 0011.25 1h-2.5zM10 4c.84 0 1.673.025 2.5.075V3.75c0-.69-.56-1.25-1.25-1.25h-2.5c-.69 0-1.25.56-1.25 1.25v.325C8.327 4.025 9.16 4 10 4zM8.58 7.72a.75.75 0 00-1.5.06l.3 7.5a.75.75 0 101.5-.06l-.3-7.5zm4.34.06a.75.75 0 10-1.5-.06l-.3 7.5a.75.75 0 101.5.06l.3-7.5z"
                  clipRule="evenodd"
                />
              </svg>
              Delete
            </button>
          )}

          <div className="flex gap-2">
            <button
              className="btn btn-secondary"
              onClick={onClose}
            >
              Cancel
            </button>
            <button
              className="btn btn-primary"
              onClick={handleSave}
              disabled={!title.trim()}
            >
              Save
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

