import { useState, useCallback, memo } from 'react';
import Card from './Card';
import AddCardForm from './AddCardForm';

/**
 * Column component representing a single column in the Kanban board.
 * Contains a list of cards and handles drag-and-drop operations.
 *
 * @param {Object} props - Component props
 * @param {Object} props.column - Column data (id, name, cards, position)
 * @param {Function} props.onUpdateColumn - Callback to update column properties
 * @param {Function} props.onDeleteColumn - Callback to delete the column
 * @param {Function} props.onCreateCard - Callback to create a new card
 * @param {Function} props.onCardClick - Callback when a card is clicked
 * @param {Function} props.createDragHandlers - Function to create drag handlers for cards
 * @param {Function} props.createDropHandlers - Function to create drop handlers for column
 * @param {Object} props.draggedItem - Currently dragged card (if any)
 * @param {number} props.dropTargetId - ID of the column being hovered for drop
 */
function Column({
  column,
  onUpdateColumn,
  onDeleteColumn,
  onCreateCard,
  onCardClick,
  createDragHandlers,
  createDropHandlers,
  draggedItem,
  dropTargetId,
}) {
  const [isEditing, setIsEditing] = useState(false);
  const [editName, setEditName] = useState(column.name);

  // Sort cards by position for display
  const sortedCards = [...(column.cards || [])].sort(
    (a, b) => a.position - b.position
  );

  /**
   * Handles column name edit submission
   */
  const handleNameSubmit = useCallback(() => {
    if (editName.trim() && editName.trim() !== column.name) {
      onUpdateColumn(column.id, { name: editName.trim() });
    } else {
      setEditName(column.name);
    }
    setIsEditing(false);
  }, [editName, column.id, column.name, onUpdateColumn]);

  /**
   * Handles keyboard events in the name input
   */
  const handleNameKeyDown = (e) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      handleNameSubmit();
    } else if (e.key === 'Escape') {
      setEditName(column.name);
      setIsEditing(false);
    }
  };

  /**
   * Handles card creation
   */
  const handleCreateCard = useCallback(
    (title) => {
      onCreateCard(column.id, title);
    },
    [column.id, onCreateCard]
  );

  /**
   * Handles column deletion with confirmation
   */
  const handleDelete = () => {
    if (window.confirm(`Delete "${column.name}" and all its cards?`)) {
      onDeleteColumn(column.id);
    }
  };

  // Get drop handlers for this column
  const dropHandlers = createDropHandlers(column.id, sortedCards);
  const isDropTarget = dropTargetId === column.id;

  return (
    <section
      className={`column ${isDropTarget ? 'drop-target' : ''}`}
      aria-label={`Column: ${column.name}`}
      {...dropHandlers}
    >
      <header className="column-header">
        {isEditing ? (
          <input
            type="text"
            value={editName}
            onChange={(e) => setEditName(e.target.value)}
            onBlur={handleNameSubmit}
            onKeyDown={handleNameKeyDown}
            className="column-title-input"
            autoFocus
            aria-label="Column name"
          />
        ) : (
          <h3
            className="column-title"
            onClick={() => setIsEditing(true)}
