import { useState, useCallback } from 'react';
import { useBoard } from '../hooks/useBoard';
import { useDragDrop } from '../hooks/useDragDrop';
import Column from './Column';
import CardModal from './CardModal';

/**
 * Board component that displays the full Kanban board view.
 * Manages columns, cards, and drag-and-drop operations.
 *
 * @param {Object} props - Component props
 * @param {number} props.boardId - The ID of the board to display
 * @param {Function} props.onBack - Callback to navigate back to board list
 * @param {Function} props.onDelete - Callback to delete the board
 */
export default function Board({ boardId, onBack, onDelete }) {
  const { board, loading, error, actions, refetch } = useBoard(boardId);
  const [selectedCard, setSelectedCard] = useState(null);
  const [isCreatingColumn, setIsCreatingColumn] = useState(false);
  const [newColumnName, setNewColumnName] = useState('');

  /**
   * Handles card drop from drag-and-drop
   */
  const handleCardDrop = useCallback(
    (cardId, targetColumnId, position) => {
      actions.moveCard(cardId, targetColumnId, position);
    },
    [actions]
  );

  const {
    draggedItem,
    dropTargetId,
    createDragHandlers,
    createDropHandlers,
  } = useDragDrop({ onDrop: handleCardDrop });

  /**
   * Opens the card edit modal
   */
  const handleCardClick = useCallback((card) => {
    setSelectedCard(card);
  }, []);

  /**
   * Closes the card edit modal
   */
  const handleCloseModal = useCallback(() => {
    setSelectedCard(null);
  }, []);

  /**
   * Saves card changes from the modal
   */
  const handleSaveCard = useCallback(
    (updates) => {
      if (selectedCard) {
        actions.updateCard(selectedCard.id, updates);
      }
    },
    [selectedCard, actions]
  );

  /**
   * Deletes the selected card
   */
  const handleDeleteCard = useCallback(() => {
    if (selectedCard) {
      actions.deleteCard(selectedCard.id);
    }
  }, [selectedCard, actions]);

  /**
   * Creates a new column
   */
  const handleCreateColumn = async (e) => {
    e.preventDefault();
    if (!newColumnName.trim()) return;

    try {
      await actions.createColumn(newColumnName.trim());
      setNewColumnName('');
      setIsCreatingColumn(false);
    } catch (err) {
      // Error is handled by useBoard
    }
  };

  /**
   * Creates a new card in a column
   */
  const handleCreateCard = useCallback(
    (columnId, title) => {
      actions.createCard(columnId, title);
    },
    [actions]
  );

  // Loading state
  if (loading) {
    return (
      <div className="board-container">
        <header className="board-header">
          <button className="btn btn-secondary" onClick={onBack} aria-label="Back to boards">
            <svg
              xmlns="http://www.w3.org/2000/svg"
              viewBox="0 0 20 20"
              fill="currentColor"
              width="16"
              height="16"
              aria-hidden="true"
            >
              <path
                fillRule="evenodd"
                d="M17 10a.75.75 0 01-.75.75H5.612l4.158 3.96a.75.75 0 11-1.04 1.08l-5.5-5.25a.75.75 0 010-1.08l5.5-5.25a.75.75 0 111.04 1.08L5.612 9.25H16.25A.75.75 0 0117 10z"
                clipRule="evenodd"
              />
            </svg>
            Back
          </button>
        </header>
        <div className="loading" role="status" aria-label="Loading board">
          <div className="loading-spinner" />
        </div>
      </div>
    );
  }

  // Error state
  if (error) {
    return (
      <div className="board-container">
        <header className="board-header">
          <button className="btn btn-secondary" onClick={onBack} aria-label="Back to boards">
            <svg
              xmlns="http://www.w3.org/2000/svg"
              viewBox="0 0 20 20"
              fill="currentColor"
              width="16"
              height="16"
              aria-hidden="true"
            >
              <path
                fillRule="evenodd"
                d="M17 10a.75.75 0 01-.75.75H5.612l4.158 3.96a.75.75 0 11-1.04 1.08l-5.5-5.25a.75.75 0 010-1.08l5.5-5.25a.75.75 0 111.04 1.08L5.612 9.25H16.25A.75.75 0 0117 10z"
                clipRule="evenodd"
              />
            </svg>
            Back
          </button>
        </header>
        <div className="error" role="alert">
          <p className="error-message">Failed to load board: {error}</p>
          <button className="error-retry" onClick={refetch}>
            Try again
          </button>
        </div>
      </div>
    );
  }

  // No board found
  if (!board) {
    return (
      <div className="board-container">
        <header className="board-header">
          <button className="btn btn-secondary" onClick={onBack} aria-label="Back to boards">
            <svg
              xmlns="http://www.w3.org/2000/svg"
              viewBox="0 0 20 20"
              fill="currentColor"
              width="16"
              height="16"
              aria-hidden="true"
            >
              <path
                fillRule="evenodd"
                d="M17 10a.75.75 0 01-.75.75H5.612l4.158 3.96a.75.75 0 11-1.04 1.08l-5.5-5.25a.75.75 0 010-1.08l5.5-5.25a.75.75 0 111.04 1.08L5.612 9.25H16.25A.75.75 0 0117 10z"
                clipRule="evenodd"
              />
            </svg>
            Back
          </button>
        </header>
        <div className="empty-state">
          <p>Board not found</p>
        </div>
      </div>
    );
  }

  // Sort columns by position
  const sortedColumns = [...(board.columns || [])].sort(
    (a, b) => a.position - b.position
  );

  return (
    <div className="board-container">
      <header className="board-header">
        <button className="btn btn-secondary" onClick={onBack} aria-label="Back to boards">
          <svg
            xmlns="http://www.w3.org/2000/svg"
            viewBox="0 0 20 20"
            fill="currentColor"
            width="16"
            height="16"
            aria-hidden="true"
          >
            <path
              fillRule="evenodd"
              d="M17 10a.75.75 0 01-.75.75H5.612l4.158 3.96a.75.75 0 11-1.04 1.08l-5.5-5.25a.75.75 0 010-1.08l5.5-5.25a.75.75 0 111.04 1.08L5.612 9.25H16.25A.75.75 0 0117 10z"
              clipRule="evenodd"
            />
          </svg>
          Back
        </button>

        <h1 className="board-title">{board.name}</h1>

        <div className="flex gap-2" style={{ marginLeft: 'auto' }}>
          <button
            className="btn btn-danger"
            onClick={onDelete}
            aria-label="Delete board"
          >
            <svg
              xmlns="http://www.w3.org/2000/svg"
              viewBox="0 0 20 20"
              fill="currentColor"
              width="16"
              height="16"
              aria-hidden="true"
            >
              <path
                fillRule="evenodd"
                d="M8.75 1A2.75 2.75 0 006 3.75v.443c-.795.077-1.584.176-2.365.298a.75.75 0 10.23 1.482l.149-.022.841 10.518A2.75 2.75 0 007.596 19h4.807a2.75 2.75 0 002.742-2.53l.841-10.519.149.023a.75.75 0 00.23-1.482A41.03 41.03 0 0014 4.193V3.75A2.75 2.75 0 0011.25 1h-2.5zM10 4c.84 0 1.673.025 2.5.075V3.75c0-.69-.56-1.25-1.25-1.25h-2.5c-.69 0-1.25.56-1.25 1.25v.325C8.327 4.025 9.16 4 10 4zM8.58 7.72a.75.75 0 00-1.5.06l.3 7.5a.75.75 0 101.5-.06l-.3-7.5zm4.34.06a.75.75 0 10-1.5-.06l-.3 7.5a.75.75 0 101.5.06l.3-7.5z"
                clipRule="evenodd"
              />
            </svg>
            Delete Board
          </button>
        </div>
      </header>

      <main className="board" role="main" aria-label={`Board: ${board.name}`}>
        {sortedColumns.map((column) => (
          <Column
            key={column.id}
            column={column}
            onUpdateColumn={actions.updateColumn}
            onDeleteColumn={actions.deleteColumn}
            onCreateCard={handleCreateCard}
            onCardClick={handleCardClick}
            createDragHandlers={createDragHandlers}
            createDropHandlers={createDropHandlers}
            draggedItem={draggedItem}
            dropTargetId={dropTargetId}
          />
        ))}

        {isCreatingColumn ? (
          <form onSubmit={handleCreateColumn} className="column" style={{ padding: 'var(--space-4)' }}>
            <input
              type="text"
              value={newColumnName}
              onChange={(e) => setNewColumnName(e.target.value)}
              placeholder="Enter column name..."
              className="form-input"
              autoFocus
              aria-label="New column name"
            />
            <div className="add-card-actions" style={{ marginTop: 'var(--space-3)' }}>
              <button type="submit" className="btn btn-primary" disabled={!newColumnName.trim()}>
                Add Column
              </button>
              <button
                type="button"
                className="btn btn-secondary"
                onClick={() => {
                  setIsCreatingColumn(false);
                  setNewColumnName('');
                }}
              >
                Cancel
              </button>
            </div>
          </form>
        ) : (
          <button
            className="add-column-btn"
            onClick={() => setIsCreatingColumn(true)}
            aria-label="Add a column"
          >
            <svg
              xmlns="http://www.w3.org/2000/svg"
              viewBox="0 0 20 20"
              fill="currentColor"
              aria-hidden="true"
            >
              <path d="M10.75 4.75a.75.75 0 00-1.5 0v4.5h-4.5a.75.75 0 000 1.5h4.5v4.5a.75.75 0 001.5 0v-4.5h4.5a.75.75 0 000-1.5h-4.5v-4.5z" />
            </svg>
            Add Column
          </button>
        )}
      </main>

      {selectedCard && (
        <CardModal
          card={selectedCard}
          onSave={handleSaveCard}
          onDelete={handleDeleteCard}
          onClose={handleCloseModal}
        />
      )}
    </div>
  );
}

