import { render, screen, fireEvent } from '@testing-library/react';
import userEvent from '@testing-library/user-event';
import Card from '../src/components/Card';

// Mock card data
const mockCard = {
  id: 1,
  title: 'Test Task',
  description: 'This is a test task description',
  position: 1024,
  column_id: 1,
};

const mockCardNoDescription = {
  id: 2,
  title: 'Task Without Description',
  description: '',
  position: 2048,
  column_id: 1,
};

// Mock drag handlers
const createMockDragHandlers = () => ({
  draggable: true,
  'data-card-id': mockCard.id,
  onDragStart: jest.fn(),
  onDragEnd: jest.fn(),
});

describe('Card', () => {
  describe('Rendering', () => {
    it('renders the card title', () => {
      render(
        <Card
          card={mockCard}
          onClick={jest.fn()}
          dragHandlers={createMockDragHandlers()}
          isDragging={false}
        />
      );

      expect(screen.getByRole('heading', { name: 'Test Task' })).toBeInTheDocument();
    });

    it('renders the card description when present', () => {
      render(
        <Card
          card={mockCard}
          onClick={jest.fn()}
          dragHandlers={createMockDragHandlers()}
          isDragging={false}
        />
      );

      expect(screen.getByText('This is a test task description')).toBeInTheDocument();
    });

    it('does not render description when empty', () => {
      render(
        <Card
          card={mockCardNoDescription}
          onClick={jest.fn()}
          dragHandlers={createMockDragHandlers()}
          isDragging={false}
        />
      );

      expect(screen.queryByText('', { selector: '.card-description' })).not.toBeInTheDocument();
      // Verify title is still rendered
      expect(screen.getByRole('heading', { name: 'Task Without Description' })).toBeInTheDocument();
    });

    it('applies dragging class when isDragging is true', () => {
      const { container } = render(
        <Card
          card={mockCard}
          onClick={jest.fn()}
          dragHandlers={createMockDragHandlers()}
          isDragging={true}
        />
      );

      expect(container.querySelector('.card')).toHaveClass('dragging');
    });

    it('does not apply dragging class when isDragging is false', () => {
      const { container } = render(
        <Card
          card={mockCard}
          onClick={jest.fn()}
          dragHandlers={createMockDragHandlers()}
          isDragging={false}
        />
      );

      expect(container.querySelector('.card')).not.toHaveClass('dragging');
    });
  });

  describe('Interactions', () => {
    it('calls onClick when card is clicked', async () => {
      const mockOnClick = jest.fn();
      render(
        <Card
          card={mockCard}
          onClick={mockOnClick}
          dragHandlers={createMockDragHandlers()}
          isDragging={false}
        />
      );

      await userEvent.click(screen.getByRole('button', { name: /edit card: test task/i }));

      expect(mockOnClick).toHaveBeenCalledWith(mockCard);
    });

    it('calls onClick when Enter key is pressed', () => {
      const mockOnClick = jest.fn();
      render(
        <Card
          card={mockCard}
          onClick={mockOnClick}
          dragHandlers={createMockDragHandlers()}
          isDragging={false}
        />
      );

      const card = screen.getByRole('button', { name: /edit card: test task/i });
      fireEvent.keyDown(card, { key: 'Enter', code: 'Enter' });

      expect(mockOnClick).toHaveBeenCalledWith(mockCard);
    });

    it('calls onClick when Space key is pressed', () => {
      const mockOnClick = jest.fn();
      render(
        <Card
          card={mockCard}
          onClick={mockOnClick}
          dragHandlers={createMockDragHandlers()}
          isDragging={false}
        />
      );

      const card = screen.getByRole('button', { name: /edit card: test task/i });
      fireEvent.keyDown(card, { key: ' ', code: 'Space' });

      expect(mockOnClick).toHaveBeenCalledWith(mockCard);
    });

    it('does not call onClick when other keys are pressed', () => {
      const mockOnClick = jest.fn();
      render(
        <Card
          card={mockCard}
          onClick={mockOnClick}
          dragHandlers={createMockDragHandlers()}
          isDragging={false}
        />
      );

      const card = screen.getByRole('button', { name: /edit card: test task/i });
      fireEvent.keyDown(card, { key: 'Escape', code: 'Escape' });

      expect(mockOnClick).not.toHaveBeenCalled();
    });
  });

  describe('Drag and Drop', () => {
    it('has draggable attribute', () => {
      render(
        <Card
          card={mockCard}
          onClick={jest.fn()}
          dragHandlers={createMockDragHandlers()}
          isDragging={false}
        />
      );

      expect(screen.getByRole('button')).toHaveAttribute('draggable', 'true');
    });

    it('has data-card-id attribute', () => {
      const dragHandlers = createMockDragHandlers();
      render(
        <Card
          card={mockCard}
          onClick={jest.fn()}
          dragHandlers={dragHandlers}
          isDragging={false}
        />
      );

      expect(screen.getByRole('button')).toHaveAttribute('data-card-id', '1');
    });

    it('calls onDragStart when drag begins', () => {
      const dragHandlers = createMockDragHandlers();
      render(
        <Card
          card={mockCard}
          onClick={jest.fn()}
          dragHandlers={dragHandlers}
          isDragging={false}
        />
      );

      fireEvent.dragStart(screen.getByRole('button'));

      expect(dragHandlers.onDragStart).toHaveBeenCalled();
    });

    it('calls onDragEnd when drag ends', () => {
      const dragHandlers = createMockDragHandlers();
      render(
        <Card
          card={mockCard}
          onClick={jest.fn()}
          dragHandlers={dragHandlers}
          isDragging={false}
        />
      );

      fireEvent.dragEnd(screen.getByRole('button'));

      expect(dragHandlers.onDragEnd).toHaveBeenCalled();
    });
  });

  describe('Accessibility', () => {
    it('has role of button', () => {
      render(
        <Card
          card={mockCard}
          onClick={jest.fn()}
          dragHandlers={createMockDragHandlers()}
          isDragging={false}
        />
      );

      expect(screen.getByRole('button')).toBeInTheDocument();
    });

    it('has correct aria-label', () => {
      render(
        <Card
          card={mockCard}
          onClick={jest.fn()}
          dragHandlers={createMockDragHandlers()}
          isDragging={false}
        />
      );

      expect(screen.getByRole('button')).toHaveAttribute('aria-label', 'Edit card: Test Task');
    });

    it('is focusable with tabindex 0', () => {
      render(
        <Card
          card={mockCard}
          onClick={jest.fn()}
          dragHandlers={createMockDragHandlers()}
          isDragging={false}
        />
      );

      expect(screen.getByRole('button')).toHaveAttribute('tabindex', '0');
    });
  });

  describe('Memoization', () => {
    it('does not re-render when props are the same', () => {
      const mockOnClick = jest.fn();
      const dragHandlers = createMockDragHandlers();

      const { rerender } = render(
        <Card
          card={mockCard}
          onClick={mockOnClick}
          dragHandlers={dragHandlers}
          isDragging={false}
        />
      );

      // Re-render with same props
      rerender(
        <Card
          card={mockCard}
          onClick={mockOnClick}
          dragHandlers={dragHandlers}
          isDragging={false}
        />
      );

      // Card should still be rendered correctly
      expect(screen.getByText('Test Task')).toBeInTheDocument();
    });
  });
});

describe('Card - Edge Cases', () => {
  it('handles very long titles gracefully', () => {
    const longTitleCard = {
      ...mockCard,
      title: 'This is a very long title that should be handled gracefully by the card component without breaking the layout',
    };

    render(
      <Card
        card={longTitleCard}
        onClick={jest.fn()}
        dragHandlers={createMockDragHandlers()}
        isDragging={false}
      />
    );

    expect(screen.getByText(longTitleCard.title)).toBeInTheDocument();
  });

  it('handles special characters in title', () => {
    const specialCard = {
      ...mockCard,
      title: '<script>alert("XSS")</script> & "quotes" \'single\'',
    };

    render(
      <Card
        card={specialCard}
        onClick={jest.fn()}
        dragHandlers={createMockDragHandlers()}
        isDragging={false}
      />
    );

    // Should render as text, not execute
    expect(screen.getByText(/alert/)).toBeInTheDocument();
  });

  it('handles null onClick handler', () => {
    render(
      <Card
        card={mockCard}
        onClick={null}
        dragHandlers={createMockDragHandlers()}
        isDragging={false}
      />
    );

    // Should not throw when clicking
    expect(() => {
      fireEvent.click(screen.getByRole('button'));
    }).not.toThrow();
  });
});

