import { render, screen, waitFor, fireEvent } from '@testing-library/react';
import userEvent from '@testing-library/user-event';
import Board from '../src/components/Board';

// Mock the hooks
jest.mock('../src/hooks/useBoard', () => ({
  useBoard: jest.fn(),
}));

jest.mock('../src/hooks/useDragDrop', () => ({
  useDragDrop: jest.fn(),
}));

import { useBoard } from '../src/hooks/useBoard';
import { useDragDrop } from '../src/hooks/useDragDrop';

// Mock data
const mockBoard = {
  id: 1,
  name: 'Test Board',
  columns: [
    {
      id: 1,
      name: 'To Do',
      position: 1024,
      cards: [
        { id: 1, title: 'Task 1', description: 'Description 1', position: 1024, column_id: 1 },
        { id: 2, title: 'Task 2', description: '', position: 2048, column_id: 1 },
      ],
    },
    {
      id: 2,
      name: 'In Progress',
      position: 2048,
      cards: [
        { id: 3, title: 'Task 3', description: 'Working on it', position: 1024, column_id: 2 },
      ],
    },
    {
      id: 3,
      name: 'Done',
      position: 3072,
      cards: [],
    },
  ],
};

const mockActions = {
  createColumn: jest.fn(),
  updateColumn: jest.fn(),
  deleteColumn: jest.fn(),
  createCard: jest.fn(),
  updateCard: jest.fn(),
  deleteCard: jest.fn(),
  moveCard: jest.fn(),
};

const mockDragDrop = {
  isDragging: false,
  draggedItem: null,
  dropTargetId: null,
  createDragHandlers: jest.fn(() => ({
    draggable: true,
    'data-card-id': 1,
    onDragStart: jest.fn(),
    onDragEnd: jest.fn(),
  })),
  createDropHandlers: jest.fn(() => ({
    onDragOver: jest.fn(),
    onDragEnter: jest.fn(),
    onDragLeave: jest.fn(),
    onDrop: jest.fn(),
  })),
};

describe('Board', () => {
  const mockOnBack = jest.fn();
  const mockOnDelete = jest.fn();

  beforeEach(() => {
    jest.clearAllMocks();
    useBoard.mockReturnValue({
      board: mockBoard,
      loading: false,
      error: null,
      actions: mockActions,
      refetch: jest.fn(),
    });
    useDragDrop.mockReturnValue(mockDragDrop);
  });

  describe('Rendering', () => {
    it('renders the board with its name', () => {
      render(<Board boardId={1} onBack={mockOnBack} onDelete={mockOnDelete} />);

      expect(screen.getByRole('heading', { name: 'Test Board' })).toBeInTheDocument();
    });

    it('renders all columns', () => {
      render(<Board boardId={1} onBack={mockOnBack} onDelete={mockOnDelete} />);

      expect(screen.getByRole('region', { name: /Column: To Do/i })).toBeInTheDocument();
      expect(screen.getByRole('region', { name: /Column: In Progress/i })).toBeInTheDocument();
      expect(screen.getByRole('region', { name: /Column: Done/i })).toBeInTheDocument();
    });

    it('renders cards within columns', () => {
      render(<Board boardId={1} onBack={mockOnBack} onDelete={mockOnDelete} />);

      expect(screen.getByText('Task 1')).toBeInTheDocument();
      expect(screen.getByText('Task 2')).toBeInTheDocument();
      expect(screen.getByText('Task 3')).toBeInTheDocument();
    });

    it('shows card count badges for each column', () => {
      render(<Board boardId={1} onBack={mockOnBack} onDelete={mockOnDelete} />);

      // To Do has 2 cards, In Progress has 1, Done has 0
      const badges = screen.getAllByLabelText(/\d+ cards/);
      expect(badges[0]).toHaveTextContent('2');
      expect(badges[1]).toHaveTextContent('1');
      expect(badges[2]).toHaveTextContent('0');
    });

    it('renders Back button', () => {
      render(<Board boardId={1} onBack={mockOnBack} onDelete={mockOnDelete} />);

      expect(screen.getByRole('button', { name: /back/i })).toBeInTheDocument();
    });

    it('renders Delete Board button', () => {
      render(<Board boardId={1} onBack={mockOnBack} onDelete={mockOnDelete} />);

      expect(screen.getByRole('button', { name: /delete board/i })).toBeInTheDocument();
    });

    it('renders Add Column button', () => {
      render(<Board boardId={1} onBack={mockOnBack} onDelete={mockOnDelete} />);

      expect(screen.getByRole('button', { name: /add a column/i })).toBeInTheDocument();
    });
  });

  describe('Loading State', () => {
    it('shows loading spinner when loading', () => {
      useBoard.mockReturnValue({
        board: null,
        loading: true,
        error: null,
        actions: mockActions,
        refetch: jest.fn(),
      });

      render(<Board boardId={1} onBack={mockOnBack} onDelete={mockOnDelete} />);

      expect(screen.getByRole('status', { name: /loading board/i })).toBeInTheDocument();
    });
  });

  describe('Error State', () => {
    it('shows error message when there is an error', () => {
      useBoard.mockReturnValue({
        board: null,
        loading: false,
        error: 'Failed to fetch',
        actions: mockActions,
        refetch: jest.fn(),
      });

      render(<Board boardId={1} onBack={mockOnBack} onDelete={mockOnDelete} />);

      expect(screen.getByRole('alert')).toHaveTextContent('Failed to load board: Failed to fetch');
    });

    it('shows retry button on error', async () => {
      const mockRefetch = jest.fn();
      useBoard.mockReturnValue({
        board: null,
        loading: false,
        error: 'Failed to fetch',
        actions: mockActions,
        refetch: mockRefetch,
      });

      render(<Board boardId={1} onBack={mockOnBack} onDelete={mockOnDelete} />);

      const retryButton = screen.getByText('Try again');
      await userEvent.click(retryButton);

      expect(mockRefetch).toHaveBeenCalled();
    });
  });

  describe('Navigation', () => {
    it('calls onBack when Back button is clicked', async () => {
      render(<Board boardId={1} onBack={mockOnBack} onDelete={mockOnDelete} />);

      await userEvent.click(screen.getByRole('button', { name: /back/i }));

      expect(mockOnBack).toHaveBeenCalled();
    });

    it('calls onDelete when Delete Board button is clicked', async () => {
      render(<Board boardId={1} onBack={mockOnBack} onDelete={mockOnDelete} />);

      await userEvent.click(screen.getByRole('button', { name: /delete board/i }));

      expect(mockOnDelete).toHaveBeenCalled();
    });
  });

  describe('Column Management', () => {
    it('shows column creation form when Add Column is clicked', async () => {
      render(<Board boardId={1} onBack={mockOnBack} onDelete={mockOnDelete} />);

      await userEvent.click(screen.getByRole('button', { name: /add a column/i }));

      expect(screen.getByRole('textbox', { name: /new column name/i })).toBeInTheDocument();
      expect(screen.getByRole('button', { name: /add column/i })).toBeInTheDocument();
      expect(screen.getByRole('button', { name: /cancel/i })).toBeInTheDocument();
    });

    it('creates a new column when form is submitted', async () => {
      render(<Board boardId={1} onBack={mockOnBack} onDelete={mockOnDelete} />);

      await userEvent.click(screen.getByRole('button', { name: /add a column/i }));
      await userEvent.type(screen.getByRole('textbox', { name: /new column name/i }), 'New Column');
      await userEvent.click(screen.getByRole('button', { name: /add column/i }));

      expect(mockActions.createColumn).toHaveBeenCalledWith('New Column');
    });

    it('cancels column creation when Cancel is clicked', async () => {
      render(<Board boardId={1} onBack={mockOnBack} onDelete={mockOnDelete} />);

      await userEvent.click(screen.getByRole('button', { name: /add a column/i }));
      await userEvent.type(screen.getByRole('textbox', { name: /new column name/i }), 'New Column');
      await userEvent.click(screen.getByRole('button', { name: /cancel/i }));

      // Form should be closed, Add Column button should be visible again
      expect(screen.getByRole('button', { name: /add a column/i })).toBeInTheDocument();
      expect(mockActions.createColumn).not.toHaveBeenCalled();
    });

    it('does not create column if name is empty', async () => {
      render(<Board boardId={1} onBack={mockOnBack} onDelete={mockOnDelete} />);

      await userEvent.click(screen.getByRole('button', { name: /add a column/i }));

      // Submit button should be disabled
      expect(screen.getByRole('button', { name: /add column/i })).toBeDisabled();
    });
  });

  describe('Card Modal', () => {
    it('opens card modal when a card is clicked', async () => {
      render(<Board boardId={1} onBack={mockOnBack} onDelete={mockOnDelete} />);

      await userEvent.click(screen.getByRole('button', { name: /edit card: task 1/i }));

      expect(screen.getByRole('dialog')).toBeInTheDocument();
      expect(screen.getByRole('textbox', { name: /title/i })).toHaveValue('Task 1');
    });

    it('closes modal when close button is clicked', async () => {
      render(<Board boardId={1} onBack={mockOnBack} onDelete={mockOnDelete} />);

      await userEvent.click(screen.getByRole('button', { name: /edit card: task 1/i }));
      await userEvent.click(screen.getByRole('button', { name: /close modal/i }));

      await waitFor(() => {
        expect(screen.queryByRole('dialog')).not.toBeInTheDocument();
      });
    });

    it('updates card when changes are saved', async () => {
      render(<Board boardId={1} onBack={mockOnBack} onDelete={mockOnDelete} />);

      await userEvent.click(screen.getByRole('button', { name: /edit card: task 1/i }));

      const titleInput = screen.getByRole('textbox', { name: /title/i });
      await userEvent.clear(titleInput);
      await userEvent.type(titleInput, 'Updated Task');

      await userEvent.click(screen.getByRole('button', { name: /save/i }));

      expect(mockActions.updateCard).toHaveBeenCalledWith(1, {
        title: 'Updated Task',
        description: 'Description 1',
      });
    });

    it('deletes card when delete is confirmed', async () => {
      render(<Board boardId={1} onBack={mockOnBack} onDelete={mockOnDelete} />);

      await userEvent.click(screen.getByRole('button', { name: /edit card: task 1/i }));

      // Click delete once to show confirmation
      await userEvent.click(screen.getByRole('button', { name: /delete/i }));

      // Click confirm delete
      await userEvent.click(screen.getByRole('button', { name: /confirm delete/i }));

      expect(mockActions.deleteCard).toHaveBeenCalledWith(1);
    });
  });

  describe('Accessibility', () => {
    it('has accessible board region', () => {
      render(<Board boardId={1} onBack={mockOnBack} onDelete={mockOnDelete} />);

      expect(screen.getByRole('main', { name: /board: test board/i })).toBeInTheDocument();
    });

    it('has accessible column sections', () => {
      render(<Board boardId={1} onBack={mockOnBack} onDelete={mockOnDelete} />);

      const columns = screen.getAllByRole('region');
      expect(columns.length).toBeGreaterThanOrEqual(3);
    });

    it('cards are keyboard accessible', async () => {
      render(<Board boardId={1} onBack={mockOnBack} onDelete={mockOnDelete} />);

      const card = screen.getByRole('button', { name: /edit card: task 1/i });
      card.focus();

