import { render, screen, fireEvent } from '@testing-library/react';
import userEvent from '@testing-library/user-event';
import AddCardForm from '../src/components/AddCardForm';

describe('AddCardForm', () => {
  describe('Initial State', () => {
    it('renders as a button initially', () => {
      render(<AddCardForm onSubmit={jest.fn()} />);

      expect(screen.getByRole('button', { name: /add a card/i })).toBeInTheDocument();
      expect(screen.queryByRole('textbox')).not.toBeInTheDocument();
    });

    it('has correct button text and icon', () => {
      render(<AddCardForm onSubmit={jest.fn()} />);

      const button = screen.getByRole('button', { name: /add a card/i });
      expect(button).toHaveTextContent('Add a card');
    });
  });

  describe('Opening the Form', () => {
    it('shows form when button is clicked', async () => {
      render(<AddCardForm onSubmit={jest.fn()} />);

      await userEvent.click(screen.getByRole('button', { name: /add a card/i }));

      expect(screen.getByRole('textbox', { name: /card title/i })).toBeInTheDocument();
      expect(screen.getByRole('button', { name: /add card/i })).toBeInTheDocument();
      expect(screen.getByRole('button', { name: /cancel/i })).toBeInTheDocument();
    });

    it('focuses the input when form opens', async () => {
      render(<AddCardForm onSubmit={jest.fn()} />);

      await userEvent.click(screen.getByRole('button', { name: /add a card/i }));

      expect(screen.getByRole('textbox', { name: /card title/i })).toHaveFocus();
    });

    it('shows placeholder text in input', async () => {
      render(<AddCardForm onSubmit={jest.fn()} />);

      await userEvent.click(screen.getByRole('button', { name: /add a card/i }));

      expect(screen.getByPlaceholderText(/enter a title for this card/i)).toBeInTheDocument();
    });
  });

  describe('Submitting the Form', () => {
    it('calls onSubmit with title when form is submitted', async () => {
      const mockOnSubmit = jest.fn();
      render(<AddCardForm onSubmit={mockOnSubmit} />);

      await userEvent.click(screen.getByRole('button', { name: /add a card/i }));
      await userEvent.type(screen.getByRole('textbox'), 'New Card Title');
      await userEvent.click(screen.getByRole('button', { name: /add card/i }));

      expect(mockOnSubmit).toHaveBeenCalledWith('New Card Title');
    });

    it('trims whitespace from title', async () => {
      const mockOnSubmit = jest.fn();
      render(<AddCardForm onSubmit={mockOnSubmit} />);

      await userEvent.click(screen.getByRole('button', { name: /add a card/i }));
      await userEvent.type(screen.getByRole('textbox'), '  Card with spaces  ');
      await userEvent.click(screen.getByRole('button', { name: /add card/i }));

      expect(mockOnSubmit).toHaveBeenCalledWith('Card with spaces');
    });

    it('clears input after successful submission', async () => {
      const mockOnSubmit = jest.fn();
      render(<AddCardForm onSubmit={mockOnSubmit} />);

      await userEvent.click(screen.getByRole('button', { name: /add a card/i }));
      await userEvent.type(screen.getByRole('textbox'), 'New Card');
      await userEvent.click(screen.getByRole('button', { name: /add card/i }));

      expect(screen.getByRole('textbox')).toHaveValue('');
    });

    it('keeps form open after submission for quick multiple card creation', async () => {
      const mockOnSubmit = jest.fn();
      render(<AddCardForm onSubmit={mockOnSubmit} />);

      await userEvent.click(screen.getByRole('button', { name: /add a card/i }));
      await userEvent.type(screen.getByRole('textbox'), 'Card 1');
      await userEvent.click(screen.getByRole('button', { name: /add card/i }));

      // Form should still be visible
      expect(screen.getByRole('textbox')).toBeInTheDocument();
    });

    it('disables Add Card button when title is empty', async () => {
      render(<AddCardForm onSubmit={jest.fn()} />);

      await userEvent.click(screen.getByRole('button', { name: /add a card/i }));

      expect(screen.getByRole('button', { name: /add card/i })).toBeDisabled();
    });

    it('disables Add Card button when title is only whitespace', async () => {
      render(<AddCardForm onSubmit={jest.fn()} />);

      await userEvent.click(screen.getByRole('button', { name: /add a card/i }));
      await userEvent.type(screen.getByRole('textbox'), '   ');

      expect(screen.getByRole('button', { name: /add card/i })).toBeDisabled();
    });
  });

  describe('Canceling the Form', () => {
    it('closes form when Cancel is clicked', async () => {
      const mockOnCancel = jest.fn();
      render(<AddCardForm onSubmit={jest.fn()} onCancel={mockOnCancel} />);

      await userEvent.click(screen.getByRole('button', { name: /add a card/i }));
      await userEvent.click(screen.getByRole('button', { name: /cancel/i }));

      expect(screen.getByRole('button', { name: /add a card/i })).toBeInTheDocument();
      expect(screen.queryByRole('textbox')).not.toBeInTheDocument();
    });

    it('calls onCancel when Cancel is clicked', async () => {
      const mockOnCancel = jest.fn();
      render(<AddCardForm onSubmit={jest.fn()} onCancel={mockOnCancel} />);

      await userEvent.click(screen.getByRole('button', { name: /add a card/i }));
      await userEvent.click(screen.getByRole('button', { name: /cancel/i }));

      expect(mockOnCancel).toHaveBeenCalled();
    });

    it('closes form when Escape key is pressed', async () => {
      render(<AddCardForm onSubmit={jest.fn()} />);

      await userEvent.click(screen.getByRole('button', { name: /add a card/i }));
      fireEvent.keyDown(screen.getByRole('textbox'), { key: 'Escape' });

      expect(screen.getByRole('button', { name: /add a card/i })).toBeInTheDocument();
      expect(screen.queryByRole('textbox')).not.toBeInTheDocument();
    });

    it('clears input when form is canceled', async () => {
      render(<AddCardForm onSubmit={jest.fn()} />);

      await userEvent.click(screen.getByRole('button', { name: /add a card/i }));
      await userEvent.type(screen.getByRole('textbox'), 'Some text');
      await userEvent.click(screen.getByRole('button', { name: /cancel/i }));

      // Reopen form
      await userEvent.click(screen.getByRole('button', { name: /add a card/i }));

      expect(screen.getByRole('textbox')).toHaveValue('');
    });
  });

  describe('Accessibility', () => {
    it('button has accessible label', () => {
      render(<AddCardForm onSubmit={jest.fn()} />);

      expect(screen.getByRole('button', { name: /add a card/i })).toHaveAttribute('aria-label', 'Add a card');
    });

    it('textarea has accessible label', async () => {
      render(<AddCardForm onSubmit={jest.fn()} />);

      await userEvent.click(screen.getByRole('button', { name: /add a card/i }));

      expect(screen.getByRole('textbox')).toHaveAttribute('aria-label', 'Card title');
    });
  });
});

