import { renderHook, act, waitFor } from '@testing-library/react';
import { useApi } from '../src/hooks/useApi';

describe('useApi', () => {
  beforeEach(() => {
    jest.clearAllMocks();
    global.fetch = jest.fn();
  });

  describe('GET requests', () => {
    it('makes a GET request successfully', async () => {
      const mockData = [{ id: 1, name: 'Test' }];
      global.fetch.mockResolvedValueOnce({
        ok: true,
        status: 200,
        json: () => Promise.resolve(mockData),
      });

      const { result } = renderHook(() => useApi());

      let data;
      await act(async () => {
        data = await result.current.get('/boards');
      });

      expect(data).toEqual(mockData);
      expect(global.fetch).toHaveBeenCalledWith('/api/boards', {
        method: 'GET',
        headers: { 'Content-Type': 'application/json' },
      });
    });

    it('handles GET request errors', async () => {
      global.fetch.mockResolvedValueOnce({
        ok: false,
        status: 404,
        json: () => Promise.resolve({ error: { message: 'Not found' } }),
      });

      const { result } = renderHook(() => useApi());

      await act(async () => {
        await expect(result.current.get('/boards/999')).rejects.toThrow('Not found');
      });

      expect(result.current.error).toBeTruthy();
    });
  });

  describe('POST requests', () => {
    it('makes a POST request with body', async () => {
      const requestBody = { name: 'New Board' };
      const responseData = { id: 1, name: 'New Board' };

      global.fetch.mockResolvedValueOnce({
        ok: true,
        status: 201,
        json: () => Promise.resolve(responseData),
      });

      const { result } = renderHook(() => useApi());

      let data;
      await act(async () => {
        data = await result.current.post('/boards', requestBody);
      });

      expect(data).toEqual(responseData);
      expect(global.fetch).toHaveBeenCalledWith('/api/boards', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(requestBody),
      });
    });
  });

  describe('PUT requests', () => {
    it('makes a PUT request with body', async () => {
      const requestBody = { name: 'Updated Board' };
      const responseData = { id: 1, name: 'Updated Board' };

      global.fetch.mockResolvedValueOnce({
        ok: true,
        status: 200,
        json: () => Promise.resolve(responseData),
      });

      const { result } = renderHook(() => useApi());

      let data;
      await act(async () => {
        data = await result.current.put('/boards/1', requestBody);
      });

      expect(data).toEqual(responseData);
      expect(global.fetch).toHaveBeenCalledWith('/api/boards/1', {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(requestBody),
      });
    });
  });

  describe('PATCH requests', () => {
    it('makes a PATCH request with body', async () => {
      const requestBody = { columnId: 2, position: 1024 };
      const responseData = { id: 1, column_id: 2, position: 1024 };

      global.fetch.mockResolvedValueOnce({
        ok: true,
        status: 200,
        json: () => Promise.resolve(responseData),
      });

      const { result } = renderHook(() => useApi());

      let data;
      await act(async () => {
        data = await result.current.patch('/cards/1/move', requestBody);
      });

      expect(data).toEqual(responseData);
      expect(global.fetch).toHaveBeenCalledWith('/api/cards/1/move', {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(requestBody),
      });
    });
  });

  describe('DELETE requests', () => {
    it('makes a DELETE request', async () => {
      global.fetch.mockResolvedValueOnce({
        ok: true,
        status: 204,
      });

      const { result } = renderHook(() => useApi());

      let data;
      await act(async () => {
        data = await result.current.del('/boards/1');
      });

      expect(data).toBeNull();
      expect(global.fetch).toHaveBeenCalledWith('/api/boards/1', {
        method: 'DELETE',
        headers: { 'Content-Type': 'application/json' },
      });
    });
  });

  describe('Loading state', () => {
    it('sets loading to true during request', async () => {
      let resolvePromise;
      global.fetch.mockReturnValueOnce(
        new Promise((resolve) => {
          resolvePromise = resolve;
        })
      );

      const { result } = renderHook(() => useApi());

      act(() => {
        result.current.get('/boards');
      });

      expect(result.current.loading).toBe(true);

      await act(async () => {
        resolvePromise({
          ok: true,
          status: 200,
          json: () => Promise.resolve([]),
        });
      });

      await waitFor(() => {
        expect(result.current.loading).toBe(false);
      });
    });
  });

  describe('Error handling', () => {
    it('sets error on request failure', async () => {
      global.fetch.mockResolvedValueOnce({
        ok: false,
        status: 500,
        json: () => Promise.resolve({ error: { message: 'Server error' } }),
      });

      const { result } = renderHook(() => useApi());

      await act(async () => {
        try {
          await result.current.get('/boards');
        } catch (e) {
          // Expected error
        }
      });

      expect(result.current.error).toBeTruthy();
      expect(result.current.error.message).toBe('Server error');
    });

    it('uses default error message when none provided', async () => {
      global.fetch.mockResolvedValueOnce({
        ok: false,
        status: 500,
        json: () => Promise.resolve({}),
      });

      const { result } = renderHook(() => useApi());

      await act(async () => {
        try {
          await result.current.get('/boards');
        } catch (e) {
          expect(e.message).toBe('Request failed with status 500');
        }
      });
    });

    it('clears error with clearError', async () => {
      global.fetch.mockResolvedValueOnce({
        ok: false,
        status: 500,
        json: () => Promise.resolve({ error: { message: 'Error' } }),
      });

      const { result } = renderHook(() => useApi());

      await act(async () => {
        try {
          await result.current.get('/boards');
        } catch (e) {
          // Expected
        }
      });

      expect(result.current.error).toBeTruthy();

      act(() => {
        result.current.clearError();
      });

      expect(result.current.error).toBeNull();
    });
  });
});

import { renderHook, act } from '@testing-library/react';
import { useDragDrop } from '../src/hooks/useDragDrop';

describe('useDragDrop', () => {
  const mockOnDrop = jest.fn();

  beforeEach(() => {
    jest.clearAllMocks();
  });

  describe('Initial state', () => {
    it('starts with no dragging', () => {
      const { result } = renderHook(() => useDragDrop({ onDrop: mockOnDrop }));

      expect(result.current.isDragging).toBe(false);
      expect(result.current.draggedItem).toBeNull();
      expect(result.current.dropTargetId).toBeNull();
    });
  });

  describe('createDragHandlers', () => {
    const mockCard = { id: 1, title: 'Test', column_id: 1 };

    it('returns handler object with draggable property', () => {
      const { result } = renderHook(() => useDragDrop({ onDrop: mockOnDrop }));

      const handlers = result.current.createDragHandlers(mockCard);

      expect(handlers.draggable).toBe(true);
      expect(handlers['data-card-id']).toBe(1);
    });

    it('sets isDragging to true on dragStart', () => {
      const { result } = renderHook(() => useDragDrop({ onDrop: mockOnDrop }));

      const handlers = result.current.createDragHandlers(mockCard);

      const mockEvent = {
        dataTransfer: {
          setData: jest.fn(),
          setDragImage: jest.fn(),
          effectAllowed: '',
        },
        currentTarget: document.createElement('div'),
      };

      act(() => {
        handlers.onDragStart(mockEvent);
      });

      expect(result.current.isDragging).toBe(true);
      expect(result.current.draggedItem).toEqual(mockCard);
    });

    it('sets drag data on dragStart', () => {
      const { result } = renderHook(() => useDragDrop({ onDrop: mockOnDrop }));

      const handlers = result.current.createDragHandlers(mockCard);

      const mockSetData = jest.fn();
      const mockEvent = {
        dataTransfer: {
          setData: mockSetData,
          setDragImage: jest.fn(),
          effectAllowed: '',
        },
        currentTarget: document.createElement('div'),
      };

      act(() => {
        handlers.onDragStart(mockEvent);
      });

      expect(mockSetData).toHaveBeenCalledWith(
        'text/plain',
        JSON.stringify({ cardId: 1, columnId: 1 })
      );
    });

    it('resets state on dragEnd', () => {
      const { result } = renderHook(() => useDragDrop({ onDrop: mockOnDrop }));

      const handlers = result.current.createDragHandlers(mockCard);

      // First start dragging
      const mockStartEvent = {
        dataTransfer: {
          setData: jest.fn(),
          setDragImage: jest.fn(),
          effectAllowed: '',
        },
        currentTarget: document.createElement('div'),
      };

      act(() => {
        handlers.onDragStart(mockStartEvent);
      });

      expect(result.current.isDragging).toBe(true);

      // Then end dragging
      act(() => {
        handlers.onDragEnd();
      });

      expect(result.current.isDragging).toBe(false);
      expect(result.current.draggedItem).toBeNull();
      expect(result.current.dropTargetId).toBeNull();
    });
  });

  describe('createDropHandlers', () => {
    const columnId = 1;
    const cards = [
      { id: 1, title: 'Card 1', position: 1024 },
      { id: 2, title: 'Card 2', position: 2048 },
    ];

    it('returns handler object with drag event handlers', () => {
      const { result } = renderHook(() => useDragDrop({ onDrop: mockOnDrop }));

      const handlers = result.current.createDropHandlers(columnId, cards);

      expect(handlers.onDragOver).toBeDefined();
      expect(handlers.onDragEnter).toBeDefined();
      expect(handlers.onDragLeave).toBeDefined();
      expect(handlers.onDrop).toBeDefined();
    });

    it('sets dropTargetId on dragOver', () => {
      const { result } = renderHook(() => useDragDrop({ onDrop: mockOnDrop }));

      const handlers = result.current.createDropHandlers(columnId, cards);

      const mockEvent = {
        preventDefault: jest.fn(),
        dataTransfer: { dropEffect: '' },
      };

      act(() => {
        handlers.onDragOver(mockEvent);
      });

      expect(result.current.dropTargetId).toBe(columnId);
      expect(mockEvent.preventDefault).toHaveBeenCalled();
    });

    it('sets dropTargetId on dragEnter', () => {
      const { result } = renderHook(() => useDragDrop({ onDrop: mockOnDrop }));

      const handlers = result.current.createDropHandlers(columnId, cards);

      const mockEvent = { preventDefault: jest.fn() };

      act(() => {
        handlers.onDragEnter(mockEvent);
      });

      expect(result.current.dropTargetId).toBe(columnId);
    });

    it('clears dropTargetId on dragLeave when leaving column', () => {
      const { result } = renderHook(() => useDragDrop({ onDrop: mockOnDrop }));

      const handlers = result.current.createDropHandlers(columnId, cards);

      // First set the drop target
      act(() => {
        handlers.onDragEnter({ preventDefault: jest.fn() });
      });

      expect(result.current.dropTargetId).toBe(columnId);

      // Create a mock element that is NOT contained in currentTarget
      const mockEvent = {
        relatedTarget: document.createElement('div'),
        currentTarget: {
          contains: () => false,
        },
      };

      act(() => {
        handlers.onDragLeave(mockEvent);
      });

      expect(result.current.dropTargetId).toBeNull();
    });

    it('does not clear dropTargetId when moving within column', () => {
      const { result } = renderHook(() => useDragDrop({ onDrop: mockOnDrop }));

      const handlers = result.current.createDropHandlers(columnId, cards);

      // First set the drop target
      act(() => {
        handlers.onDragEnter({ preventDefault: jest.fn() });
      });

      // Mock staying within the column
      const mockEvent = {
        relatedTarget: document.createElement('div'),
        currentTarget: {
          contains: () => true,
        },
      };

      act(() => {
        handlers.onDragLeave(mockEvent);
      });

      expect(result.current.dropTargetId).toBe(columnId);
    });

    it('calls onDrop with correct arguments on drop', () => {
      const { result } = renderHook(() => useDragDrop({ onDrop: mockOnDrop }));

      const handlers = result.current.createDropHandlers(columnId, cards);

      const mockElement = document.createElement('div');
      const mockEvent = {
        preventDefault: jest.fn(),
        dataTransfer: {
          getData: () => JSON.stringify({ cardId: 3, columnId: 2 }),
        },
        clientY: 100,
        currentTarget: mockElement,
      };

      act(() => {
        handlers.onDrop(mockEvent);
      });

      expect(mockEvent.preventDefault).toHaveBeenCalled();
      expect(mockOnDrop).toHaveBeenCalledWith(3, columnId, expect.any(Number));
      expect(result.current.dropTargetId).toBeNull();
    });

    it('handles invalid drag data gracefully', () => {
      const { result } = renderHook(() => useDragDrop({ onDrop: mockOnDrop }));

      const handlers = result.current.createDropHandlers(columnId, cards);

      const mockEvent = {
        preventDefault: jest.fn(),
        dataTransfer: {
          getData: () => 'invalid json',
        },
        clientY: 100,
        currentTarget: document.createElement('div'),
      };

      // Should not throw
      act(() => {
        handlers.onDrop(mockEvent);
      });

      expect(mockOnDrop).not.toHaveBeenCalled();
    });
  });

  describe('Position calculation', () => {
    it('returns 1024 for empty column', () => {
      const { result } = renderHook(() => useDragDrop({ onDrop: mockOnDrop }));

      const handlers = result.current.createDropHandlers(1, []);

      const mockEvent = {
        preventDefault: jest.fn(),
        dataTransfer: {
          getData: () => JSON.stringify({ cardId: 1, columnId: 2 }),
        },
        clientY: 100,
        currentTarget: document.createElement('div'),
      };

      act(() => {
        handlers.onDrop(mockEvent);
      });

      expect(mockOnDrop).toHaveBeenCalledWith(1, 1, 1024);
    });
  });
});

import { render, screen, waitFor } from '@testing-library/react';
import userEvent from '@testing-library/user-event';
import App from '../src/App';

// Mock the Board component
jest.mock('../src/components/Board', () => {
  return function MockBoard({ boardId, onBack, onDelete }) {
    return (
      <div data-testid="mock-board">
        <span>Board {boardId}</span>
        <button onClick={onBack}>Back</button>
        <button onClick={onDelete}>Delete</button>
      </div>
    );
  };
});

describe('App', () => {
  beforeEach(() => {
    jest.clearAllMocks();
    global.fetch = jest.fn();
  });

  describe('Initial Render', () => {
    it('renders app header with title', async () => {
      global.fetch.mockResolvedValueOnce({
        ok: true,
        status: 200,
        json: () => Promise.resolve([]),
      });

      render(<App />);

      expect(screen.getByRole('heading', { name: /kanban board/i })).toBeInTheDocument();
    });

    it('shows loading state initially', () => {
      global.fetch.mockReturnValue(new Promise(() => {})); // Never resolves

      render(<App />);

      expect(screen.getByRole('status', { name: /loading boards/i })).toBeInTheDocument();
    });

    it('fetches boards on mount', async () => {
      global.fetch.mockResolvedValueOnce({
        ok: true,
        status: 200,
        json: () => Promise.resolve([]),
      });

      render(<App />);

      await waitFor(() => {
        expect(global.fetch).toHaveBeenCalledWith('/api/boards', expect.any(Object));
      });
    });
  });

  describe('Empty State', () => {
    it('shows empty state when no boards exist', async () => {
      global.fetch.mockResolvedValueOnce({
        ok: true,
        status: 200,
        json: () => Promise.resolve([]),
      });

      render(<App />);

      await waitFor(() => {
        expect(screen.getByText(/no boards yet/i)).toBeInTheDocument();
      });
    });

    it('shows create board button in empty state', async () => {
      global.fetch.mockResolvedValueOnce({
        ok: true,
        status: 200,
        json: () => Promise.resolve([]),
      });

      render(<App />);

      await waitFor(() => {
        expect(screen.getByRole('button', { name: /create board/i })).toBeInTheDocument();
      });
    });
  });

  describe('Board List', () => {
    const mockBoards = [
      { id: 1, name: 'Project Alpha', created_at: '2024-01-15T10:00:00Z' },
      { id: 2, name: 'Project Beta', created_at: '2024-01-16T10:00:00Z' },
    ];

    it('displays list of boards', async () => {
      global.fetch.mockResolvedValueOnce({
        ok: true,
        status: 200,
        json: () => Promise.resolve(mockBoards),
      });

      render(<App />);

      await waitFor(() => {
        expect(screen.getByText('Project Alpha')).toBeInTheDocument();
        expect(screen.getByText('Project Beta')).toBeInTheDocument();
      });
    });

    it('shows board creation date', async () => {
      global.fetch.mockResolvedValueOnce({
        ok: true,
        status: 200,
        json: () => Promise.resolve(mockBoards),
      });

      render(<App />);

      await waitFor(() => {
        expect(screen.getByText(/created 1\/15\/2024/i)).toBeInTheDocument();
      });
    });

    it('opens board when clicked', async () => {
      global.fetch.mockResolvedValueOnce({
        ok: true,
        status: 200,
        json: () => Promise.resolve(mockBoards),
      });

      render(<App />);

      await waitFor(() => {
        expect(screen.getByText('Project Alpha')).toBeInTheDocument();
      });

      await userEvent.click(screen.getByRole('button', { name: /open project alpha board/i }));

      expect(screen.getByTestId('mock-board')).toBeInTheDocument();
    });

    it('opens board on keyboard Enter', async () => {
      global.fetch.mockResolvedValueOnce({
        ok: true,
        status: 200,
        json: () => Promise.resolve(mockBoards),
      });

      render(<App />);

      await waitFor(() => {
        expect(screen.getByText('Project Alpha')).toBeInTheDocument();
      });

      const boardItem = screen.getByRole('button', { name: /open project alpha board/i });
      boardItem.focus();
      await userEvent.keyboard('{Enter}');

      expect(screen.getByTestId('mock-board')).toBeInTheDocument();
    });
  });

  describe('Creating Boards', () => {
    it('shows create form when New Board button is clicked', async () => {
      global.fetch.mockResolvedValueOnce({
        ok: true,
        status: 200,
        json: () => Promise.resolve([{ id: 1, name: 'Existing Board', created_at: '2024-01-15' }]),
      });

      render(<App />);

      await waitFor(() => {
        expect(screen.getByText('Existing Board')).toBeInTheDocument();
      });

      await userEvent.click(screen.getByRole('button', { name: /new board/i }));

      expect(screen.getByRole('textbox', { name: /new board name/i })).toBeInTheDocument();
    });

    it('creates board and navigates to it', async () => {
      global.fetch
        .mockResolvedValueOnce({
          ok: true,
          status: 200,
          json: () => Promise.resolve([]),
        })
        .mockResolvedValueOnce({
          ok: true,
          status: 201,
          json: () => Promise.resolve({ id: 1, name: 'New Board', created_at: '2024-01-17' }),
        });

      render(<App />);

      await waitFor(() => {
        expect(screen.getByText(/no boards yet/i)).toBeInTheDocument();
      });

      await userEvent.click(screen.getByRole('button', { name: /create board/i }));
      await userEvent.type(screen.getByRole('textbox'), 'New Board');
      await userEvent.click(screen.getByRole('button', { name: /^create$/i }));

      await waitFor(() => {
        expect(screen.getByTestId('mock-board')).toBeInTheDocument();
      });
    });

    it('cancels board creation when Cancel is clicked', async () => {
      global.fetch.mockResolvedValueOnce({
        ok: true,
        status: 200,
        json: () => Promise.resolve([{ id: 1, name: 'Existing', created_at: '2024-01-15' }]),
      });

      render(<App />);

      await waitFor(() => {
        expect(screen.getByText('Existing')).toBeInTheDocument();
      });

      await userEvent.click(screen.getByRole('button', { name: /new board/i }));
      await userEvent.type(screen.getByRole('textbox'), 'Draft Board');
      await userEvent.click(screen.getByRole('button', { name: /cancel/i }));

      expect(screen.queryByRole('textbox')).not.toBeInTheDocument();
      expect(screen.getByRole('button', { name: /new board/i })).toBeInTheDocument();
    });

    it('disables Create button when name is empty', async () => {
      global.fetch.mockResolvedValueOnce({
        ok: true,
        status: 200,
        json: () => Promise.resolve([]),
      });

      render(<App />);

      await waitFor(() => {
        expect(screen.getByText(/no boards yet/i)).toBeInTheDocument();
      });

      await userEvent.click(screen.getByRole('button', { name: /create board/i }));

      expect(screen.getByRole('button', { name: /^create$/i })).toBeDisabled();
    });
  });

  describe('Error Handling', () => {
    it('shows error message on fetch failure', async () => {
      global.fetch.mockResolvedValueOnce({
        ok: false,
        status: 500,
        json: () => Promise.resolve({ error: { message: 'Server error' } }),
      });

      render(<App />);

      await waitFor(() => {
        expect(screen.getByRole('alert')).toHaveTextContent('Failed to load boards');
      });
    });

    it('allows retry after error', async () => {
      global.fetch
        .mockResolvedValueOnce({
          ok: false,
          status: 500,
          json: () => Promise.resolve({ error: { message: 'Server error' } }),
        })
        .mockResolvedValueOnce({
          ok: true,
          status: 200,
          json: () => Promise.resolve([{ id: 1, name: 'Board', created_at: '2024-01-15' }]),
        });

      render(<App />);

      await waitFor(() => {
        expect(screen.getByRole('alert')).toBeInTheDocument();
      });

      await userEvent.click(screen.getByText(/try again/i));

      await waitFor(() => {
        expect(screen.getByText('Board')).toBeInTheDocument();
      });
    });
  });

  describe('Board Navigation', () => {
    it('returns to board list when Back is clicked', async () => {
      global.fetch.mockResolvedValueOnce({
        ok: true,
        status: 200,
        json: () => Promise.resolve([{ id: 1, name: 'My Board', created_at: '2024-01-15' }]),
      });

      render(<App />);

      await waitFor(() => {
        expect(screen.getByText('My Board')).toBeInTheDocument();
      });

      await userEvent.click(screen.getByRole('button', { name: /open my board/i }));

      expect(screen.getByTestId('mock-board')).toBeInTheDocument();

      await userEvent.click(screen.getByText('Back'));

      expect(screen.queryByTestId('mock-board')).not.toBeInTheDocument();
      expect(screen.getByText('My Board')).toBeInTheDocument();
    });

    it('deletes board and returns to list', async () => {
      window.confirm = jest.fn(() => true);

      global.fetch
        .mockResolvedValueOnce({
          ok: true,
          status: 200,
          json: () => Promise.resolve([{ id: 1, name: 'Delete Me', created_at: '2024-01-15' }]),
        })
        .mockResolvedValueOnce({
          ok: true,
          status: 204,
        });

      render(<App />);

      await waitFor(() => {
        expect(screen.getByText('Delete Me')).toBeInTheDocument();
      });

      await userEvent.click(screen.getByRole('button', { name: /open delete me/i }));

      expect(screen.getByTestId('mock-board')).toBeInTheDocument();

      await userEvent.click(screen.getByText('Delete'));

      await waitFor(() => {
        expect(screen.queryByTestId('mock-board')).not.toBeInTheDocument();
        expect(screen.queryByText('Delete Me')).not.toBeInTheDocument();
      });
    });
  });
});

