import { useState, useCallback } from 'react';

/**
 * Base URL for API requests.
 * Uses environment variable in production, falls back to /api for dev proxy.
 */
const API_BASE = import.meta.env.VITE_API_URL || '/api';

/**
 * Custom hook for making API requests with consistent error handling
 * and loading state management.
 *
 * @returns {Object} API methods (get, post, put, patch, del) and loading state
 *
 * @example
 * const { get, post, loading, error } = useApi();
 * const boards = await get('/boards');
 * await post('/boards', { name: 'My Board' });
 */
export function useApi() {
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  /**
   * Makes a fetch request with consistent error handling
   * @param {string} endpoint - API endpoint (e.g., '/boards')
   * @param {Object} options - Fetch options
   * @returns {Promise<any>} Response data
   */
  const request = useCallback(async (endpoint, options = {}) => {
    setLoading(true);
    setError(null);

    try {
      const url = `${API_BASE}${endpoint}`;
      const response = await fetch(url, {
        headers: {
          'Content-Type': 'application/json',
          ...options.headers,
        },
        ...options,
      });

      // Handle no-content responses (e.g., DELETE)
      if (response.status === 204) {
        setLoading(false);
        return null;
      }

      const data = await response.json();

      if (!response.ok) {
        const errorMessage = data?.error?.message || `Request failed with status ${response.status}`;
        throw new Error(errorMessage);
      }

      setLoading(false);
      return data;
    } catch (err) {
      setError(err);
      setLoading(false);
      throw err;
    }
  }, []);

  const get = useCallback(
    (endpoint) => request(endpoint, { method: 'GET' }),
    [request]
  );

  const post = useCallback(
    (endpoint, body) =>
      request(endpoint, {
        method: 'POST',
        body: JSON.stringify(body),
      }),
    [request]
  );

  const put = useCallback(
    (endpoint, body) =>
      request(endpoint, {
        method: 'PUT',
        body: JSON.stringify(body),
      }),
    [request]
  );

  const patch = useCallback(
    (endpoint, body) =>
      request(endpoint, {
        method: 'PATCH',
        body: JSON.stringify(body),
      }),
    [request]
  );

  const del = useCallback(
    (endpoint) => request(endpoint, { method: 'DELETE' }),
    [request]
  );

  return {
    get,
    post,
    put,
    patch,
    del,
    loading,
    error,
    clearError: () => setError(null),
  };
}

