import { render, screen, fireEvent } from '@testing-library/react';
import userEvent from '@testing-library/user-event';
import Column from '../src/components/Column';

const mockColumn = {
  id: 1,
  name: 'To Do',
  position: 1024,
  cards: [
    { id: 1, title: 'Task 1', description: 'Description 1', position: 1024, column_id: 1 },
    { id: 2, title: 'Task 2', description: '', position: 2048, column_id: 1 },
  ],
};

const mockEmptyColumn = {
  id: 2,
  name: 'Empty Column',
  position: 2048,
  cards: [],
};

const createMockDragHandlers = () => ({
  draggable: true,
  'data-card-id': 1,
  onDragStart: jest.fn(),
  onDragEnd: jest.fn(),
});

const createMockDropHandlers = () => ({
  onDragOver: jest.fn(),
  onDragEnter: jest.fn(),
  onDragLeave: jest.fn(),
  onDrop: jest.fn(),
});

describe('Column', () => {
  const defaultProps = {
    column: mockColumn,
    onUpdateColumn: jest.fn(),
    onDeleteColumn: jest.fn(),
    onCreateCard: jest.fn(),
    onCardClick: jest.fn(),
    createDragHandlers: jest.fn(() => createMockDragHandlers()),
    createDropHandlers: jest.fn(() => createMockDropHandlers()),
    draggedItem: null,
    dropTargetId: null,
  };

  beforeEach(() => {
    jest.clearAllMocks();
    window.confirm = jest.fn(() => true);
  });

  describe('Rendering', () => {
    it('renders column name', () => {
      render(<Column {...defaultProps} />);

      expect(screen.getByText('TO DO')).toBeInTheDocument();
    });

    it('renders card count badge', () => {
      render(<Column {...defaultProps} />);

      expect(screen.getByLabelText('2 cards')).toHaveTextContent('2');
    });

    it('renders all cards in the column', () => {
      render(<Column {...defaultProps} />);

      expect(screen.getByText('Task 1')).toBeInTheDocument();
      expect(screen.getByText('Task 2')).toBeInTheDocument();
    });

    it('renders cards sorted by position', () => {
      const unsortedColumn = {
        ...mockColumn,
        cards: [
          { id: 2, title: 'Second', position: 2048, column_id: 1 },
          { id: 1, title: 'First', position: 1024, column_id: 1 },
        ],
      };

      render(<Column {...defaultProps} column={unsortedColumn} />);

      const cards = screen.getAllByRole('button', { name: /edit card/i });
      expect(cards[0]).toHaveAttribute('aria-label', 'Edit card: First');
      expect(cards[1]).toHaveAttribute('aria-label', 'Edit card: Second');
    });

    it('renders Add Card button', () => {
      render(<Column {...defaultProps} />);

      expect(screen.getByRole('button', { name: /add a card/i })).toBeInTheDocument();
    });

    it('renders delete column button', () => {
      render(<Column {...defaultProps} />);

      expect(screen.getByRole('button', { name: /delete column to do/i })).toBeInTheDocument();
    });

    it('shows 0 cards for empty column', () => {
      render(<Column {...defaultProps} column={mockEmptyColumn} />);

      expect(screen.getByLabelText('0 cards')).toHaveTextContent('0');
    });
  });

  describe('Column Name Editing', () => {
    it('shows input when clicking column title', async () => {
      render(<Column {...defaultProps} />);

      await userEvent.click(screen.getByRole('button', { name: /edit column name/i }));

      expect(screen.getByRole('textbox', { name: /column name/i })).toBeInTheDocument();
    });

    it('shows input when pressing Enter on column title', () => {
      render(<Column {...defaultProps} />);

      const title = screen.getByRole('button', { name: /edit column name/i });
      fireEvent.keyDown(title, { key: 'Enter' });

      expect(screen.getByRole('textbox', { name: /column name/i })).toBeInTheDocument();
    });

    it('shows input when pressing Space on column title', () => {
      render(<Column {...defaultProps} />);

      const title = screen.getByRole('button', { name: /edit column name/i });
      fireEvent.keyDown(title, { key: ' ' });

      expect(screen.getByRole('textbox', { name: /column name/i })).toBeInTheDocument();
    });

    it('calls onUpdateColumn when name is changed and input blurs', async () => {
      const mockOnUpdateColumn = jest.fn();
      render(<Column {...defaultProps} onUpdateColumn={mockOnUpdateColumn} />);

      await userEvent.click(screen.getByRole('button', { name: /edit column name/i }));

      const input = screen.getByRole('textbox', { name: /column name/i });
      await userEvent.clear(input);
      await userEvent.type(input, 'New Name');
      fireEvent.blur(input);

      expect(mockOnUpdateColumn).toHaveBeenCalledWith(1, { name: 'New Name' });
    });

    it('calls onUpdateColumn when Enter is pressed', async () => {
      const mockOnUpdateColumn = jest.fn();
      render(<Column {...defaultProps} onUpdateColumn={mockOnUpdateColumn} />);

      await userEvent.click(screen.getByRole('button', { name: /edit column name/i }));

      const input = screen.getByRole('textbox', { name: /column name/i });
      await userEvent.clear(input);
      await userEvent.type(input, 'New Name');
      fireEvent.keyDown(input, { key: 'Enter' });

      expect(mockOnUpdateColumn).toHaveBeenCalledWith(1, { name: 'New Name' });
    });

    it('cancels edit when Escape is pressed', async () => {
      const mockOnUpdateColumn = jest.fn();
      render(<Column {...defaultProps} onUpdateColumn={mockOnUpdateColumn} />);

      await userEvent.click(screen.getByRole('button', { name: /edit column name/i }));

      const input = screen.getByRole('textbox', { name: /column name/i });
      await userEvent.clear(input);
      await userEvent.type(input, 'New Name');
      fireEvent.keyDown(input, { key: 'Escape' });

      // Should not call update
      expect(mockOnUpdateColumn).not.toHaveBeenCalled();
      // Title should revert
      expect(screen.getByText('TO DO')).toBeInTheDocument();
    });

    it('does not call onUpdateColumn if name is unchanged', async () => {
      const mockOnUpdateColumn = jest.fn();
      render(<Column {...defaultProps} onUpdateColumn={mockOnUpdateColumn} />);

      await userEvent.click(screen.getByRole('button', { name: /edit column name/i }));

      const input = screen.getByRole('textbox', { name: /column name/i });
      fireEvent.blur(input);

      expect(mockOnUpdateColumn).not.toHaveBeenCalled();
    });

    it('does not call onUpdateColumn if name is empty', async () => {
      const mockOnUpdateColumn = jest.fn();
      render(<Column {...defaultProps} onUpdateColumn={mockOnUpdateColumn} />);

      await userEvent.click(screen.getByRole('button', { name: /edit column name/i }));

      const input = screen.getByRole('textbox', { name: /column name/i });
      await userEvent.clear(input);
      fireEvent.blur(input);

      expect(mockOnUpdateColumn).not.toHaveBeenCalled();
    });
  });

  describe('Column Deletion', () => {
    it('shows confirmation dialog when delete is clicked', async () => {
      render(<Column {...defaultProps} />);

      await userEvent.click(screen.getByRole('button', { name: /delete column/i }));

      expect(window.confirm).toHaveBeenCalledWith('Delete "To Do" and all its cards?');
    });

    it('calls onDeleteColumn when confirmed', async () => {
      const mockOnDeleteColumn = jest.fn();
      window.confirm = jest.fn(() => true);

      render(<Column {...defaultProps} onDeleteColumn={mockOnDeleteColumn} />);

      await userEvent.click(screen.getByRole('button', { name: /delete column/i }));

      expect(mockOnDeleteColumn).toHaveBeenCalledWith(1);
    });

    it('does not call onDeleteColumn when cancelled', async () => {
      const mockOnDeleteColumn = jest.fn();
      window.confirm = jest.fn(() => false);

      render(<Column {...defaultProps} onDeleteColumn={mockOnDeleteColumn} />);

      await userEvent.click(screen.getByRole('button', { name: /delete column/i }));

      expect(mockOnDeleteColumn).not.toHaveBeenCalled();
    });
  });

  describe('Card Creation', () => {
    it('calls onCreateCard when card is added', async () => {
      const mockOnCreateCard = jest.fn();
      render(<Column {...defaultProps} onCreateCard={mockOnCreateCard} />);

      await userEvent.click(screen.getByRole('button', { name: /add a card/i }));
      await userEvent.type(screen.getByRole('textbox'), 'New Card');
      await userEvent.click(screen.getByRole('button', { name: /add card/i }));

      expect(mockOnCreateCard).toHaveBeenCalledWith(1, 'New Card');
    });
  });

  describe('Card Click', () => {
    it('calls onCardClick when a card is clicked', async () => {
      const mockOnCardClick = jest.fn();
      render(<Column {...defaultProps} onCardClick={mockOnCardClick} />);

      await userEvent.click(screen.getByRole('button', { name: /edit card: task 1/i }));

      expect(mockOnCardClick).toHaveBeenCalledWith(mockColumn.cards[0]);
    });
  });

  describe('Drag and Drop', () => {
    it('applies drop-target class when being hovered', () => {
      const { container } = render(<Column {...defaultProps} dropTargetId={1} />);

      expect(container.querySelector('.column')).toHaveClass('drop-target');
    });

    it('does not apply drop-target class when not being hovered', () => {
      const { container } = render(<Column {...defaultProps} dropTargetId={2} />);

      expect(container.querySelector('.column')).not.toHaveClass('drop-target');
    });

    it('creates drag handlers for each card', () => {
      const mockCreateDragHandlers = jest.fn(() => createMockDragHandlers());
      render(<Column {...defaultProps} createDragHandlers={mockCreateDragHandlers} />);

      expect(mockCreateDragHandlers).toHaveBeenCalledTimes(2); // 2 cards
    });

    it('creates drop handlers for the column', () => {
      const mockCreateDropHandlers = jest.fn(() => createMockDropHandlers());
      render(<Column {...defaultProps} createDropHandlers={mockCreateDropHandlers} />);

      expect(mockCreateDropHandlers).toHaveBeenCalledWith(1, expect.any(Array));
    });
  });

  describe('Accessibility', () => {
    it('has accessible section role with label', () => {
      render(<Column {...defaultProps} />);

      expect(screen.getByRole('region', { name: /column: to do/i })).toBeInTheDocument();
    });

    it('column title is keyboard accessible', () => {
      render(<Column {...defaultProps} />);

      const title = screen.getByRole('button', { name: /edit column name/i });
      expect(title).toHaveAttribute('tabindex', '0');
    });

    it('cards are keyboard accessible', () => {
      render(<Column {...defaultProps} />);

      const cards = screen.getAllByRole('button', { name: /edit card/i });
      cards.forEach(card => {
        expect(card).toHaveAttribute('tabindex', '0');
      });
    });
  });
});

