     1→import { useState, useEffect, useRef, useCallback } from 'react';
     2→
     3→/**
     4→ * Modal dialog for editing card details.
     5→ * Supports title and description editing, and card deletion.
     6→ *
     7→ * @param {Object} props - Component props
     8→ * @param {Object} props.card - Card data to edit
     9→ * @param {Function} props.onSave - Callback to save changes (receives { title, description })
    10→ * @param {Function} props.onDelete - Callback to delete the card
    11→ * @param {Function} props.onClose - Callback to close the modal
    12→ */
    13→export default function CardModal({ card, onSave, onDelete, onClose }) {
    14→  const [title, setTitle] = useState(card?.title || '');
    15→  const [description, setDescription] = useState(card?.description || '');
    16→  const [isDeleting, setIsDeleting] = useState(false);
    17→  const modalRef = useRef(null);
    18→  const titleInputRef = useRef(null);
    19→
    20→  // Focus title input on mount
    21→  useEffect(() => {
    22→    if (titleInputRef.current) {
    23→      titleInputRef.current.focus();
    24→      titleInputRef.current.select();
    25→    }
    26→  }, []);
    27→
    28→  // Handle escape key to close modal
    29→  useEffect(() => {
    30→    const handleKeyDown = (e) => {
    31→      if (e.key === 'Escape') {
    32→        onClose();
    33→      }
    34→    };
    35→
    36→    document.addEventListener('keydown', handleKeyDown);
    37→    return () => document.removeEventListener('keydown', handleKeyDown);
    38→  }, [onClose]);
    39→
    40→  // Handle click outside modal to close
    41→  const handleOverlayClick = (e) => {
    42→    if (e.target === e.currentTarget) {
    43→      handleSave();
    44→    }
    45→  };
    46→
    47→  /**
    48→   * Saves changes and closes modal
    49→   */
    50→  const handleSave = useCallback(() => {
    51→    if (title.trim() && (title !== card.title || description !== card.description)) {
    52→      onSave({
    53→        title: title.trim(),
    54→        description: description.trim(),
    55→      });
    56→    }
    57→    onClose();
    58→  }, [title, description, card.title, card.description, onSave, onClose]);
    59→
    60→  /**
    61→   * Handles delete button click with confirmation
    62→   */
    63→  const handleDelete = () => {
    64→    if (isDeleting) {
    65→      onDelete();
    66→      onClose();
    67→    } else {
    68→      setIsDeleting(true);
    69→    }
    70→  };
