import { useState, useEffect, useRef, useCallback } from 'react';

/**
 * Modal dialog for editing card details.
 * Supports title and description editing, and card deletion.
 *
 * @param {Object} props - Component props
 * @param {Object} props.card - Card data to edit
 * @param {Function} props.onSave - Callback to save changes (receives { title, description })
 * @param {Function} props.onDelete - Callback to delete the card
 * @param {Function} props.onClose - Callback to close the modal
 */
export default function CardModal({ card, onSave, onDelete, onClose }) {
  const [title, setTitle] = useState(card?.title || '');
  const [description, setDescription] = useState(card?.description || '');
  const [isDeleting, setIsDeleting] = useState(false);
  const modalRef = useRef(null);
  const titleInputRef = useRef(null);

  // Focus title input on mount
  useEffect(() => {
    if (titleInputRef.current) {
      titleInputRef.current.focus();
      titleInputRef.current.select();
    }
  }, []);

  // Handle escape key to close modal
  useEffect(() => {
    const handleKeyDown = (e) => {
      if (e.key === 'Escape') {
        onClose();
      }
    };

    document.addEventListener('keydown', handleKeyDown);
    return () => document.removeEventListener('keydown', handleKeyDown);
  }, [onClose]);

  // Handle click outside modal to close
  const handleOverlayClick = (e) => {
    if (e.target === e.currentTarget) {
      handleSave();
    }
  };

  /**
   * Saves changes and closes modal
   */
  const handleSave = useCallback(() => {
    if (title.trim() && (title !== card.title || description !== card.description)) {
      onSave({
        title: title.trim(),
        description: description.trim(),
      });
    }
    onClose();
  }, [title, description, card.title, card.description, onSave, onClose]);

  /**
   * Handles delete button click with confirmation
   */
  const handleDelete = () => {
    if (isDeleting) {
      onDelete();
      onClose();
    } else {
      setIsDeleting(true);
    }
  };

  /**
   * Cancels delete confirmation
   */
  const handleCancelDelete = () => {
    setIsDeleting(false);
  };

  if (!card) return null;

  return (
    <div
      className="modal-overlay"
      onClick={handleOverlayClick}
      role="dialog"
      aria-modal="true"
      aria-labelledby="modal-title"
    >
      <div className="modal" ref={modalRef} role="document">
        <div className="modal-header">
          <h2 id="modal-title" className="modal-title">Edit Card</h2>
          <button
            className="modal-close-btn"
            onClick={handleSave}
            aria-label="Close modal"
          >
            <svg
              xmlns="http://www.w3.org/2000/svg"
              viewBox="0 0 20 20"
              fill="currentColor"
              aria-hidden="true"
            >
              <path d="M6.28 5.22a.75.75 0 00-1.06 1.06L8.94 10l-3.72 3.72a.75.75 0 101.06 1.06L10 11.06l3.72 3.72a.75.75 0 101.06-1.06L11.06 10l3.72-3.72a.75.75 0 00-1.06-1.06L10 8.94 6.28 5.22z" />
            </svg>
          </button>
        </div>

        <div className="modal-body">
          <div className="form-group">
            <label htmlFor="card-title" className="form-label">
              Title
            </label>
            <input
              ref={titleInputRef}
              id="card-title"
              type="text"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              className="form-input"
              placeholder="Enter card title..."
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="card-description" className="form-label">
              Description
            </label>
            <textarea
              id="card-description"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              className="form-textarea"
              placeholder="Add a more detailed description..."
              rows={4}
            />
          </div>
        </div>

        <div className="modal-footer">
          {isDeleting ? (
            <div className="flex gap-2">
              <button
                className="btn btn-danger"
                onClick={handleDelete}
                aria-label="Confirm delete"
              >
                Confirm Delete
              </button>
              <button
                className="btn btn-secondary"
                onClick={handleCancelDelete}
              >
                Cancel
              </button>
            </div>
          ) : (
            <button
              className="btn btn-danger"
              onClick={handleDelete}
              aria-label="Delete card"
            >
              <svg
                xmlns="http://www.w3.org/2000/svg"
                viewBox="0 0 20 20"
                fill="currentColor"
                width="16"
                height="16"
                aria-hidden="true"
              >
                <path
                  fillRule="evenodd"
                  d="M8.75 1A2.75 2.75 0 006 3.75v.443c-.795.077-1.584.176-2.365.298a.75.75 0 10.23 1.482l.149-.022.841 10.518A2.75 2.75 0 007.596 19h4.807a2.75 2.75 0 002.742-2.53l.841-10.519.149.023a.75.75 0 00.23-1.482A41.03 41.03 0 0014 4.193V3.75A2.75 2.75 0 0011.25 1h-2.5zM10 4c.84 0 1.673.025 2.5.075V3.75c0-.69-.56-1.25-1.25-1.25h-2.5c-.69 0-1.25.56-1.25 1.25v.325C8.327 4.025 9.16 4 10 4zM8.58 7.72a.75.75 0 00-1.5.06l.3 7.5a.75.75 0 101.5-.06l-.3-7.5zm4.34.06a.75.75 0 10-1.5-.06l-.3 7.5a.75.75 0 101.5.06l.3-7.5z"
                  clipRule="evenodd"
                />
              </svg>
              Delete
            </button>
          )}

          <div className="flex gap-2">
            <button
              className="btn btn-secondary"
              onClick={onClose}
            >
              Cancel
            </button>
            <button
              className="btn btn-primary"
              onClick={handleSave}
              disabled={!title.trim()}
            >
              Save
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

