import { render, screen, fireEvent, waitFor } from '@testing-library/react';
import userEvent from '@testing-library/user-event';
import CardModal from '../src/components/CardModal';

const mockCard = {
  id: 1,
  title: 'Test Card',
  description: 'Test description',
  position: 1024,
  column_id: 1,
};

describe('CardModal', () => {
  const mockOnSave = jest.fn();
  const mockOnDelete = jest.fn();
  const mockOnClose = jest.fn();

  beforeEach(() => {
    jest.clearAllMocks();
  });

  describe('Rendering', () => {
    it('renders modal with card title', () => {
      render(
        <CardModal card={mockCard} onSave={mockOnSave} onDelete={mockOnDelete} onClose={mockOnClose} />
      );

      expect(screen.getByRole('dialog')).toBeInTheDocument();
      expect(screen.getByRole('textbox', { name: /title/i })).toHaveValue('Test Card');
    });

    it('renders modal with card description', () => {
      render(
        <CardModal card={mockCard} onSave={mockOnSave} onDelete={mockOnDelete} onClose={mockOnClose} />
      );

      expect(screen.getByRole('textbox', { name: /description/i })).toHaveValue('Test description');
    });

    it('renders modal title', () => {
      render(
        <CardModal card={mockCard} onSave={mockOnSave} onDelete={mockOnDelete} onClose={mockOnClose} />
      );

      expect(screen.getByRole('heading', { name: /edit card/i })).toBeInTheDocument();
    });

    it('renders Delete, Cancel, and Save buttons', () => {
      render(
        <CardModal card={mockCard} onSave={mockOnSave} onDelete={mockOnDelete} onClose={mockOnClose} />
      );

      expect(screen.getByRole('button', { name: /delete/i })).toBeInTheDocument();
      expect(screen.getByRole('button', { name: /cancel/i })).toBeInTheDocument();
      expect(screen.getByRole('button', { name: /save/i })).toBeInTheDocument();
    });

    it('returns null when no card provided', () => {
      const { container } = render(
        <CardModal card={null} onSave={mockOnSave} onDelete={mockOnDelete} onClose={mockOnClose} />
      );

      expect(container).toBeEmptyDOMElement();
    });
  });

  describe('Form Interaction', () => {
    it('updates title input value', async () => {
      render(
        <CardModal card={mockCard} onSave={mockOnSave} onDelete={mockOnDelete} onClose={mockOnClose} />
      );

      const titleInput = screen.getByRole('textbox', { name: /title/i });
      await userEvent.clear(titleInput);
      await userEvent.type(titleInput, 'New Title');

      expect(titleInput).toHaveValue('New Title');
    });

    it('updates description input value', async () => {
      render(
        <CardModal card={mockCard} onSave={mockOnSave} onDelete={mockOnDelete} onClose={mockOnClose} />
      );

      const descInput = screen.getByRole('textbox', { name: /description/i });
      await userEvent.clear(descInput);
      await userEvent.type(descInput, 'New description');

      expect(descInput).toHaveValue('New description');
    });

    it('focuses title input on mount', () => {
      render(
        <CardModal card={mockCard} onSave={mockOnSave} onDelete={mockOnDelete} onClose={mockOnClose} />
      );

      expect(screen.getByRole('textbox', { name: /title/i })).toHaveFocus();
    });
  });

  describe('Saving Changes', () => {
    it('calls onSave with updated values when Save is clicked', async () => {
      render(
        <CardModal card={mockCard} onSave={mockOnSave} onDelete={mockOnDelete} onClose={mockOnClose} />
      );

      const titleInput = screen.getByRole('textbox', { name: /title/i });
      await userEvent.clear(titleInput);
      await userEvent.type(titleInput, 'Updated Title');

      await userEvent.click(screen.getByRole('button', { name: /save/i }));

      expect(mockOnSave).toHaveBeenCalledWith({
        title: 'Updated Title',
        description: 'Test description',
      });
      expect(mockOnClose).toHaveBeenCalled();
    });

    it('does not call onSave when title is empty', async () => {
      render(
        <CardModal card={mockCard} onSave={mockOnSave} onDelete={mockOnDelete} onClose={mockOnClose} />
      );

      const titleInput = screen.getByRole('textbox', { name: /title/i });
      await userEvent.clear(titleInput);

      expect(screen.getByRole('button', { name: /save/i })).toBeDisabled();
    });

    it('does not call onSave when values are unchanged', async () => {
      render(
        <CardModal card={mockCard} onSave={mockOnSave} onDelete={mockOnDelete} onClose={mockOnClose} />
      );

      await userEvent.click(screen.getByRole('button', { name: /save/i }));

      expect(mockOnSave).not.toHaveBeenCalled();
      expect(mockOnClose).toHaveBeenCalled();
    });

    it('trims whitespace from title and description', async () => {
      render(
        <CardModal card={mockCard} onSave={mockOnSave} onDelete={mockOnDelete} onClose={mockOnClose} />
      );

      const titleInput = screen.getByRole('textbox', { name: /title/i });
      const descInput = screen.getByRole('textbox', { name: /description/i });

      await userEvent.clear(titleInput);
      await userEvent.type(titleInput, '  Trimmed Title  ');
      await userEvent.clear(descInput);
      await userEvent.type(descInput, '  Trimmed Desc  ');

      await userEvent.click(screen.getByRole('button', { name: /save/i }));

      expect(mockOnSave).toHaveBeenCalledWith({
        title: 'Trimmed Title',
        description: 'Trimmed Desc',
      });
    });
  });

  describe('Deleting Card', () => {
    it('shows confirmation when Delete is clicked first time', async () => {
      render(
        <CardModal card={mockCard} onSave={mockOnSave} onDelete={mockOnDelete} onClose={mockOnClose} />
      );

      await userEvent.click(screen.getByRole('button', { name: /delete card/i }));

      expect(screen.getByRole('button', { name: /confirm delete/i })).toBeInTheDocument();
      expect(screen.getByRole('button', { name: /cancel/i })).toBeInTheDocument();
    });

    it('calls onDelete when deletion is confirmed', async () => {
      render(
        <CardModal card={mockCard} onSave={mockOnSave} onDelete={mockOnDelete} onClose={mockOnClose} />
      );

      await userEvent.click(screen.getByRole('button', { name: /delete card/i }));
      await userEvent.click(screen.getByRole('button', { name: /confirm delete/i }));

      expect(mockOnDelete).toHaveBeenCalled();
      expect(mockOnClose).toHaveBeenCalled();
    });

    it('cancels deletion when Cancel is clicked in confirmation', async () => {
      render(
        <CardModal card={mockCard} onSave={mockOnSave} onDelete={mockOnDelete} onClose={mockOnClose} />
      );

      await userEvent.click(screen.getByRole('button', { name: /delete card/i }));

      // Find the Cancel button (there are two - one in confirmation, one main)
      const cancelButtons = screen.getAllByRole('button', { name: /cancel/i });
      await userEvent.click(cancelButtons[0]); // The confirmation cancel

      // Should show Delete card button again
      expect(screen.getByRole('button', { name: /delete card/i })).toBeInTheDocument();
      expect(mockOnDelete).not.toHaveBeenCalled();
    });
  });

  describe('Closing Modal', () => {
    it('calls onClose when Cancel button is clicked', async () => {
      render(
        <CardModal card={mockCard} onSave={mockOnSave} onDelete={mockOnDelete} onClose={mockOnClose} />
      );

      // Get the main Cancel button (not in delete confirmation)
      const cancelButtons = screen.getAllByRole('button', { name: /cancel/i });
      const mainCancel = cancelButtons[cancelButtons.length - 1]; // Main cancel is last
      await userEvent.click(mainCancel);

      expect(mockOnClose).toHaveBeenCalled();
    });

    it('calls onClose and saves when close (X) button is clicked', async () => {
      render(
        <CardModal card={mockCard} onSave={mockOnSave} onDelete={mockOnDelete} onClose={mockOnClose} />
      );

      await userEvent.click(screen.getByRole('button', { name: /close modal/i }));

      expect(mockOnClose).toHaveBeenCalled();
    });

    it('closes modal when Escape key is pressed', () => {
      render(
        <CardModal card={mockCard} onSave={mockOnSave} onDelete={mockOnDelete} onClose={mockOnClose} />
      );

      fireEvent.keyDown(document, { key: 'Escape' });

      expect(mockOnClose).toHaveBeenCalled();
    });

    it('saves and closes when clicking overlay', async () => {
      render(
        <CardModal card={mockCard} onSave={mockOnSave} onDelete={mockOnDelete} onClose={mockOnClose} />
      );

      // Click the overlay (the modal-overlay element)
      const overlay = screen.getByRole('dialog').parentElement;
      fireEvent.click(overlay);

      expect(mockOnClose).toHaveBeenCalled();
    });
  });

  describe('Accessibility', () => {
    it('has correct ARIA attributes', () => {
      render(
        <CardModal card={mockCard} onSave={mockOnSave} onDelete={mockOnDelete} onClose={mockOnClose} />
      );

      const dialog = screen.getByRole('dialog');
      expect(dialog).toHaveAttribute('aria-modal', 'true');
      expect(dialog).toHaveAttribute('aria-labelledby', 'modal-title');
    });

    it('form inputs have proper labels', () => {
      render(
        <CardModal card={mockCard} onSave={mockOnSave} onDelete={mockOnDelete} onClose={mockOnClose} />
      );

      expect(screen.getByLabelText(/title/i)).toBeInTheDocument();
      expect(screen.getByLabelText(/description/i)).toBeInTheDocument();
    });
  });
});

