import { renderHook, act, waitFor } from '@testing-library/react';
import { useBoard } from '../src/hooks/useBoard';

// Mock the useApi hook
jest.mock('../src/hooks/useApi', () => ({
  useApi: () => ({
    get: jest.fn(),
    post: jest.fn(),
    put: jest.fn(),
    patch: jest.fn(),
    del: jest.fn(),
  }),
}));

import { useApi } from '../src/hooks/useApi';

describe('useBoard', () => {
  let mockApi;

  const mockBoard = {
    id: 1,
    name: 'Test Board',
    columns: [
      {
        id: 1,
        name: 'To Do',
        position: 1024,
        cards: [
          { id: 1, title: 'Card 1', description: 'Desc 1', position: 1024, column_id: 1 },
          { id: 2, title: 'Card 2', description: '', position: 2048, column_id: 1 },
        ],
      },
      {
        id: 2,
        name: 'In Progress',
        position: 2048,
        cards: [
          { id: 3, title: 'Card 3', description: 'Working', position: 1024, column_id: 2 },
        ],
      },
    ],
  };

  beforeEach(() => {
    jest.clearAllMocks();
    mockApi = useApi();
  });

  describe('Initial Fetch', () => {
    it('fetches board data on mount', async () => {
      mockApi.get.mockResolvedValueOnce(mockBoard);

      const { result } = renderHook(() => useBoard(1));

      expect(result.current.loading).toBe(true);

      await waitFor(() => {
        expect(result.current.loading).toBe(false);
      });

      expect(mockApi.get).toHaveBeenCalledWith('/boards/1');
      expect(result.current.board).toEqual(mockBoard);
    });

    it('handles fetch error', async () => {
      mockApi.get.mockRejectedValueOnce(new Error('Network error'));

      const { result } = renderHook(() => useBoard(1));

      await waitFor(() => {
        expect(result.current.loading).toBe(false);
      });

      expect(result.current.error).toBe('Network error');
      expect(result.current.board).toBeNull();
    });

    it('does not fetch when boardId is falsy', async () => {
      const { result } = renderHook(() => useBoard(null));

      // Give time for any potential fetch to occur
      await new Promise(resolve => setTimeout(resolve, 100));

      expect(mockApi.get).not.toHaveBeenCalled();
    });

    it('ensures columns have cards array', async () => {
      const boardWithNullCards = {
        ...mockBoard,
        columns: [
          { id: 1, name: 'Empty', position: 1024, cards: null },
          { id: 2, name: 'Undefined', position: 2048 }, // no cards property
        ],
      };
      mockApi.get.mockResolvedValueOnce(boardWithNullCards);

      const { result } = renderHook(() => useBoard(1));

      await waitFor(() => {
        expect(result.current.loading).toBe(false);
      });

      expect(result.current.board.columns[0].cards).toEqual([]);
      expect(result.current.board.columns[1].cards).toEqual([]);
    });
  });

  describe('createColumn', () => {
    it('creates a new column', async () => {
      mockApi.get.mockResolvedValueOnce(mockBoard);
      mockApi.post.mockResolvedValueOnce({ id: 3, name: 'Done', position: 3072 });

      const { result } = renderHook(() => useBoard(1));

      await waitFor(() => {
        expect(result.current.loading).toBe(false);
      });

      await act(async () => {
        await result.current.actions.createColumn('Done');
      });

      expect(mockApi.post).toHaveBeenCalledWith('/boards/1/columns', { name: 'Done' });
      expect(result.current.board.columns).toHaveLength(3);
      expect(result.current.board.columns[2].name).toBe('Done');
    });

    it('handles createColumn error', async () => {
      mockApi.get.mockResolvedValueOnce(mockBoard);
      mockApi.post.mockRejectedValueOnce(new Error('Create failed'));

      const { result } = renderHook(() => useBoard(1));

      await waitFor(() => {
        expect(result.current.loading).toBe(false);
      });

      await act(async () => {
        await expect(result.current.actions.createColumn('Done')).rejects.toThrow('Create failed');
      });

      expect(result.current.error).toBe('Create failed');
    });
  });

  describe('updateColumn', () => {
    it('updates column with optimistic update', async () => {
      mockApi.get.mockResolvedValueOnce(mockBoard);
      mockApi.put.mockResolvedValueOnce({ id: 1, name: 'Updated', position: 1024 });

      const { result } = renderHook(() => useBoard(1));

      await waitFor(() => {
        expect(result.current.loading).toBe(false);
      });

      await act(async () => {
        await result.current.actions.updateColumn(1, { name: 'Updated' });
      });

      expect(mockApi.put).toHaveBeenCalledWith('/columns/1', { name: 'Updated' });
      expect(result.current.board.columns[0].name).toBe('Updated');
    });

    it('rolls back on update error', async () => {
      mockApi.get
        .mockResolvedValueOnce(mockBoard)
        .mockResolvedValueOnce(mockBoard); // For refetch on error
      mockApi.put.mockRejectedValueOnce(new Error('Update failed'));

      const { result } = renderHook(() => useBoard(1));

      await waitFor(() => {
        expect(result.current.loading).toBe(false);
      });

      await act(async () => {
        await expect(result.current.actions.updateColumn(1, { name: 'Updated' })).rejects.toThrow();
      });

      // Should have refetched
      expect(mockApi.get).toHaveBeenCalledTimes(2);
    });
  });

  describe('deleteColumn', () => {
    it('deletes column with optimistic update', async () => {
      mockApi.get.mockResolvedValueOnce(mockBoard);
      mockApi.del.mockResolvedValueOnce(null);

      const { result } = renderHook(() => useBoard(1));

      await waitFor(() => {
        expect(result.current.loading).toBe(false);
      });

      const initialColumnCount = result.current.board.columns.length;

      await act(async () => {
        await result.current.actions.deleteColumn(1);
      });

      expect(mockApi.del).toHaveBeenCalledWith('/columns/1');
      expect(result.current.board.columns).toHaveLength(initialColumnCount - 1);
    });

    it('rolls back on delete error', async () => {
      mockApi.get.mockResolvedValueOnce(mockBoard);
      mockApi.del.mockRejectedValueOnce(new Error('Delete failed'));

      const { result } = renderHook(() => useBoard(1));

      await waitFor(() => {
        expect(result.current.loading).toBe(false);
      });

      const originalColumns = result.current.board.columns;

      await act(async () => {
        await expect(result.current.actions.deleteColumn(1)).rejects.toThrow();
      });

      // Should have rolled back
      expect(result.current.board.columns.length).toBe(originalColumns.length);
    });
  });

  describe('createCard', () => {
    it('creates a new card in column', async () => {
      mockApi.get.mockResolvedValueOnce(mockBoard);
      mockApi.post.mockResolvedValueOnce({ id: 4, title: 'New Card', description: '', position: 3072 });

      const { result } = renderHook(() => useBoard(1));

      await waitFor(() => {
        expect(result.current.loading).toBe(false);
      });

      await act(async () => {
        await result.current.actions.createCard(1, 'New Card', 'Description');
      });

      expect(mockApi.post).toHaveBeenCalledWith('/columns/1/cards', {
        title: 'New Card',
        description: 'Description',
      });

      // Card should be added to column 1
      const column1 = result.current.board.columns.find(c => c.id === 1);
      expect(column1.cards.some(c => c.title === 'New Card')).toBe(true);
    });

    it('handles createCard error', async () => {
      mockApi.get.mockResolvedValueOnce(mockBoard);
      mockApi.post.mockRejectedValueOnce(new Error('Create card failed'));

      const { result } = renderHook(() => useBoard(1));

      await waitFor(() => {
        expect(result.current.loading).toBe(false);
      });

      await act(async () => {
        await expect(result.current.actions.createCard(1, 'New Card')).rejects.toThrow();
      });

      expect(result.current.error).toBe('Create card failed');
    });
  });

  describe('updateCard', () => {
    it('updates card with optimistic update', async () => {
      mockApi.get.mockResolvedValueOnce(mockBoard);
      mockApi.put.mockResolvedValueOnce({ id: 1, title: 'Updated Card', description: 'New desc' });

      const { result } = renderHook(() => useBoard(1));

      await waitFor(() => {
        expect(result.current.loading).toBe(false);
      });

      await act(async () => {
        await result.current.actions.updateCard(1, { title: 'Updated Card' });
      });

      expect(mockApi.put).toHaveBeenCalledWith('/cards/1', { title: 'Updated Card' });

      // Card should be updated
      const column1 = result.current.board.columns.find(c => c.id === 1);
      const card1 = column1.cards.find(c => c.id === 1);
      expect(card1.title).toBe('Updated Card');
    });
  });

  describe('deleteCard', () => {
    it('deletes card with optimistic update', async () => {
      mockApi.get.mockResolvedValueOnce(mockBoard);
      mockApi.del.mockResolvedValueOnce(null);

      const { result } = renderHook(() => useBoard(1));

      await waitFor(() => {
        expect(result.current.loading).toBe(false);
      });

      const column1InitialCards = result.current.board.columns[0].cards.length;

      await act(async () => {
        await result.current.actions.deleteCard(1);
      });

      expect(mockApi.del).toHaveBeenCalledWith('/cards/1');
      expect(result.current.board.columns[0].cards.length).toBe(column1InitialCards - 1);
    });
  });

  describe('moveCard', () => {
    it('moves card between columns', async () => {
      mockApi.get.mockResolvedValueOnce(mockBoard);
      mockApi.patch.mockResolvedValueOnce({ id: 1, column_id: 2, position: 2048 });

      const { result } = renderHook(() => useBoard(1));

      await waitFor(() => {
        expect(result.current.loading).toBe(false);
      });

      // Card 1 is in column 1, move to column 2
      await act(async () => {
        await result.current.actions.moveCard(1, 2, 2048);
      });

      expect(mockApi.patch).toHaveBeenCalledWith('/cards/1/move', {
        columnId: 2,
        position: 2048,
      });

      // Card 1 should now be in column 2
      const column1 = result.current.board.columns.find(c => c.id === 1);
      const column2 = result.current.board.columns.find(c => c.id === 2);

      expect(column1.cards.some(c => c.id === 1)).toBe(false);
      expect(column2.cards.some(c => c.id === 1)).toBe(true);
    });

    it('does not move card if source not found', async () => {
      mockApi.get.mockResolvedValueOnce(mockBoard);

      const { result } = renderHook(() => useBoard(1));

      await waitFor(() => {
        expect(result.current.loading).toBe(false);
      });

      // Try to move non-existent card
      await act(async () => {
        await result.current.actions.moveCard(999, 2, 2048);
      });

      // Patch should not be called
      expect(mockApi.patch).not.toHaveBeenCalled();
    });

    it('rolls back on move error', async () => {
      mockApi.get.mockResolvedValueOnce(mockBoard);
      mockApi.patch.mockRejectedValueOnce(new Error('Move failed'));

      const { result } = renderHook(() => useBoard(1));

      await waitFor(() => {
        expect(result.current.loading).toBe(false);
      });

      const column1CardsBefore = result.current.board.columns[0].cards.length;

      await act(async () => {
        await expect(result.current.actions.moveCard(1, 2, 2048)).rejects.toThrow();
      });

      // Should have rolled back
      expect(result.current.board.columns[0].cards.length).toBe(column1CardsBefore);
    });
  });

  describe('refetch', () => {
    it('allows manual refetch', async () => {
      mockApi.get
        .mockResolvedValueOnce(mockBoard)
        .mockResolvedValueOnce({ ...mockBoard, name: 'Updated Board' });

      const { result } = renderHook(() => useBoard(1));

      await waitFor(() => {
        expect(result.current.loading).toBe(false);
      });

      expect(result.current.board.name).toBe('Test Board');

      await act(async () => {
        await result.current.refetch();
      });

      await waitFor(() => {
        expect(result.current.board.name).toBe('Updated Board');
      });

      expect(mockApi.get).toHaveBeenCalledTimes(2);
    });
  });
});

