    await userEvent.click(screen.getByRole('button', { name: /confirm delete/i }));

    expect(mockOnDelete).toHaveBeenCalled();
    expect(mockOnClose).toHaveBeenCalled();
  });

  it('cancels deletion when Cancel is clicked in confirmation', async () => {
    render(
      <CardModal card={mockCard} onSave={mockOnSave} onDelete={mockOnDelete} onClose={mockOnClose} />
    );

    await userEvent.click(screen.getByRole('button', { name: /delete card/i }));

