{
  "fixture": "taskflow-api",
  "description": "Full-stack Python/React task management API",
  "expected_skills": {
    "backend": [
      "developing-with-python",
      "pytest"
    ],
    "frontend": [
      "developing-with-react",
      "developing-with-typescript",
      "jest",
      "styling-with-tailwind"
    ],
    "testing": [
      "writing-playwright-tests"
    ],
    "infrastructure": [
      "managing-vercel"
    ]
  },
  "expected_agent_tailoring": {
    "backend-implementer": {
      "should_mention": [
        "FastAPI",
        "SQLAlchemy",
        "Alembic",
        "Pydantic",
        "JWT",
        "python-jose"
      ],
      "should_reference": [
        "pytest",
        "httpx",
        "pytest-asyncio"
      ]
    },
    "frontend-implementer": {
      "should_mention": [
        "React 18",
        "TypeScript",
        "TailwindCSS",
        "React Query",
        "Zustand"
      ],
      "should_reference": [
        "Jest",
        "React Testing Library"
      ]
    },
    "devops-engineer": {
      "should_mention": [
        "Kubernetes",
        "GKE",
        "Docker",
        "GitHub Actions"
      ],
      "should_reference": [
        "OpenTelemetry",
        "Prometheus",
        "structlog"
      ]
    }
  },
  "validation_criteria": {
    "prd_should_contain": [
      "task management",
      "Kanban",
      "JWT authentication",
      "team workspace"
    ],
    "trd_should_contain": [
      "FastAPI endpoints",
      "SQLAlchemy models",
      "React components",
      "API response time < 200ms"
    ]
  }
}
