/**
 * Status Hook
 * Tracks implementation progress and updates .trd-state/current.json.
 */

const fs = require('fs');
const path = require('path');

// Read hook input from stdin
let input = '';
process.stdin.setEncoding('utf8');
process.stdin.on('data', chunk => input += chunk);
process.stdin.on('end', () => {
  try {
    const hookData = JSON.parse(input);
    const result = updateStatus(hookData);
    console.log(JSON.stringify(result));
  } catch (error) {
    console.log(JSON.stringify({
      success: false,
      error: error.message
    }));
  }
});

function loadCurrentState() {
  const statePath = path.join(process.cwd(), '.trd-state', 'current.json');
  if (fs.existsSync(statePath)) {
    return JSON.parse(fs.readFileSync(statePath, 'utf8'));
  }
  return {
    prd: null,
    trd: null,
    branch: null,
    status: 'idle',
    currentTask: null,
    completedTasks: [],
    lastUpdated: null
  };
}

function saveCurrentState(state) {
  const stateDir = path.join(process.cwd(), '.trd-state');
  if (!fs.existsSync(stateDir)) {
    fs.mkdirSync(stateDir, { recursive: true });
  }
  const statePath = path.join(stateDir, 'current.json');
  state.lastUpdated = new Date().toISOString();
  fs.writeFileSync(statePath, JSON.stringify(state, null, 2));
}

function updateStatus(hookData) {
  const { action, data } = hookData;
  const state = loadCurrentState();

  switch (action) {
