#!/usr/bin/env node
/**
 * Permitter Hook
 * Enforces permission boundaries for Claude Code operations.
 * Runs before tool execution to validate against constitution rules.
 */

const fs = require('fs');
const path = require('path');

// Read hook input from stdin
let input = '';
process.stdin.setEncoding('utf8');
process.stdin.on('data', chunk => input += chunk);
process.stdin.on('end', () => {
  try {
    const hookData = JSON.parse(input);
    const result = checkPermission(hookData);
    console.log(JSON.stringify(result));
  } catch (error) {
    console.log(JSON.stringify({
      decision: 'allow',
      reason: 'Permitter error, defaulting to allow'
    }));
  }
});

function checkPermission(hookData) {
  const { tool, parameters } = hookData;

  // Load constitution rules if available
  const constitutionPath = path.join(process.cwd(), '.claude', 'rules', 'constitution.md');
  let constitution = '';
  if (fs.existsSync(constitutionPath)) {
    constitution = fs.readFileSync(constitutionPath, 'utf8');
  }

  // Check for dangerous patterns
  const dangerousPatterns = [
    /rm\s+-rf\s+\//,           // rm -rf /
    /DROP\s+DATABASE/i,         // DROP DATABASE
    /TRUNCATE\s+TABLE/i,        // TRUNCATE TABLE
    /--force.*push/,            // force push
    /git\s+push.*--force/,      // git push --force
  ];

  if (tool === 'Bash' && parameters?.command) {
    for (const pattern of dangerousPatterns) {
      if (pattern.test(parameters.command)) {
        return {
          decision: 'deny',
          reason: `Command matches dangerous pattern: ${pattern}`
        };
      }
    }
  }

  // Check file write permissions
  if (tool === 'Write' || tool === 'Edit') {
    const filePath = parameters?.file_path || '';

    // Deny writes to system directories
    if (filePath.startsWith('/etc/') || filePath.startsWith('/usr/')) {
      return {
        decision: 'deny',
        reason: 'Cannot write to system directories'
      };
    }

    // Deny writes to .env files with secrets
    if (/\.env(\.local)?$/.test(filePath)) {
      return {
        decision: 'ask',
        reason: 'Writing to environment files requires explicit approval'
      };
    }
  }

  return {
    decision: 'allow',
    reason: 'Operation permitted'
  };
}

#!/usr/bin/env python3
"""
Router Hook
Routes prompts to appropriate commands, skills, and agents based on content analysis.
"""

import json
import sys
import os
import re
from pathlib import Path

def load_router_rules():
    """Load routing rules from configuration."""
    rules_path = Path.cwd() / '.claude' / 'router-rules.json'
    if rules_path.exists():
        with open(rules_path) as f:
            return json.load(f)
    return {'rules': []}

def match_rule(prompt: str, rule: dict) -> bool:
    """Check if a prompt matches a routing rule."""
    patterns = rule.get('patterns', [])
    for pattern in patterns:
        if re.search(pattern, prompt, re.IGNORECASE):
            return True
    return False

def route_prompt(prompt: str) -> dict:
    """Determine routing for a given prompt."""
    rules = load_router_rules()

    # Check each rule in priority order
    for rule in rules.get('rules', []):
        if match_rule(prompt, rule):
            return {
                'route': rule.get('route', 'default'),
                'target': rule.get('target'),
                'confidence': rule.get('confidence', 0.8),
                'reason': rule.get('description', 'Matched routing rule')
            }

    # Default routing
    return {
        'route': 'default',
        'target': None,
        'confidence': 1.0,
        'reason': 'No specific route matched'
    }

def main():
    """Main entry point for router hook."""
    input_data = sys.stdin.read()

    try:
        hook_data = json.loads(input_data)
        prompt = hook_data.get('prompt', '')
        result = route_prompt(prompt)
        print(json.dumps(result))
    except json.JSONDecodeError:
        print(json.dumps({
            'route': 'default',
            'target': None,
            'confidence': 1.0,
            'reason': 'Router error, using default'
        }))

if __name__ == '__main__':
    main()

